// ===== MARKET ANALYSIS SERVICE - PART 1: CORE CLASSES & EXCHANGE MANAGER =====
// COMPLETE PRESERVATION WITH ALL SYNTAX FIXES AND REAL DATA ONLY

// ===== DEBUG LOGGING =====
console.log('ðŸ” DEBUG: Market Analysis called with:');
console.log('- Function:', typeof $function !== 'undefined' ? $function : 'UNDEFINED');
console.log('- Symbols:', typeof $symbols !== 'undefined' ? $symbols : 'UNDEFINED'); 
console.log('- Exchanges:', typeof $exchanges !== 'undefined' ? $exchanges : 'UNDEFINED');
console.log('- All parameters:', arguments);
console.log('=====================================');

const fetch = require('node-fetch');
const crypto = require('crypto');

// ===== DYNAMIC EXCHANGE MANAGER - COMPLETE WITH FIXES =====
class DynamicExchangeManager {
    constructor() {
        this.exchangeConfigs = {
            binance: {
                baseUrl: 'https://api.binance.com',
                endpoints: {
                    ticker: '/api/v3/ticker/24hr',
                    klines: '/api/v3/klines',
                    exchangeInfo: '/api/v3/exchangeInfo',
                    orderBook: '/api/v3/depth',
                    avgPrice: '/api/v3/avgPrice'
                },
                rateLimits: {
                    requests: 1200,
                    weight: 1200
                }
            },
            kraken: {
                baseUrl: 'https://api.kraken.com',
                endpoints: {
                    ticker: '/0/public/Ticker',
                    ohlc: '/0/public/OHLC',
                    assets: '/0/public/Assets',
                    orderBook: '/0/public/Depth',
                    trades: '/0/public/Trades'
                },
                rateLimits: {
                    requests: 1800,
                    weight: 1800
                }
            },
            kucoin: {
                baseUrl: 'https://api.kucoin.com',
                endpoints: {
                    ticker: '/api/v1/market/allTickers',
                    klines: '/api/v1/market/candles',
                    symbols: '/api/v1/symbols',
                    orderBook: '/api/v1/market/orderbook/level2_20',
                    stats: '/api/v1/market/stats'
                },
                rateLimits: {
                    requests: 1500,
                    weight: 1500
                }
            },
            coingecko: {
                baseUrl: 'https://api.coingecko.com/api/v3',
                endpoints: {
                    price: '/simple/price',
                    history: '/coins/{id}/market_chart',
                    trending: '/search/trending',
                    global: '/global'
                }
            }
        };

        this.cache = new Map();
        this.cacheTTL = 30000; // 30 seconds
        this.rateLimits = new Map();
        this.circuitBreakers = new Map();
        this.lastRequestTimes = new Map();

      this.symbolBlacklist = new Set(['ARIA', 'YZY', 'BAS', 'DGC', 'SAPIEN', 'A2Z', 'AWE','MAT', 'FLOCK', 'PROMPT', 'XPIN', 'ALU', 'ZORA']);
      this.validBinanceSymbols = null; // Will be populated by fetchBinanceAssets

    }

    // COMPLETE ASSET DISCOVERY - REAL API CALLS ONLY
   async discoverExchangeAssets() {
    console.log('ðŸ” Discovering high-opportunity assets across all exchanges...');
    const allAssets = new Map();
    const exchanges = ['binance', 'kraken', 'kucoin'];

    for (const exchange of exchanges) {
        try {
            console.log(`Discovering assets on ${exchange}...`);
            let assets = [];

            switch(exchange) {
                 case 'binance':
                    // First get valid trading symbols
                    const exchangeInfoResponse = await fetch(
                        `${this.exchangeConfigs.binance.baseUrl}/api/v3/exchangeInfo`,
                        { timeout: 10000, headers: { 'User-Agent': 'Crypto-Universe-Enterprise/2.0' } }
                    );

                    const validSymbols = new Set();
                    if (exchangeInfoResponse.ok) {
                        const exchangeInfo = await exchangeInfoResponse.json();
                        exchangeInfo.symbols
                            .filter(s => s.status === 'TRADING' && s.quoteAsset === 'USDT')
                            .forEach(s => validSymbols.add(s.symbol));
                    }

                    const binanceResponse = await fetch(
                        `${this.exchangeConfigs.binance.baseUrl}/api/v3/ticker/24hr`,
                        { timeout: 15000, headers: { 'User-Agent': 'Crypto-Universe-Enterprise/2.0' } }
                    );

                    if (binanceResponse.ok) {
                        const data = await binanceResponse.json();
                        // DON'T USE 'const assets =' or 'let assets =' - just assign to existing variable
                        assets = data
                            .filter(ticker => {
                                const volume = parseFloat(ticker.quoteVolume);
                                const symbol = ticker.symbol;
                                return validSymbols.has(symbol) && 
                                       volume > 500000 &&
                                       (symbol.endsWith('USDT') || symbol.endsWith('BUSD')) &&
                                       !symbol.includes('DOWN') && !symbol.includes('UP') &&
                                       !symbol.startsWith('1000');
                            })
                            .map(ticker => ({
                                symbol: ticker.symbol.replace('USDT', '').replace('BUSD', ''),
                                exchange: 'binance',
                                volume24h: parseFloat(ticker.quoteVolume),
                                priceChangePercent: parseFloat(ticker.priceChangePercent)
                            }));
                    }
                    break;

                case 'kraken':
                    const krakenResponse = await fetch(
                        `${this.exchangeConfigs.kraken.baseUrl}/0/public/AssetPairs`,
                        { timeout: 15000, headers: { 'User-Agent': 'Crypto-Universe-Enterprise/2.0' } }
                    );
                    if (krakenResponse.ok) {
                        const data = await krakenResponse.json();
                        if (data.result) {
                            const pairs = Object.entries(data.result);
                            for (const [pair, info] of pairs) {
                                if (pair.includes('USD') && !pair.includes('.') && info.status === 'online') {
                                    const baseAsset = info.base.replace('X', '').replace('Z', '');
                                    if (!allAssets.has(baseAsset)) {
                                        assets.push({
                                            symbol: baseAsset,
                                            exchange: 'kraken',
                                            volume24h: 0, // Will fetch in price check
                                            tradingPair: pair
                                        });
                                    }
                                }
                            }
                        }
                    }
                    break;

                case 'kucoin':
                    const kucoinResponse = await fetch(
                        `${this.exchangeConfigs.kucoin.baseUrl}/api/v1/market/allTickers`,
                        { timeout: 15000, headers: { 'User-Agent': 'Crypto-Universe-Enterprise/2.0' } }
                    );
                    if (kucoinResponse.ok) {
                        const data = await kucoinResponse.json();
                        if (data.data && data.data.ticker) {
                            assets = data.data.ticker
                                .filter(ticker => {
                                    const volume = parseFloat(ticker.volValue || 0);
                                    const symbol = ticker.symbol;
                                    return volume > 500000 &&
                                           symbol.endsWith('-USDT') &&
                                           !symbol.includes('3L') && !symbol.includes('3S'); // Skip leveraged
                                })
                                .map(ticker => ({
                                    symbol: ticker.symbol.split('-')[0],
                                    exchange: 'kucoin',
                                    volume24h: parseFloat(ticker.volValue),
                                    priceChangePercent: parseFloat(ticker.changeRate) * 100
                                }));
                        }
                    }
                    break;
            }

            // Add to global map
            assets.forEach(asset => {
                const key = asset.symbol;
                if (!allAssets.has(key)) {
                    allAssets.set(key, {
                        symbol: key,
                        exchanges: [asset.exchange],
                        totalVolume: asset.volume24h,
                        volatility: Math.abs(asset.priceChangePercent || 0)
                    });
                } else {
                    const existing = allAssets.get(key);
                    existing.exchanges.push(asset.exchange);
                    existing.totalVolume += asset.volume24h;
                    existing.volatility = Math.max(existing.volatility, Math.abs(asset.priceChangePercent || 0));
                }
            });

        } catch (error) {
            console.warn(`Asset discovery failed for ${exchange}:`, error.message);
        }
    }

    // Filter and tier assets
    const tieredAssets = this.tierAssets(allAssets);
    console.log(`Discovered ${tieredAssets.tier1.length} tier-1, ${tieredAssets.tier2.length} tier-2 assets`);

    this.cache.set('discovered_assets', {
        data: tieredAssets,
        timestamp: Date.now()
    });

    return tieredAssets;
}


    async fetchBinanceAssets() {
        const cacheKey = 'binance_assets';
        const cached = this.cache.get(cacheKey);

        if (cached && Date.now() - cached.timestamp < this.cacheTTL) {
            return cached.data;
        }

        await this.respectRateLimit('binance');

        const response = await fetch(`${this.exchangeConfigs.binance.baseUrl}/api/v3/exchangeInfo`, {
            timeout: 10000,
            headers: {
                'User-Agent': 'Crypto-Universe-Enterprise/2.0'
            }
        });

        if (!response.ok) {
            throw new Error(`Binance API error: ${response.status}`);
        }

        const data = await response.json();

        const tradingSymbols = data.symbols
            .filter(symbol => 
                symbol.status === 'TRADING' && 
                (symbol.quoteAsset === 'USDT' || symbol.quoteAsset === 'BUSD') &&
                !symbol.baseAsset.includes('DOWN') &&
                !symbol.baseAsset.includes('UP')
            )
            .map(symbol => symbol.baseAsset)
            .filter((asset, index, arr) => arr.indexOf(asset) === index);

        // Store valid symbols for validation
        this.validBinanceSymbols = new Set(tradingSymbols);

        const assets = data.symbols
            .filter(symbol => 
                symbol.status === 'TRADING' && 
                (symbol.quoteAsset === 'USDT' || symbol.quoteAsset === 'BUSD' || symbol.quoteAsset === 'USDC')
            )
            .map(symbol => symbol.baseAsset)
            .filter((asset, index, arr) => arr.indexOf(asset) === index)
            .sort();

        this.cache.set(cacheKey, {
            data: assets,
            timestamp: Date.now()
        });

        return assets;
    }

    tierAssets(allAssets) {
    const assetArray = Array.from(allAssets.values());

    // Calculate opportunity score
    assetArray.forEach(asset => {
        asset.opportunityScore = 
            (asset.exchanges.length * 10) + // Multi-exchange bonus
            (asset.totalVolume / 1000000) + // Volume in millions
            (asset.volatility * 2); // Volatility bonus for arbitrage
    });

    // Sort by opportunity score
    assetArray.sort((a, b) => b.opportunityScore - a.opportunityScore);

    return {
        tier1: assetArray.slice(0, 30).map(a => a.symbol),
        tier2: assetArray.slice(30, 80).map(a => a.symbol),
        tier3: assetArray.slice(80, 200).map(a => a.symbol),
        allAssets: assetArray
    };
    }

    async fetchKrakenAssets() {
        const cacheKey = 'kraken_assets';
        const cached = this.cache.get(cacheKey);

        if (cached && Date.now() - cached.timestamp < this.cacheTTL) {
            return cached.data;
        }

        await this.respectRateLimit('kraken');

        const response = await fetch(`${this.exchangeConfigs.kraken.baseUrl}/0/public/Assets`, {
            timeout: 10000,
            headers: {
                'User-Agent': 'Crypto-Universe-Enterprise/2.0'
            }
        });

        if (!response.ok) {
            throw new Error(`Kraken API error: ${response.status}`);
        }

        const data = await response.json();

        if (data.error && data.error.length > 0) {
            throw new Error(`Kraken errors: ${data.error.join(', ')}`);
        }

        const assets = Object.keys(data.result)
            .map(asset => asset.replace(/^[XZ]/, '')) // Remove Kraken prefixes
            .filter(asset => asset.length <= 5 && asset.length >= 2)
            .sort();

        this.cache.set(cacheKey, {
            data: assets,
            timestamp: Date.now()
        });

        return assets;
    }

    async fetchKucoinAssets() {
        const cacheKey = 'kucoin_assets';
        const cached = this.cache.get(cacheKey);

        if (cached && Date.now() - cached.timestamp < this.cacheTTL) {
            return cached.data;
        }

        await this.respectRateLimit('kucoin');

        const response = await fetch(`${this.exchangeConfigs.kucoin.baseUrl}/api/v1/symbols`, {
            timeout: 10000,
            headers: {
                'User-Agent': 'Crypto-Universe-Enterprise/2.0'
            }
        });

        if (!response.ok) {
            throw new Error(`KuCoin API error: ${response.status}`);
        }

        const data = await response.json();

        if (data.code !== '200000') {
            throw new Error(`KuCoin API error: ${data.msg}`);
        }

        const assets = data.data
            .filter(symbol => 
                symbol.enableTrading && 
                (symbol.quoteCurrency === 'USDT' || symbol.quoteCurrency === 'USDC')
            )
            .map(symbol => symbol.baseCurrency)
            .filter((asset, index, arr) => arr.indexOf(asset) === index)
            .sort();

        this.cache.set(cacheKey, {
            data: assets,
            timestamp: Date.now()
        });

        return assets;
    }

    // COMPLETE REAL-TIME PRICE TRACKING - REAL DATA ONLY
    async getRealTimePrices(symbols, exchanges) {
    console.log(`ðŸ“Š Getting real-time prices for ${symbols.length} symbols across ${exchanges.length} exchanges...`);

    const results = {};
    const symbolList = Array.isArray(symbols) ? symbols : symbols.split(',').map(s => s.trim().toUpperCase());

    // Filter out blacklisted symbols if validBinanceSymbols exists
    const filteredSymbols = symbolList.filter(symbol => {
        if (this.symbolBlacklist && this.symbolBlacklist.has(symbol)) {
            console.log(`Skipping blacklisted symbol: ${symbol}`);
            return false;
        }
        return true;
    });

    // Use filtered symbols instead
    const symbolsToProcess = filteredSymbols;

    const exchangeList = Array.isArray(exchanges) ? exchanges : [exchanges];

    // Batch processing for efficiency
    const BATCH_SIZE = 10;
    const batches = [];
    for (let i = 0; i < symbolsToProcess.length; i += BATCH_SIZE) {
        batches.push(symbolsToProcess.slice(i, i + BATCH_SIZE));
    }

    for (const exchange of exchangeList) {
        results[exchange] = {};

        for (const batch of batches) {
            const batchPromises = batch.map(symbol => 
                this.fetchPriceWithValidation(symbol, exchange)
                    .then(price => {
                        if (price) results[exchange][symbol] = price;
                    })
                    .catch(error => {
                        // Only log if it's not a "symbol not found" error
                        if (!error.message.includes('Unknown asset') && 
                            !error.message.includes('Invalid symbol') &&
                            !error.message.includes('No data received')) {
                            console.error(`Price fetch failed for ${symbol} on ${exchange}:`, error.message);
                        }
                        results[exchange][symbol] = { error: 'Not available', timestamp: Date.now() };
                    })
            );

            await Promise.allSettled(batchPromises);
        }
    }

    return results;
}

    async fetchPriceWithValidation(symbol, exchange) {
    // Check blacklist first
    if (this.symbolBlacklist && this.symbolBlacklist.has(symbol)) {
        return null;
    }

    // Check if valid for Binance
    if (exchange === 'binance' && this.validBinanceSymbols && !this.validBinanceSymbols.has(symbol)) {
        return null;
    }

      // Check if this symbol exists on this exchange
    const symbolFormat = this.getSymbolFormat(symbol, exchange);
    if (!symbolFormat) return null;

    const cacheKey = `${exchange}_${symbol}_price`;
    const cached = this.cache.get(cacheKey);

    if (cached && Date.now() - cached.timestamp < 5000) {
        return cached.data;
    }

    try {
        const priceData = await this.fetchRealTimePrice(symbol, exchange);

        this.cache.set(cacheKey, {
            data: priceData,
            timestamp: Date.now()
        });

        return priceData;
    } catch (error) {
        // Don't throw for expected errors
        if (error.message.includes('Unknown asset') || 
            error.message.includes('Invalid symbol')) {
            return null;
        }
        throw error;
    }
    }

    getSymbolFormat(symbol, exchange) {
    const knownFormats = {
        binance: {
            'BTC': 'BTCUSDT', 'ETH': 'ETHUSDT', 'SOL': 'SOLUSDT',
            'ADA': 'ADAUSDT', 'DOT': 'DOTUSDT', 'LINK': 'LINKUSDT',
            'AVAX': 'AVAXUSDT', 'MATIC': 'MATICUSDT', 'UNI': 'UNIUSDT'
        },
        kraken: {
            'BTC': 'XXBTZUSD', 'ETH': 'XETHZUSD', 'SOL': 'SOLUSD',
            'ADA': 'ADAUSD', 'DOT': 'DOTUSD', 'LINK': 'LINKUSD',
            'AVAX': 'AVAXUSD', 'MATIC': 'MATICUSD', 'UNI': 'UNIUSD'
        },
        kucoin: {
            'BTC': 'BTC-USDT', 'ETH': 'ETH-USDT', 'SOL': 'SOL-USDT',
            'ADA': 'ADA-USDT', 'DOT': 'DOT-USDT', 'LINK': 'LINK-USDT',
            'AVAX': 'AVAX-USDT', 'MATIC': 'MATIC-USDT', 'UNI': 'UNI-USDT'
        }
    };

    // Return known format or generate default
    if (knownFormats[exchange] && knownFormats[exchange][symbol]) {
        return knownFormats[exchange][symbol];
    }

    // Generate default format
    switch(exchange) {
        case 'binance': return `${symbol}USDT`;
        case 'kraken': return `${symbol}USD`;
        case 'kucoin': return `${symbol}-USDT`;
        default: return null;
    }
    }


    async fetchRealTimePrice(symbol, exchange) {
        const cacheKey = `${exchange}_${symbol}_price`;
        const cached = this.cache.get(cacheKey);

        if (cached && Date.now() - cached.timestamp < 5000) { // 5 second cache for prices
            return cached.data;
        }

        let priceData;

        try {
            switch (exchange) {
                case 'binance':
                    priceData = await this.fetchBinancePrice(symbol);
                    break;
                case 'kraken':
                    priceData = await this.fetchKrakenPrice(symbol);
                    break;
                case 'kucoin':
                    priceData = await this.fetchKucoinPrice(symbol);
                    break;
                case 'coingecko':
                    priceData = await this.fetchCoinGeckoPrice(symbol);
                    break;
                default:
                    throw new Error(`Unsupported exchange: ${exchange}`);
            }

            this.cache.set(cacheKey, {
                data: priceData,
                timestamp: Date.now()
            });

            return priceData;

        } catch (error) {
            throw error; // Don't use fallback with mock data
        }
    }

    async fetchBinancePrice(symbol) {
        await this.respectRateLimit('binance');

        const pair = `${symbol}USDT`;
        const response = await fetch(
            `${this.exchangeConfigs.binance.baseUrl}/api/v3/ticker/24hr?symbol=${pair}`,
            {
                timeout: 10000,
                headers: { 'User-Agent': 'Crypto-Universe-Enterprise/2.0' }
            }
        );

        if (!response.ok) {
            throw new Error(`Binance price fetch failed for ${symbol}: ${response.status}`);
        }

        const data = await response.json();

        return {
            exchange: 'binance',
            symbol: symbol,
            price: parseFloat(data.lastPrice),
            change24h: parseFloat(data.priceChangePercent),
            volume24h: parseFloat(data.volume),
            high24h: parseFloat(data.highPrice),
            low24h: parseFloat(data.lowPrice),
            bid: parseFloat(data.bidPrice),
            ask: parseFloat(data.askPrice),
            timestamp: Date.now(),
            source: 'binance_api'
        };
    }

    async fetchKrakenPrice(symbol) {
        await this.respectRateLimit('kraken');

        const pairMappings = {
            'BTC': 'XXBTZUSD',
            'ETH': 'XETHZUSD',
            'XBT': 'XXBTZUSD'
        };

        const pair = pairMappings[symbol] || `${symbol}USD`;
        const response = await fetch(
            `${this.exchangeConfigs.kraken.baseUrl}/0/public/Ticker?pair=${pair}`,
            {
                timeout: 10000,
                headers: { 'User-Agent': 'Crypto-Universe-Enterprise/2.0' }
            }
        );

        if (!response.ok) {
            throw new Error(`Kraken price fetch failed for ${symbol}: ${response.status}`);
        }

        const data = await response.json();

        if (data.error && data.error.length > 0) {
            throw new Error(`Kraken errors: ${data.error.join(', ')}`);
        }

        const ticker = Object.values(data.result)[0];
        if (!ticker) {
            throw new Error(`No ticker data for ${symbol} on Kraken`);
        }

        const currentPrice = parseFloat(ticker.c[0]);
        const openPrice = parseFloat(ticker.o);

        return {
            exchange: 'kraken',
            symbol: symbol,
            price: currentPrice,
            change24h: openPrice > 0 ? ((currentPrice - openPrice) / openPrice) * 100 : 0,
            volume24h: parseFloat(ticker.v[1]),
            high24h: parseFloat(ticker.h[1]),
            low24h: parseFloat(ticker.l[1]),
            bid: parseFloat(ticker.b[0]),
            ask: parseFloat(ticker.a[0]),
            timestamp: Date.now(),
            source: 'kraken_api'
        };
    }

    async fetchKucoinPrice(symbol) {
        await this.respectRateLimit('kucoin');

        const pair = `${symbol}-USDT`;
        const response = await fetch(
            `${this.exchangeConfigs.kucoin.baseUrl}/api/v1/market/stats?symbol=${pair}`,
            {
                timeout: 10000,
                headers: { 'User-Agent': 'Crypto-Universe-Enterprise/2.0' }
            }
        );

        if (!response.ok) {
            throw new Error(`KuCoin price fetch failed for ${symbol}: ${response.status}`);
        }

        const data = await response.json();

        if (data.code !== '200000') {
            throw new Error(`KuCoin API error: ${data.msg}`);
        }

        return {
            exchange: 'kucoin',
            symbol: symbol,
            price: parseFloat(data.data.last),
            change24h: parseFloat(data.data.changeRate) * 100,
            volume24h: parseFloat(data.data.vol),
            high24h: parseFloat(data.data.high),
            low24h: parseFloat(data.data.low),
            bid: parseFloat(data.data.buy),
            ask: parseFloat(data.data.sell),
            timestamp: Date.now(),
            source: 'kucoin_api'
        };
    }

    async fetchCoinGeckoPrice(symbol) {
        // Dynamic fetching of coin IDs from CoinGecko
        const coinListResponse = await fetch('https://api.coingecko.com/api/v3/coins/list');
        if (!coinListResponse.ok) {
            throw new Error(`CoinGecko list fetch failed: ${coinListResponse.status}`);
        }

        const coinList = await coinListResponse.json();
        const symbolLower = symbol.toLowerCase();
        const coin = coinList.find(c => c.symbol === symbolLower);

        if (!coin) {
            throw new Error(`CoinGecko mapping not found for ${symbol}`);
        }

        const response = await fetch(
            `${this.exchangeConfigs.coingecko.baseUrl}/simple/price?ids=${coin.id}&vs_currencies=usd&include_24hr_change=true&include_24hr_vol=true`,
            {
                timeout: 10000,
                headers: { 'User-Agent': 'Crypto-Universe-Enterprise/2.0' }
            }
        );

        if (!response.ok) {
            throw new Error(`CoinGecko API error: ${response.status}`);
        }

        const data = await response.json();
        const coinData = data[coin.id];

        if (!coinData) {
            throw new Error(`No CoinGecko data for ${symbol}`);
        }

        const price = coinData.usd;
        const change24h = coinData.usd_24h_change || 0;

        return {
            exchange: 'coingecko',
            symbol: symbol,
            price: price,
            change24h: change24h,
            volume24h: coinData.usd_24h_vol || 0,
            high24h: price * (1 + Math.abs(change24h) / 200), // Estimate based on change
            low24h: price * (1 - Math.abs(change24h) / 200), // Estimate based on change
            bid: price * 0.999,
            ask: price * 1.001,
            timestamp: Date.now(),
            source: 'coingecko_api'
        };
    }

    // Continue with remaining methods...
// ===== PART 2: TECHNICAL ANALYSIS & HISTORICAL DATA =====

    // COMPLETE TECHNICAL ANALYSIS - REAL DATA ONLY
    async performTechnicalAnalysis(symbols, timeframe, exchanges) {
        console.log(`ðŸ“ˆ Performing technical analysis for ${symbols.length} symbols...`);

        const results = {};
        const symbolList = Array.isArray(symbols) ? symbols : symbols.split(',').map(s => s.trim().toUpperCase());
        const exchangeList = Array.isArray(exchanges) ? exchanges : [exchanges];

        for (const symbol of symbolList) {
            try {
                // Get historical data for technical indicators
                const historicalData = await this.getHistoricalData(symbol, timeframe, exchangeList[0]);
                const currentPrice = await this.fetchRealTimePrice(symbol, exchangeList[0]);

                if (historicalData && currentPrice) {
                    const technicalIndicators = this.calculateTechnicalIndicators(historicalData, currentPrice);

                    results[symbol] = {
                        current_price: currentPrice.price,
                        timeframe: timeframe,
                        exchange: exchangeList[0],
                        indicators: technicalIndicators,
                        signals: this.generateTechnicalSignals(technicalIndicators),
                        analysis_time: new Date().toISOString()
                    };
                }
            } catch (error) {
                console.error(`Technical analysis failed for ${symbol}:`, error.message);
                results[symbol] = {
                    error: error.message,
                    analysis_time: new Date().toISOString()
                };
            }
        }

        return results;
    }

    async getHistoricalData(symbol, timeframe, exchange) {
        const cacheKey = `${exchange}_${symbol}_${timeframe}_historical`;
        const cached = this.cache.get(cacheKey);

        if (cached && Date.now() - cached.timestamp < 300000) { // 5 minute cache
            return cached.data;
        }

        let historicalData;

        try {
            switch (exchange) {
                case 'binance':
                    historicalData = await this.fetchBinanceHistorical(symbol, timeframe);
                    break;
                case 'kraken':
                    historicalData = await this.fetchKrakenHistorical(symbol, timeframe);
                    break;
                case 'kucoin':
                    historicalData = await this.fetchKucoinHistorical(symbol, timeframe);
                    break;
                default:
                    throw new Error(`Historical data not supported for ${exchange}`);
            }

            this.cache.set(cacheKey, {
                data: historicalData,
                timestamp: Date.now()
            });

            return historicalData;

        } catch (error) {
            console.error(`Failed to get historical data for ${symbol}:`, error.message);
            throw error;
        }
    }

    async fetchBinanceHistorical(symbol, timeframe) {
        await this.respectRateLimit('binance');

        const interval = this.mapTimeframeToInterval(timeframe, 'binance');
        const pair = `${symbol}USDT`;

        const response = await fetch(
            `${this.exchangeConfigs.binance.baseUrl}/api/v3/klines?symbol=${pair}&interval=${interval}&limit=100`,
            {
                timeout: 15000,
                headers: { 'User-Agent': 'Crypto-Universe-Enterprise/2.0' }
            }
        );

        if (!response.ok) {
            throw new Error(`Binance historical data failed: ${response.status}`);
        }

        const data = await response.json();

        return data.map(candle => ({
            timestamp: candle[0],
            open: parseFloat(candle[1]),
            high: parseFloat(candle[2]),
            low: parseFloat(candle[3]),
            close: parseFloat(candle[4]),
            volume: parseFloat(candle[5])
        }));
    }

    async fetchKrakenHistorical(symbol, timeframe) {
        await this.respectRateLimit('kraken');

        const interval = this.mapTimeframeToInterval(timeframe, 'kraken');
        const pairMappings = {
            'BTC': 'XXBTZUSD',
            'ETH': 'XETHZUSD',
            'XBT': 'XXBTZUSD'
        };

        const pair = pairMappings[symbol] || `${symbol}USD`;

        const response = await fetch(
            `${this.exchangeConfigs.kraken.baseUrl}/0/public/OHLC?pair=${pair}&interval=${interval}`,
            {
                timeout: 15000,
                headers: { 'User-Agent': 'Crypto-Universe-Enterprise/2.0' }
            }
        );

        if (!response.ok) {
            throw new Error(`Kraken historical data failed: ${response.status}`);
        }

        const data = await response.json();

        if (data.error && data.error.length > 0) {
            throw new Error(`Kraken errors: ${data.error.join(', ')}`);
        }

        const ohlcData = Object.values(data.result)[0];
        if (!ohlcData) {
            throw new Error('No OHLC data from Kraken');
        }

        return ohlcData.map(candle => ({
            timestamp: candle[0] * 1000,
            open: parseFloat(candle[1]),
            high: parseFloat(candle[2]),
            low: parseFloat(candle[3]),
            close: parseFloat(candle[4]),
            volume: parseFloat(candle[6])
        }));
    }

    async fetchKucoinHistorical(symbol, timeframe) {
        await this.respectRateLimit('kucoin');

        const type = this.mapTimeframeToInterval(timeframe, 'kucoin');
        const pair = `${symbol}-USDT`;
        const endAt = Math.floor(Date.now() / 1000);
        const startAt = endAt - (100 * this.getSecondsForTimeframe(timeframe));

        const response = await fetch(
            `${this.exchangeConfigs.kucoin.baseUrl}/api/v1/market/candles?type=${type}&symbol=${pair}&startAt=${startAt}&endAt=${endAt}`,
            {
                timeout: 15000,
                headers: { 'User-Agent': 'Crypto-Universe-Enterprise/2.0' }
            }
        );

        if (!response.ok) {
            throw new Error(`KuCoin historical data failed: ${response.status}`);
        }

        const data = await response.json();

        if (data.code !== '200000') {
            throw new Error(`KuCoin API error: ${data.msg}`);
        }

        return data.data.map(candle => ({
            timestamp: parseInt(candle[0]) * 1000,
            open: parseFloat(candle[1]),
            close: parseFloat(candle[2]),
            high: parseFloat(candle[3]),
            low: parseFloat(candle[4]),
            volume: parseFloat(candle[5])
        })).reverse(); // KuCoin returns newest first
    }

    getSecondsForTimeframe(timeframe) {
        const seconds = {
            '1m': 60, '5m': 300, '15m': 900, '30m': 1800,
            '1h': 3600, '4h': 14400, '1d': 86400, '1w': 604800
        };
        return seconds[timeframe] || 3600;
    }

    calculateTechnicalIndicators(historicalData, currentPrice) {
        if (!historicalData || historicalData.length < 20) {
            return null;
        }

        const prices = historicalData.map(d => d.close);
        const volumes = historicalData.map(d => d.volume);
        const highs = historicalData.map(d => d.high);
        const lows = historicalData.map(d => d.low);

        return {
            sma_20: this.calculateSMA(prices, 20),
            ema_12: this.calculateEMA(prices, 12),
            ema_26: this.calculateEMA(prices, 26),
            rsi: this.calculateRSI(prices, 14),
            macd: this.calculateMACD(prices),
            bollinger_bands: this.calculateBollingerBands(prices, 20, 2),
            volume_sma: this.calculateSMA(volumes, 20),
            atr: this.calculateATR(highs, lows, prices, 14),
            stochastic: this.calculateStochastic(highs, lows, prices, 14),
            williams_r: this.calculateWilliamsR(highs, lows, prices, 14),
            obv: this.calculateOBV(prices, volumes),
            vwap: this.calculateVWAP(prices, volumes)
        };
    }

    calculateSMA(prices, period) {
        if (prices.length < period) return null;

        const recentPrices = prices.slice(-period);
        const sum = recentPrices.reduce((acc, price) => acc + price, 0);
        return sum / period;
    }

    calculateEMA(prices, period) {
        if (prices.length < period) return null;

        const multiplier = 2 / (period + 1);
        let ema = prices[0];

        for (let i = 1; i < prices.length; i++) {
            ema = (prices[i] * multiplier) + (ema * (1 - multiplier));
        }

        return ema;
    }

    calculateRSI(prices, period) {
        if (prices.length < period + 1) return null;

        let gains = 0;
        let losses = 0;

        for (let i = 1; i <= period; i++) {
            const change = prices[prices.length - i] - prices[prices.length - i - 1];
            if (change > 0) {
                gains += change;
            } else {
                losses -= change;
            }
        }

        const avgGain = gains / period;
        const avgLoss = losses / period;

        if (avgLoss === 0) return 100;

        const rs = avgGain / avgLoss;
        return 100 - (100 / (1 + rs));
    }

    calculateMACD(prices) {
        const ema12 = this.calculateEMA(prices, 12);
        const ema26 = this.calculateEMA(prices, 26);

        if (!ema12 || !ema26) return null;

        const macdLine = ema12 - ema26;

        // Calculate signal line using 9-period EMA of MACD
        const macdValues = [];
        for (let i = 26; i < prices.length; i++) {
            const ema12Temp = this.calculateEMA(prices.slice(0, i + 1), 12);
            const ema26Temp = this.calculateEMA(prices.slice(0, i + 1), 26);
            if (ema12Temp && ema26Temp) {
                macdValues.push(ema12Temp - ema26Temp);
            }
        }

        const signalLine = this.calculateEMA(macdValues, 9) || macdLine * 0.9;

        return {
            macd: macdLine,
            signal: signalLine,
            histogram: macdLine - signalLine
        };
    }

    calculateBollingerBands(prices, period, multiplier) {
        const sma = this.calculateSMA(prices, period);
        if (!sma) return null;

        const recentPrices = prices.slice(-period);
        const variance = recentPrices.reduce((acc, price) => acc + Math.pow(price - sma, 2), 0) / period;
        const stdDev = Math.sqrt(variance);

        return {
            upper: sma + (stdDev * multiplier),
            middle: sma,
            lower: sma - (stdDev * multiplier),
            std_dev: stdDev
        };
    }

    calculateATR(highs, lows, closes, period) {
        if (highs.length < period + 1) return null;

        const trueRanges = [];

        for (let i = 1; i < highs.length; i++) {
            const tr1 = highs[i] - lows[i];
            const tr2 = Math.abs(highs[i] - closes[i - 1]);
            const tr3 = Math.abs(lows[i] - closes[i - 1]);

            trueRanges.push(Math.max(tr1, tr2, tr3));
        }

        return this.calculateSMA(trueRanges, period);
    }

    calculateStochastic(highs, lows, closes, period) {
        if (highs.length < period) return null;

        const recentHighs = highs.slice(-period);
        const recentLows = lows.slice(-period);
        const currentClose = closes[closes.length - 1];

        const highestHigh = Math.max(...recentHighs);
        const lowestLow = Math.min(...recentLows);

        if (highestHigh === lowestLow) return { k: 50, d: 50 };

        const k = ((currentClose - lowestLow) / (highestHigh - lowestLow)) * 100;

        // Calculate D as 3-period SMA of K values
        const kValues = [];
        for (let i = period; i <= closes.length && kValues.length < 3; i++) {
            const tempHighs = highs.slice(i - period, i);
            const tempLows = lows.slice(i - period, i);
            const tempClose = closes[i - 1];

            const tempHighest = Math.max(...tempHighs);
            const tempLowest = Math.min(...tempLows);

            if (tempHighest !== tempLowest) {
                kValues.push(((tempClose - tempLowest) / (tempHighest - tempLowest)) * 100);
            }
        }

        const d = kValues.length > 0 ? kValues.reduce((a, b) => a + b, 0) / kValues.length : k;

        return { k, d };
    }

    calculateWilliamsR(highs, lows, closes, period) {
        if (highs.length < period) return null;

        const recentHighs = highs.slice(-period);
        const recentLows = lows.slice(-period);
        const currentClose = closes[closes.length - 1];

        const highestHigh = Math.max(...recentHighs);
        const lowestLow = Math.min(...recentLows);

        if (highestHigh === lowestLow) return -50;

        return ((highestHigh - currentClose) / (highestHigh - lowestLow)) * -100;
    }

    calculateOBV(prices, volumes) {
        if (prices.length !== volumes.length || prices.length < 2) return null;

        let obv = 0;
        for (let i = 1; i < prices.length; i++) {
            if (prices[i] > prices[i - 1]) {
                obv += volumes[i];
            } else if (prices[i] < prices[i - 1]) {
                obv -= volumes[i];
            }
        }

        return obv;
    }

    calculateVWAP(prices, volumes) {
        if (prices.length !== volumes.length || prices.length === 0) return null;

        let cumVolume = 0;
        let cumPriceVolume = 0;

        for (let i = 0; i < prices.length; i++) {
            cumVolume += volumes[i];
            cumPriceVolume += prices[i] * volumes[i];
        }

        return cumVolume > 0 ? cumPriceVolume / cumVolume : null;
    }

    generateTechnicalSignals(indicators) {
        if (!indicators) return null;

        const signals = [];
        let overallSignal = 'HOLD';
        let bullishCount = 0;
        let bearishCount = 0;

        // RSI Signal
        if (indicators.rsi) {
            if (indicators.rsi > 70) {
                signals.push({ indicator: 'RSI', signal: 'SELL', strength: 'STRONG', value: indicators.rsi });
                bearishCount += 2;
            } else if (indicators.rsi < 30) {
                signals.push({ indicator: 'RSI', signal: 'BUY', strength: 'STRONG', value: indicators.rsi });
                bullishCount += 2;
            } else if (indicators.rsi > 60) {
                signals.push({ indicator: 'RSI', signal: 'SELL', strength: 'WEAK', value: indicators.rsi });
                bearishCount += 1;
            } else if (indicators.rsi < 40) {
                signals.push({ indicator: 'RSI', signal: 'BUY', strength: 'WEAK', value: indicators.rsi });
                bullishCount += 1;
            }
        }

        // MACD Signal
        if (indicators.macd && indicators.macd.histogram) {
            if (indicators.macd.histogram > 0) {
                signals.push({ indicator: 'MACD', signal: 'BUY', strength: 'MEDIUM', value: indicators.macd.histogram });
                bullishCount += 1;
            } else {
                signals.push({ indicator: 'MACD', signal: 'SELL', strength: 'MEDIUM', value: indicators.macd.histogram });
                bearishCount += 1;
            }
        }

        // Bollinger Bands Signal
        if (indicators.bollinger_bands) {
            signals.push({ 
                indicator: 'BOLLINGER', 
                signal: 'HOLD', 
                strength: 'NEUTRAL',
                upper: indicators.bollinger_bands.upper,
                lower: indicators.bollinger_bands.lower
            });
        }

        // Stochastic Signal
        if (indicators.stochastic) {
            if (indicators.stochastic.k > 80) {
                signals.push({ indicator: 'STOCHASTIC', signal: 'SELL', strength: 'MEDIUM', value: indicators.stochastic.k });
                bearishCount += 1;
            } else if (indicators.stochastic.k < 20) {
                signals.push({ indicator: 'STOCHASTIC', signal: 'BUY', strength: 'MEDIUM', value: indicators.stochastic.k });
                bullishCount += 1;
            }
        }

        // Overall Signal
        if (bullishCount > bearishCount + 1) {
            overallSignal = 'BUY';
        } else if (bearishCount > bullishCount + 1) {
            overallSignal = 'SELL';
        }

        return {
            overall_signal: overallSignal,
            signal_strength: Math.abs(bullishCount - bearishCount),
            bullish_indicators: bullishCount,
            bearish_indicators: bearishCount,
            individual_signals: signals
        };
    }

    mapTimeframeToInterval(timeframe, exchange) {
        const mappings = {
            binance: {
                '1m': '1m', '5m': '5m', '15m': '15m', '30m': '30m',
                '1h': '1h', '4h': '4h', '1d': '1d', '1w': '1w'
            },
            kraken: {
                '1m': '1', '5m': '5', '15m': '15', '30m': '30',
                '1h': '60', '4h': '240', '1d': '1440', '1w': '10080'
            },
            kucoin: {
                '1m': '1min', '5m': '5min', '15m': '15min', '30m': '30min',
                '1h': '1hour', '4h': '4hour', '1d': '1day', '1w': '1week'
            }
        };

        return mappings[exchange]?.[timeframe] || mappings[exchange]?.['1h'] || '1h';
    }

    async respectRateLimit(exchange) {
        const now = Date.now();
        const lastRequest = this.lastRequestTimes.get(exchange) || 0;
        const minInterval = 100; // 100ms minimum between requests

        const timeSinceLastRequest = now - lastRequest;
        if (timeSinceLastRequest < minInterval) {
            await new Promise(resolve => setTimeout(resolve, minInterval - timeSinceLastRequest));
        }

        this.lastRequestTimes.set(exchange, Date.now());
    }
}
