function determineBasisTrend(currentBasis, historicalBasis) {
    if (!historicalBasis || historicalBasis.length === 0) {
        return 'UNKNOWN';
    }

    const avgHistorical = historicalBasis.reduce((sum, b) => sum + b, 0) / historicalBasis.length;

    if (currentBasis > avgHistorical * 1.1) return 'WIDENING';
    if (currentBasis < avgHistorical * 0.9) return 'NARROWING';
    return 'STABLE';
}

