        const arbitrageOpportunities = [];
        const symbolList = Array.isArray(symbols) ? symbols : symbols.split(',').map(s => s.trim().toUpperCase());
        const exchangeList = Array.isArray(exchanges) ? exchanges : 
                            exchanges === 'major' ? ['binance', 'kraken', 'kucoin'] : 
                            exchanges === 'all' ? ['binance', 'kraken', 'kucoin'] :
                            exchanges.split(',').map(e => e.trim().toLowerCase());

        for (const symbol of symbolList) {
            try {
                // GET REAL PRICES
                const realPrices = await this.exchangeManager.getRealTimePrices([symbol], exchangeList);
                const exchangePrices = [];

                for (const exchange of exchangeList) {
                    const priceData = realPrices[exchange]?.[symbol];
                    if (priceData && !priceData.error) {
                        exchangePrices.push({
                            exchange,
                            bid: priceData.bid || priceData.price * 0.9995,
                            ask: priceData.ask || priceData.price * 1.0005,
                            bidVolume: priceData.volume24h * 0.001 || 100, // Real volume estimate
                            askVolume: priceData.volume24h * 0.001 || 100,
                            spread: (priceData.ask - priceData.bid) || priceData.price * 0.001,
                            timestamp: priceData.timestamp,
                            price: priceData.price,
                            volume24h: priceData.volume24h || 0
                        });
                    }
                }

                // Find arbitrage opportunities using REAL data
                for (let i = 0; i < exchangePrices.length; i++) {
                    for (let j = i + 1; j < exchangePrices.length; j++) {
                        const buyExchange = exchangePrices[i];
                        const sellExchange = exchangePrices[j];

                        const opportunities = [
                            { buy: buyExchange, sell: sellExchange },
                            { buy: sellExchange, sell: buyExchange }
                        ];

                        for (const opp of opportunities) {
                            if (opp.buy.ask < opp.sell.bid) {
                                const priceDiff = opp.sell.bid - opp.buy.ask;
                                const profitBps = (priceDiff / opp.buy.ask) * 10000;

                                if (profitBps >= minProfitBps) {
                                    const maxVolume = Math.min(opp.buy.askVolume, opp.sell.bidVolume);
                                    const estimatedProfit = priceDiff * maxVolume * 0.8; // 80% execution efficiency
                                    const totalFees = (opp.buy.ask + opp.sell.bid) * maxVolume * 0.002; // 0.2% total fees
                                    const netProfit = estimatedProfit - totalFees;

                                    if (netProfit > 10) { // Minimum $10 profit
                                        arbitrageOpportunities.push({
                                            symbol,
                                            buy_exchange: opp.buy.exchange,
                                            sell_exchange: opp.sell.exchange,
                                            buy_price: opp.buy.ask,
                                            sell_price: opp.sell.bid,
