    // MARKET INEFFICIENCY SCANNER WITH REAL DATA
    async marketInefficiencyScanner(symbols, confidenceLevel) {
        console.log('ðŸ” Scanning market inefficiencies WITH REAL DATA...');

        const inefficiencies = [];
        const symbolList = Array.isArray(symbols) ? symbols : symbols.split(',').map(s => s.trim().toUpperCase());

        for (const symbol of symbolList) {
            try {
                // Get REAL market data
                const realPrices = await this.exchangeManager.getRealTimePrices([symbol], ['binance', 'kraken', 'kucoin']);
                const technicalData = await this.exchangeManager.performTechnicalAnalysis([symbol], '1h', ['binance']);

                const detections = [];

                // 1. VOLUME-PRICE DIVERGENCE
                const volumeDivergence = await this.detectVolumePriceDivergence(symbol, realPrices, technicalData);
                if (volumeDivergence) {
                    detections.push(volumeDivergence);
                }

                // 2. FUNDING RATE ARBITRAGE
                const fundingArbitrage = await this.detectFundingRateArbitrage(symbol);
                if (fundingArbitrage) {
                    detections.push(fundingArbitrage);
                }

                // 3. ORDER BOOK MANIPULATION
                const orderBookManipulation = await this.detectOrderBookManipulation(symbol, realPrices);
                if (orderBookManipulation) {
                    detections.push(orderBookManipulation);
                }

                // 4. SPREAD ANOMALIES
                const spreadAnomalies = await this.detectSpreadAnomalies(symbol, realPrices);
                if (spreadAnomalies) {
                    detections.push(spreadAnomalies);
                }

                // 5. MOMENTUM DIVERGENCE
                const momentumDivergence = await this.detectMomentumDivergence(symbol, technicalData);
                if (momentumDivergence) {
                    detections.push(momentumDivergence);
                }

                const significantInefficiencies = detections.filter(detection => {
                    if (confidenceLevel === 'high' && detection.confidence !== 'HIGH') return false;
                    if (detection.strength && detection.strength < 0.6 && confidenceLevel === 'high') return false;
                    if (detection.profit_potential && detection.profit_potential < 0.1) return false;
                    return true;
                });

                significantInefficiencies.forEach(ineff => {
                    inefficiencies.push({
                        symbol,
                        ...ineff,
                        timestamp: new Date().toISOString(),
                        data_source: 'REAL_MARKET_DATA'
                    });
                });

            } catch (error) {
                console.log(`Error analyzing ${symbol}:`, error.message);
            }
        }

        inefficiencies.sort((a, b) => {
            const scoreA = (a.profit_potential || a.strength || 0) * this.getConfidenceMultiplier(a.confidence);
            const scoreB = (b.profit_potential || b.strength || 0) * this.getConfidenceMultiplier(b.confidence);
            return scoreB - scoreA;
        });

        const actionableOpportunities = inefficiencies.filter(ineff => 
            ineff.confidence === 'HIGH' && (ineff.profit_potential > 0.15 || ineff.strength > 0.7)
        );

        return {
            success: true,
            timestamp: new Date().toISOString(),
            inefficiency_scan: {
                symbols_analyzed: symbolList,
                confidence_level: confidenceLevel,
                total_inefficiencies: inefficiencies.length,
                detected_inefficiencies: inefficiencies,
                actionable_opportunities: actionableOpportunities,
                market_efficiency_score: this.calculateMarketEfficiencyScore(inefficiencies),
                data_quality: 'REAL_TIME_MARKET_DATA',
                analysis_depth: 'COMPREHENSIVE'
            }
        };
    }

