    // ===== REAL DATA DETECTION METHODS =====

    async detectVolumePriceDivergence(symbol, realPrices, technicalData) {
        const exchanges = Object.keys(realPrices);
        if (exchanges.length === 0) return null;

        let totalVolume = 0;
        let avgPriceChange = 0;
        let validExchanges = 0;

        for (const exchange of exchanges) {
            const priceData = realPrices[exchange][symbol];
            if (priceData && !priceData.error) {
                totalVolume += priceData.volume24h || 0;
                avgPriceChange += priceData.change24h || 0;
                validExchanges++;
            }
        }

        if (validExchanges === 0) return null;

        avgPriceChange = avgPriceChange / validExchanges;

        const volumeNormalized = totalVolume / 1000000; // Normalize to millions
        const divergenceStrength = Math.abs(avgPriceChange) / (volumeNormalized + 1);

        if (divergenceStrength > 0.5) {
            return {
                type: 'VOLUME_PRICE_DIVERGENCE',
                strength: Math.min(divergenceStrength, 1.0),
                direction: avgPriceChange > 0 ? 'BULLISH' : 'BEARISH',
                expected_reversion_time: '2-6 hours',
                confidence: divergenceStrength > 0.8 ? 'HIGH' : 'MEDIUM',
                volume_intensity: volumeNormalized,
                price_change: avgPriceChange,
                profit_potential: divergenceStrength * 0.02
            };
        }

        return null;
    }

    async detectFundingRateArbitrage(symbol) {
        try {
            // Fetch REAL funding rates
            const fundingRates = await Promise.all([
                fetchBinanceFundingRate(symbol),
                fetchBybitFundingRate(symbol),
                fetchDeribitFundingRate(symbol)
            ]);

            const validRates = fundingRates.filter(r => r !== null);
            if (validRates.length === 0) return null;

            const maxRate = Math.max(...validRates);
            const minRate = Math.min(...validRates);
            const spread = maxRate - minRate;

            if (Math.abs(spread) > 0.0005) { // 0.05% threshold
                return {
                    type: 'FUNDING_RATE_ARBITRAGE',
                    profit_potential: Math.abs(spread) * 3, // 3 funding periods per day
                    annualized_yield: Math.abs(spread) * 3 * 365,
                    risk_level: 'LOW',
                    confidence: 'HIGH',
                    funding_rate_spread: spread,
                    max_rate: maxRate,
                    min_rate: minRate,
                    strategy: spread > 0 ? 'SHORT_HIGH_LONG_LOW' : 'LONG_HIGH_SHORT_LOW',
                    data_source: 'REAL_EXCHANGE_DATA'
                };
            }

            return null;
        } catch (error) {
            console.error('Funding rate fetch failed:', error.message);
            return null;
        }
    }

    async detectOrderBookManipulation(symbol, realPrices) {
        const exchanges = Object.keys(realPrices);
        let manipulationScore = 0;

        for (const exchange of exchanges) {
            const priceData = realPrices[exchange][symbol];
            if (priceData && !priceData.error) {
                const spread = priceData.ask - priceData.bid;
                const spreadPct = (spread / priceData.price) * 100;

                if (spreadPct > 0.5) { // >0.5% spread is unusual
                    manipulationScore += spreadPct;
                }
            }
        }

        if (manipulationScore > 0.3) {
            const avgPrice = Object.values(realPrices).reduce((sum, ex) => {
                const p = ex[symbol];
                return sum + (p && !p.error ? p.price : 0);
            }, 0) / exchanges.length;

            return {
                type: 'ORDER_BOOK_MANIPULATION',
                significance: Math.min(manipulationScore, 1.0),
                anomaly_type: 'WIDE_SPREADS',
                likely_direction: avgPrice > 0 ? 'DETECTED' : 'UNCERTAIN',
                confidence: manipulationScore > 0.7 ? 'HIGH' : 'MEDIUM',
                profit_potential: manipulationScore * 0.01
            };
        }

        return null;
    }

    async detectSpreadAnomalies(symbol, realPrices) {
        const exchanges = Object.keys(realPrices);
        if (exchanges.length < 2) return null;

        const spreads = [];
        for (const exchange of exchanges) {
            const priceData = realPrices[exchange][symbol];
            if (priceData && !priceData.error && priceData.bid && priceData.ask) {
                const spread = (priceData.ask - priceData.bid) / priceData.price * 100;
                spreads.push(spread);
            }
        }

        if (spreads.length < 2) return null;

        const avgSpread = spreads.reduce((sum, s) => sum + s, 0) / spreads.length;
        const maxSpread = Math.max(...spreads);
        const minSpread = Math.min(...spreads);

        const spreadVariance = maxSpread - minSpread;

        if (spreadVariance > avgSpread * 2) {
            return {
                type: 'SPREAD_ANOMALY',
                variance: spreadVariance,
                avg_spread: avgSpread,
                max_spread: maxSpread,
                min_spread: minSpread,
                confidence: spreadVariance > avgSpread * 3 ? 'HIGH' : 'MEDIUM',
                profit_potential: spreadVariance * 0.5,
                arbitrage_opportunity: true
            };
        }

        return null;
    }

    async detectMomentumDivergence(symbol, technicalData) {
        if (!technicalData || !technicalData[symbol]) return null;

        const indicators = technicalData[symbol].indicators;
        if (!indicators) return null;

        if (indicators.rsi) {
            const rsi = indicators.rsi;
            let divergenceStrength = 0;

            if (rsi > 80) {
                divergenceStrength = (rsi - 80) / 20;
            } else if (rsi < 20) {
                divergenceStrength = (20 - rsi) / 20;
            }

            if (divergenceStrength > 0.3) {
                return {
                    type: 'MOMENTUM_DIVERGENCE',
                    strength: divergenceStrength,
                    indicator: 'RSI',
                    value: rsi,
                    expected_reversion: rsi > 50 ? 'BEARISH' : 'BULLISH',
                    confidence: divergenceStrength > 0.7 ? 'HIGH' : 'MEDIUM',
                    profit_potential: divergenceStrength * 0.03,
                    time_horizon: '1-3 days'
                };
            }
        }

        return null;
    }

    async detectLargeOrders(symbol, realPrices, historicalData) {
        const avgVolume = historicalData ? historicalData.reduce((sum, d) => sum + d.volume, 0) / historicalData.length : 1000000;

        let currentVolume = 0;
        const exchanges = Object.keys(realPrices);
        for (const exchange of exchanges) {
            const priceData = realPrices[exchange][symbol];
            if (priceData && !priceData.error) {
                currentVolume += priceData.volume24h || 0;
            }
        }

        const volumeRatio = currentVolume / avgVolume;
        const netFlow = currentVolume > avgVolume ? currentVolume - avgVolume : avgVolume - currentVolume;

        return {
            detected: volumeRatio > 1.5,
            average_size: avgVolume,
            current_volume: currentVolume,
            volume_ratio: volumeRatio,
            net_flow: volumeRatio > 1.5 ? netFlow : -netFlow
        };
    }

