    async analyzeFuturesBasis(symbol) {
        try {
            const [spotPrice, futuresPrice] = await Promise.all([
                fetchSpotPrice(symbol),
                fetchFuturesPrice(symbol)
            ]);

            if (!spotPrice || !futuresPrice) {
                throw new Error('Failed to fetch prices');
            }

            const basis = futuresPrice - spotPrice;
            const basisPoints = (basis / spotPrice) * 10000;

            return {
                basis_points: basisPoints,
                trend: basisPoints > 0 ? 'CONTANGO' : 'BACKWARDATION',
                term_structure: basisPoints > 0 ? 'CONTANGO' : 'BACKWARDATION',
                carry_trade_opportunity: Math.abs(basisPoints) > 50,
                spot_price: spotPrice,
                futures_price: futuresPrice,
                data_source: 'REAL_FUTURES_DATA'
            };
        } catch (error) {
            console.error('Futures basis analysis failed:', error.message);
            return {
                basis_points: 0,
                trend: 'UNKNOWN',
                term_structure: 'UNKNOWN',
                carry_trade_opportunity: false,
                error: error.message
            };
