    async analyzeExchangeFlows(symbol, realPrices) {
        const flows = {};
        const exchanges = Object.keys(realPrices);

        for (const exchange of exchanges) {
            const priceData = realPrices[exchange][symbol];
            if (priceData && !priceData.error) {
                const volume = priceData.volume24h || 0;
                const priceChange = priceData.change24h || 0;

                flows[exchange] = {
                    inflow: priceChange > 0 ? volume * 0.6 : volume * 0.4,
                    outflow: priceChange < 0 ? volume * 0.6 : volume * 0.4,
                    net_flow: priceChange > 0 ? volume * 0.2 : -volume * 0.2,
                    flow_direction: priceChange > 0 ? 'INFLOW' : 'OUTFLOW'
                };
            }
        }

        return flows;
    }

