// ===== PART 5: SCORING METHODS & ARBITRAGE ENGINE =====

    // ===== SCORING AND CALCULATION METHODS =====

    calculateArbitrageRisk(buyExchange, sellExchange) {
        const exchangeRisk = {
            'binance': 5,
            'kraken': 10,
            'kucoin': 15,
            'coinbase': 8
        };

        const buyRisk = exchangeRisk[buyExchange.exchange] || 10;
        const sellRisk = exchangeRisk[sellExchange.exchange] || 10;

        return (buyRisk + sellRisk) / 2;
    }

    calculateRealDataConfidence(exchangeCount, buyVolume, sellVolume) {
        let baseConfidence = Math.min(exchangeCount * 20, 80);

        const avgVolume = (buyVolume + sellVolume) / 2;
        if (avgVolume > 1000000) baseConfidence += 15;
        else if (avgVolume > 100000) baseConfidence += 10;
        else if (avgVolume > 10000) baseConfidence += 5;

        return Math.min(baseConfidence, 95);
    }

    assessMarketDataQuality(buyData, sellData) {
        let quality = 'GOOD';

        if (buyData.timestamp && sellData.timestamp) {
            const timeDiff = Math.abs(buyData.timestamp - sellData.timestamp);
            if (timeDiff > 60000) quality = 'STALE';
        }

        if (buyData.volume24h < 10000 || sellData.volume24h < 10000) {
            quality = 'LOW_LIQUIDITY';
        }

        return quality;
    }

    estimateExecutionWindow(profitBps) {
        if (profitBps > 100) return 30;
        if (profitBps > 50) return 60;
        return 120;
    }

    calculateOverallDataQuality(opportunities) {
        if (opportunities.length === 0) return 0;

        const qualityScores = opportunities.map(opp => {
            if (opp.market_data_quality === 'GOOD') return 100;
            if (opp.market_data_quality === 'STALE') return 70;
            if (opp.market_data_quality === 'LOW_LIQUIDITY') return 50;
            return 80;
        });

        return qualityScores.reduce((sum, score) => sum + score, 0) / qualityScores.length;
    }

    async assessMarketConditions(symbols, exchanges) {
        const conditions = {
            volatility: 'NORMAL',
            liquidity: 'ADEQUATE',
            correlation: 'MEDIUM',
            regime: 'NORMAL_TRADING'
        };

        try {
            const marketData = await this.exchangeManager.getRealTimePrices(symbols.slice(0, 3), exchanges.slice(0, 2));

            let totalVolatility = 0;
            let validPairs = 0;

            for (const exchange of Object.keys(marketData)) {
                for (const symbol of Object.keys(marketData[exchange])) {
                    const data = marketData[exchange][symbol];
                    if (data && !data.error && data.change24h !== undefined) {
                        totalVolatility += Math.abs(data.change24h);
                        validPairs++;
                    }
                }
            }

            if (validPairs > 0) {
                const avgVolatility = totalVolatility / validPairs;
                if (avgVolatility > 10) conditions.volatility = 'HIGH';
                else if (avgVolatility > 5) conditions.volatility = 'ELEVATED';
                else if (avgVolatility < 1) conditions.volatility = 'LOW';
            }
        } catch (error) {
            console.warn('Failed to assess market conditions:', error.message);
        }

        return conditions;
    }

    getConfidenceMultiplier(confidence) {
        const multipliers = {
            'HIGH': 1.0,
            'MEDIUM': 0.7,
            'LOW': 0.4
        };
        return multipliers[confidence] || 0.5;
    }

    calculateMarketEfficiencyScore(inefficiencies) {
        if (inefficiencies.length === 0) return 95;

        const avgSignificance = inefficiencies.reduce((sum, ineff) => 
            sum + (ineff.strength || ineff.profit_potential || 0.5), 0) / inefficiencies.length;

        return Math.max(30, 100 - (inefficiencies.length * 10 + avgSignificance * 50));
    }

    calculateInstitutionalScore(analysis) {
        let score = 50;
        let primarySignal = 'MIXED';
        let direction = 'NEUTRAL';
        let flowStrength = 'MEDIUM';
        let marketImpact = 'LOW';
        let urgency = 'NORMAL';

        if (analysis.large_orders.detected) {
            score += 15;
            primarySignal = 'LARGE_ORDER_FLOW';
            direction = analysis.large_orders.net_flow > 0 ? 'BULLISH' : 'BEARISH';
            flowStrength = analysis.large_orders.volume_ratio > 2 ? 'STRONG' : 'MEDIUM';
        }

        if (analysis.whale_movements.accumulation_score > 70) {
            score += 20;
            if (primarySignal === 'MIXED') {
                primarySignal = 'WHALE_ACCUMULATION';
                direction = 'BULLISH';
            }
            marketImpact = 'HIGH';
        }

        if (Math.abs(analysis.futures_basis.basis_points) > 50) {
            score += 10;
            if (analysis.futures_basis.carry_trade_opportunity) {
                urgency = 'HIGH';
            }
        }

        if (analysis.options_flow.unusual_activity) {
            score += 15;
            urgency = 'HIGH';
        }

        const exchangeFlows = Object.values(analysis.exchange_flows || {});
        if (exchangeFlows.length > 0) {
            const netFlow = exchangeFlows.reduce((sum, flow) => sum + (flow.net_flow || 0), 0);
            if (Math.abs(netFlow) > 1000000) {
                score += 10;
                marketImpact = 'MEDIUM';
            }
        }

        return {
            score: Math.min(100, score),
            primary_signal: primarySignal,
            direction: direction,
            confidence: score > 80 ? 'HIGH' : score > 65 ? 'MEDIUM' : 'LOW',
            time_horizon: score > 80 ? '1-3 days' : '3-7 days',
            flow_strength: flowStrength,
            market_impact: marketImpact,
            urgency: urgency,
            evidence: [
                analysis.large_orders.detected ? 'Large order activity' : null,
                analysis.whale_movements.accumulation_score > 70 ? 'Whale accumulation' : null,
                analysis.options_flow.unusual_activity ? 'Unusual options activity' : null,
                analysis.futures_basis.carry_trade_opportunity ? 'Carry trade opportunity' : null
            ].filter(Boolean)
        };
    }

    assessMarketStructureHealth(signals) {
        if (signals.length === 0) return 'NEUTRAL';

        const bullishSignals = signals.filter(s => s.direction === 'BULLISH').length;
        const bearishSignals = signals.filter(s => s.direction === 'BEARISH').length;
        const totalFlow = signals.reduce((sum, s) => sum + s.institutional_score, 0);

        let health = 'NEUTRAL';

        if (bullishSignals > bearishSignals * 2) health = 'STRONG_BULLISH';
        else if (bearishSignals > bullishSignals * 2) health = 'STRONG_BEARISH';
        else if (bullishSignals > bearishSignals) health = 'MODERATELY_BULLISH';
        else if (bearishSignals > bullishSignals) health = 'MODERATELY_BEARISH';

        return {
            overall_health: health,
            flow_intensity: totalFlow / signals.length,
            signal_consistency: Math.abs(bullishSignals - bearishSignals) / signals.length,
            market_participation: signals.length > 5 ? 'HIGH' : signals.length > 2 ? 'MEDIUM' : 'LOW'
        };
    }

    calculateFlowIntensity(signals) {
        if (signals.length === 0) return 0;

        const totalScore = signals.reduce((sum, signal) => sum + signal.institutional_score, 0);
        const avgScore = totalScore / signals.length;
        const highConfidenceSignals = signals.filter(s => s.confidence === 'HIGH').length;

        return {
            average_score: avgScore,
            intensity_level: avgScore > 80 ? 'EXTREME' : avgScore > 70 ? 'HIGH' : avgScore > 60 ? 'MEDIUM' : 'LOW',
            high_confidence_ratio: highConfidenceSignals / signals.length,
            flow_direction: this.calculateNetFlowDirection(signals)
        };
    }

    calculateNetFlowDirection(signals) {
        const bullishWeight = signals
            .filter(s => s.direction === 'BULLISH')
            .reduce((sum, s) => sum + s.institutional_score, 0);

        const bearishWeight = signals
            .filter(s => s.direction === 'BEARISH')
            .reduce((sum, s) => sum + s.institutional_score, 0);

        const netWeight = bullishWeight - bearishWeight;

        if (Math.abs(netWeight) < 50) return 'NEUTRAL';
        return netWeight > 0 ? 'BULLISH' : 'BEARISH';
    }

    detectSectorRotation(signals) {
        const sectorFlows = {};

        signals.forEach(signal => {
            // Dynamic sector detection based on symbol
            const sector = this.determineSector(signal.symbol);
            if (!sectorFlows[sector]) {
                sectorFlows[sector] = { score: 0, count: 0 };
            }
            sectorFlows[sector].score += signal.institutional_score;
            sectorFlows[sector].count += 1;
        });

        const sectorAverages = {};
        Object.keys(sectorFlows).forEach(sector => {
            sectorAverages[sector] = sectorFlows[sector].score / sectorFlows[sector].count;
        });

        return {
            sector_flows: sectorAverages,
            rotation_detected: Object.keys(sectorAverages).length > 2,
            leading_sector: Object.keys(sectorAverages).length > 0 ? 
                Object.keys(sectorAverages).reduce((a, b) => 
                    sectorAverages[a] > sectorAverages[b] ? a : b
                ) : null,
            lagging_sector: Object.keys(sectorAverages).length > 0 ?
                Object.keys(sectorAverages).reduce((a, b) => 
                    sectorAverages[a] < sectorAverages[b] ? a : b
                ) : null
        };
    }

    determineSector(symbol) {
        // This would be expanded with a comprehensive mapping
        // For now, using basic categorization
        const symbolUpper = symbol.toUpperCase();

        if (['BTC', 'LTC', 'BCH'].includes(symbolUpper)) return 'STORE_OF_VALUE';
        if (['ETH', 'ETC'].includes(symbolUpper)) return 'SMART_CONTRACTS';
        if (['SOL', 'ADA', 'AVAX', 'NEAR', 'ALGO'].includes(symbolUpper)) return 'LAYER1';
        if (['DOT', 'ATOM', 'COSMOS'].includes(symbolUpper)) return 'INTEROPERABILITY';
        if (['LINK', 'BAND', 'API3'].includes(symbolUpper)) return 'ORACLES';
        if (['MATIC', 'ARB', 'OP'].includes(symbolUpper)) return 'SCALING';
        if (['UNI', 'SUSHI', 'CAKE'].includes(symbolUpper)) return 'DEFI';

        return 'OTHER';
    }

    recommendPositionSize(signal) {
        const baseSize = 1000;
        const confidenceMultiplier = signal.confidence === 'HIGH' ? 2 : signal.confidence === 'MEDIUM' ? 1.5 : 1;
        const scoreMultiplier = signal.institutional_score / 100;

        return Math.round(baseSize * confidenceMultiplier * scoreMultiplier);
    }

    assessSignalRisk(signal) {
        let risk = 'MEDIUM';

        if (signal.confidence === 'HIGH' && signal.institutional_score > 80) {
            risk = 'LOW';
        } else if (signal.confidence === 'LOW' || signal.institutional_score < 60) {
            risk = 'HIGH';
        }

        if (signal.execution_urgency === 'HIGH') {
            risk = risk === 'LOW' ? 'MEDIUM' : 'HIGH';
        }

        return risk;
    }

    calculateOpportunityPriority(opportunity, type) {
        let priority = 50;

        if (type === 'arbitrage') {
            priority += opportunity.profit_bps || 0;
            priority += opportunity.estimated_profit_usd / 100;
            if (opportunity.risk_score < 20) priority += 10;
            if (opportunity.confidence > 80) priority += 15;
            if (opportunity.market_data_quality === 'GOOD') priority += 10;
        } else if (type === 'inefficiency') {
            priority += (opportunity.strength || opportunity.profit_potential || 0) * 100;
            if (opportunity.confidence === 'HIGH') priority += 20;
            priority += opportunity.profit_potential * 1000;
        } else if (type === 'institutional') {
            priority += opportunity.institutional_score || 0;
            if (opportunity.confidence === 'HIGH') priority += 15;
            if (opportunity.execution_urgency === 'HIGH') priority += 10;
            if (opportunity.market_impact === 'HIGH') priority += 5;
        }

        return Math.min(100, priority);
    }

    assessRiskLevel(opportunity) {
        let risk = 'MEDIUM';

        if (opportunity.confidence > 80 && opportunity.profit_bps > 50) {
            risk = 'LOW';
        } else if (opportunity.confidence < 60 || (opportunity.profit_bps && opportunity.profit_bps < 25)) {
            risk = 'HIGH';
        }

        return risk;
    }

    assessExecutionComplexity(opportunity) {
        if (opportunity.type === 'ARBITRAGE') {
            return opportunity.buy_exchange === opportunity.sell_exchange ? 'LOW' : 'MEDIUM';
        } else if (opportunity.type === 'INEFFICIENCY') {
            return 'HIGH';
        } else if (opportunity.type === 'INSTITUTIONAL_FLOW') {
            return opportunity.execution_urgency === 'HIGH' ? 'HIGH' : 'MEDIUM';
        }

        return 'MEDIUM';
    }

    estimateMarketImpact(opportunity) {
        if (opportunity.max_volume && opportunity.max_volume > 1000000) return 'HIGH';
        if (opportunity.capital_requirement && opportunity.capital_requirement > 100000) return 'MEDIUM';
        return 'LOW';
    }

    calculateTimeDecay(opportunity) {
        if (opportunity.type === 'ARBITRAGE') {
            return opportunity.profit_bps > 100 ? 'FAST' : 'MEDIUM';
        }
        return 'SLOW';
    }

    estimateCapitalRequirement(opportunity) {
        if (opportunity.type === 'INEFFICIENCY') {
            const baseCapital = 10000;
            const multiplier = (opportunity.profit_potential || 0.01) * 100;
            return baseCapital * Math.max(multiplier, 0.5);
        } else if (opportunity.type === 'INSTITUTIONAL_FLOW') {
            return opportunity.institutional_score * 500;
        }

        return 10000;
    }

    async generateExecutionPlan(opportunities) {
        const plan = opportunities.map((opportunity, index) => ({
            opportunity,
            execution_order: index + 1,
            expected_return: opportunity.expected_return || 0,
            execution_priority: opportunity.priority,
            estimated_execution_time: this.estimateExecutionTime(opportunity),
            success_probability: this.calculateSuccessProbability(opportunity),
            capital_requirement: opportunity.capital_requirement || 0,
            risk_level: opportunity.risk_level || 'MEDIUM'
        }));

        return plan;
    }

    estimateExecutionTime(opportunity) {
        const baseTime = {
            'ARBITRAGE': 60,
            'INEFFICIENCY': 300,
            'INSTITUTIONAL_FLOW': 900
        };

        return baseTime[opportunity.type] || 300;
    }

    calculateSuccessProbability(opportunity) {
        let probability = 0.7;

        if (opportunity.confidence === 'HIGH') probability += 0.2;
        else if (opportunity.confidence === 'LOW') probability -= 0.2;

        if (opportunity.risk_level === 'LOW') probability += 0.1;
        else if (opportunity.risk_level === 'HIGH') probability -= 0.1;

        if (opportunity.market_data_quality === 'GOOD') probability += 0.1;

        return Math.max(0.1, Math.min(0.95, probability));
    }

    async detectMarketRegime(symbols, exchanges) {
        try {
            const marketData = await this.exchangeManager.getRealTimePrices(symbols.slice(0, 3), exchanges.slice(0, 2));

            let totalVolatility = 0;
            let totalVolume = 0;
            let validPairs = 0;

            for (const exchange of Object.keys(marketData)) {
                for (const symbol of Object.keys(marketData[exchange])) {
                    const data = marketData[exchange][symbol];
                    if (data && !data.error) {
                        if (data.change24h !== undefined) {
                            totalVolatility += Math.abs(data.change24h);
                        }
                        if (data.volume24h !== undefined) {
                            totalVolume += data.volume24h;
                        }
                        validPairs++;
                    }
                }
            }

            if (validPairs > 0) {
                const avgVolatility = totalVolatility / validPairs;
                const avgVolume = totalVolume / validPairs;

                let regime = 'NORMAL';
                if (avgVolatility > 10 && avgVolume > 1000000) regime = 'HIGH_VOLATILITY_HIGH_VOLUME';
                else if (avgVolatility > 10) regime = 'HIGH_VOLATILITY';
                else if (avgVolume > 5000000) regime = 'HIGH_VOLUME';
                else if (avgVolatility < 1) regime = 'LOW_VOLATILITY';

                return {
                    regime: regime,
                    volatility: avgVolatility,
                    volume: avgVolume,
                    confidence: validPairs > 4 ? 'HIGH' : 'MEDIUM'
                };
            }
        } catch (error) {
            console.warn('Failed to detect market regime:', error.message);
        }

        return {
            regime: 'UNKNOWN',
            volatility: 0,
            volume: 0,
            confidence: 'LOW'
        };
    }
}
