// ===== ENTERPRISE SYMBOL MAPPER =====
class EnterpriseSymbolMapper {
    constructor() {
        this.symbolMappings = new Map();
        this.reverseMapping = new Map();
        this.alternativeSymbols = new Map();

        this.initializeSymbolMappings();
    }

    async initializeSymbolMappings() {
        // Fetch real symbol mappings from exchanges
        try {
            const [binanceSymbols, krakenAssets] = await Promise.all([
                this.fetchBinanceSymbols(),
                this.fetchKrakenAssets()
            ]);

            // Build dynamic mappings from real data
            this.buildDynamicMappings(binanceSymbols, krakenAssets);
        } catch (error) {
            console.error('Failed to initialize symbol mappings:', error.message);
            // Use basic fallback structure
            this.initializeFallbackMappings();
        }
    }

    async fetchBinanceSymbols() {
        try {
            const response = await fetch('https://api.binance.com/api/v3/exchangeInfo');
            if (!response.ok) throw new Error('Failed to fetch Binance symbols');
            const data = await response.json();
            return data.symbols;
        } catch {
            return [];
        }
    }

    async fetchKrakenAssets() {
        try {
            const response = await fetch('https://api.kraken.com/0/public/Assets');
            if (!response.ok) throw new Error('Failed to fetch Kraken assets');
            const data = await response.json();
            return data.result;
        } catch {
            return {};
        }
    }

    buildDynamicMappings(binanceSymbols, krakenAssets) {
        // Build mappings from real exchange data
        const baseAssets = new Set();

        // Extract base assets from Binance
        binanceSymbols.forEach(symbol => {
            if (symbol.status === 'TRADING') {
                baseAssets.add(symbol.baseAsset);
            }
        });

        // Build mappings for each asset
        baseAssets.forEach(asset => {
            const mapping = {
                binance: `${asset}USDT`,
                kraken: this.findKrakenPair(asset, krakenAssets),
                kucoin: `${asset}-USDT`,
                coinbase: `${asset}-USD`,
                alternatives: []
            };

            this.symbolMappings.set(asset, mapping);
        });
    }

    findKrakenPair(asset, krakenAssets) {
        // Map common assets to Kraken format
        const krakenMap = {
            'BTC': 'XXBTZUSD',
            'ETH': 'XETHZUSD',
            'LTC': 'XLTCZUSD'
        };

        return krakenMap[asset] || `${asset}USD`;
    }

    initializeFallbackMappings() {
        // Basic fallback mappings if API calls fail
        const basicMappings = {
            'BTC': {
                binance: 'BTCUSDT',
                kraken: 'XXBTZUSD',
                kucoin: 'BTC-USDT',
                coinbase: 'BTC-USD'
            },
            'ETH': {
                binance: 'ETHUSDT',
                kraken: 'XETHZUSD',
                kucoin: 'ETH-USDT',
                coinbase: 'ETH-USD'
            }
        };

        Object.entries(basicMappings).forEach(([symbol, config]) => {
            this.symbolMappings.set(symbol, config);
        });
    }

    resolveSymbol(symbol, exchange) {
        const mapping = this.symbolMappings.get(symbol.toUpperCase());
        if (mapping && mapping[exchange]) {
            return mapping[exchange];
        }

        const primarySymbol = this.alternativeSymbols.get(symbol.toUpperCase());
        if (primarySymbol) {
            const primaryMapping = this.symbolMappings.get(primarySymbol);
            if (primaryMapping && primaryMapping[exchange]) {
                return primaryMapping[exchange];
            }
        }

        return this.generateFallbackFormat(symbol, exchange);
    }

    generateFallbackFormat(symbol, exchange) {
        switch (exchange) {
            case 'binance':
                return `${symbol.toUpperCase()}USDT`;
            case 'kraken':
                return `${symbol.toUpperCase()}USD`;
            case 'kucoin':
                return `${symbol.toUpperCase()}-USDT`;
            case 'coinbase':
                return `${symbol.toUpperCase()}-USD`;
            default:
                return symbol.toUpperCase();
        }
    }

    getAllAlternatives(symbol) {
        const mapping = this.symbolMappings.get(symbol.toUpperCase());
        return mapping?.alternatives || [];
    }
}

// ===== ENTERPRISE CIRCUIT BREAKER SYSTEM =====
class ExchangeCircuitBreaker {
    constructor() {
        this.breakers = new Map();
        this.healthMetrics = new Map();
        this.config = {
            failureThreshold: 5,
            recoveryTimeout: 30000,
            halfOpenMaxAttempts: 3,
            successThreshold: 2
        };
    }

    async executeWithBreaker(exchangeKey, operation, fallback = null) {
    const breaker = this.getOrCreateBreaker(exchangeKey);

    if (breaker.state === 'OPEN') {
        if (Date.now() - breaker.lastFailure > this.config.recoveryTimeout) {
            breaker.state = 'HALF_OPEN';
            breaker.halfOpenAttempts = 0;
            console.log(`âš¡ Circuit breaker HALF_OPEN for ${exchangeKey}`);
        } else {
            if (fallback) return await fallback();
            return null; // Return null instead of throwing
        }
    }

    try {
        const result = await Promise.race([
            operation(),
            new Promise((_, reject) => 
                setTimeout(() => reject(new Error('Operation timeout')), 15000)
            )
        ]);

        this.recordSuccess(exchangeKey);
        return result;

    } catch (error) {
        // Don't record failure for expected errors
        if (error.message.includes('Unknown asset') || 
            error.message.includes('Invalid symbol') ||
            error.message.includes('No data received') ||
            error.message.includes('EQuery:Unknown asset pair')) {
            return fallback ? await fallback() : null;
        }

        this.recordFailure(exchangeKey, error);

        if (fallback) {
            console.warn(`âš ï¸ Using fallback for ${exchangeKey}: ${error.message}`);
            return await fallback();
        }

        throw error;
    }
    }

    getOrCreateBreaker(exchangeKey) {
        if (!this.breakers.has(exchangeKey)) {
            this.breakers.set(exchangeKey, {
                state: 'CLOSED',
                failures: 0,
                successes: 0,
                lastFailure: 0,
                halfOpenAttempts: 0
            });
        }
        return this.breakers.get(exchangeKey);
    }

    recordSuccess(exchangeKey) {
        const breaker = this.getOrCreateBreaker(exchangeKey);
        breaker.failures = 0;
        breaker.successes++;

        if (breaker.state === 'HALF_OPEN' && breaker.successes >= this.config.successThreshold) {
            breaker.state = 'CLOSED';
            console.log(`âœ… Circuit breaker CLOSED for ${exchangeKey}`);
        }
    }

    recordFailure(exchangeKey, error) {
        const breaker = this.getOrCreateBreaker(exchangeKey);
        breaker.failures++;
        breaker.lastFailure = Date.now();
        breaker.successes = 0;

        if (breaker.state === 'HALF_OPEN') {
            breaker.halfOpenAttempts++;
            if (breaker.halfOpenAttempts >= this.config.halfOpenMaxAttempts) {
                breaker.state = 'OPEN';
                console.error(`ðŸ”´ Circuit breaker OPEN for ${exchangeKey}`);
            }
        } else if (breaker.failures >= this.config.failureThreshold) {
            breaker.state = 'OPEN';
            console.error(`ðŸ”´ Circuit breaker OPEN for ${exchangeKey} after ${breaker.failures} failures`);
        }
    }

    getHealthReport() {
        const report = {};
        this.breakers.forEach((breaker, exchangeKey) => {
            report[exchangeKey] = {
                state: breaker.state,
                failures: breaker.failures,
                successes: breaker.successes,
                healthy: breaker.state === 'CLOSED'
            };
        });
        return report;
    }
}

// ===== INTELLIGENT RATE LIMITING SYSTEM =====
class ExchangeRateLimiter {
    constructor() {
        this.limits = new Map();
        this.queues = new Map();

        this.rateLimits = {
            binance: { requests: 1200, weight: 1200 },
            kraken: { requests: 1800, weight: 1800 },
            kucoin: { requests: 1500, weight: 1500 },
            coinbase: { requests: 600, weight: 600 }
        };
    }

    async executeWithRateLimit(exchange, operation, weight = 1) {
        const limiter = this.getOrCreateLimiter(exchange);

        await this.waitForRateLimit(exchange, weight);

        limiter.requests.push({
            timestamp: Date.now(),
            weight: weight
        });

        this.cleanOldRequests(exchange);

        return await operation();
    }

    getOrCreateLimiter(exchange) {
        if (!this.limits.has(exchange)) {
            this.limits.set(exchange, {
                requests: [],
                lastRequest: 0
            });
        }
        return this.limits.get(exchange);
    }

    async waitForRateLimit(exchange, weight) {
        const limiter = this.getOrCreateLimiter(exchange);
        const limit = this.rateLimits[exchange];

        if (!limit) return;

        const now = Date.now();
        const oneMinuteAgo = now - 60000;

        const recentRequests = limiter.requests.filter(req => req.timestamp > oneMinuteAgo);
        const currentWeight = recentRequests.reduce((sum, req) => sum + req.weight, 0);

        if (currentWeight + weight > limit.weight) {
            const oldestRequest = recentRequests[0];
            const waitTime = oldestRequest ? (oldestRequest.timestamp + 60000) - now : 0;

            if (waitTime > 0) {
                console.log(`â° Rate limiting ${exchange}: waiting ${waitTime}ms`);
                await new Promise(resolve => setTimeout(resolve, waitTime));
            }
        }
    }

    cleanOldRequests(exchange) {
        const limiter = this.getOrCreateLimiter(exchange);
        const oneMinuteAgo = Date.now() - 60000;
        limiter.requests = limiter.requests.filter(req => req.timestamp > oneMinuteAgo);
    }
}

// ===== ENHANCED PRICE FETCHER WITH MULTIPLE FALLBACKS =====
class EnhancedPriceFetcher {
    constructor(symbolMapper, circuitBreaker, rateLimiter) {
        this.symbolMapper = symbolMapper;
        this.circuitBreaker = circuitBreaker;
        this.rateLimiter = rateLimiter;
        this.priceCache = new Map();
        this.cacheTimeout = 5000;
    }

    async fetchExchangePrices(exchange, symbols) {
        const prices = {};
        const errors = [];

        for (const symbol of symbols) {
            try {
                const price = await this.fetchPriceWithFallback(symbol, exchange);
                if (price !== null) {
                    prices[symbol] = price;
                } else {
                    errors.push(`No price data for ${symbol}`);
                }
            } catch (error) {
                console.error(`Price fetch failed for ${symbol} on ${exchange}:`, error.message);
                errors.push(`${symbol}: ${error.message}`);
            }
        }

        return { prices, errors };
    }

    async fetchPriceWithFallback(symbol, exchange) {
        const cacheKey = `${exchange}_${symbol}`;
        const cached = this.priceCache.get(cacheKey);
        if (cached && Date.now() - cached.timestamp < this.cacheTimeout) {
            return cached.price;
        }

        let price = await this.attemptPriceFetch(symbol, exchange);

        if (price === null) {
            const alternatives = this.symbolMapper.getAllAlternatives(symbol);
            for (const altSymbol of alternatives) {
                price = await this.attemptPriceFetch(altSymbol, exchange);
                if (price !== null) {
                    console.log(`âœ… Found price for ${symbol} using alternative ${altSymbol}`);
                    break;
                }
            }
        }

        if (price !== null) {
            this.priceCache.set(cacheKey, {
                price: price,
                timestamp: Date.now()
            });
        }

        return price;
    }

    async attemptPriceFetch(symbol, exchange) {
        const exchangeSymbol = this.symbolMapper.resolveSymbol(symbol, exchange);

        return await this.circuitBreaker.executeWithBreaker(
            `${exchange}_price`,
            async () => await this.rateLimiter.executeWithRateLimit(
                exchange,
                async () => await this.fetchFromExchange(exchangeSymbol, exchange)
            ),
            async () => null
        );
    }

    async fetchFromExchange(symbol, exchange) {
        switch (exchange) {
            case 'binance':
                return await this.fetchBinancePrice(symbol);
            case 'kraken':
                return await this.fetchKrakenPrice(symbol);
            case 'kucoin':
                return await this.fetchKuCoinPrice(symbol);
            case 'coinbase':
                return await this.fetchCoinbasePrice(symbol);
            default:
                throw new Error(`Unsupported exchange: ${exchange}`);
        }
    }

    async fetchBinancePrice(symbol) {
        const response = await fetch(`https://api.binance.com/api/v3/ticker/price?symbol=${symbol}`, {
            timeout: 10000,
            headers: { 'User-Agent': 'Crypto-Universe-Enterprise/2.0' }
        });

        if (!response.ok) {
            throw new Error(`Binance API error: ${response.status}`);
        }

        const data = await response.json();
        const price = parseFloat(data.price);

        // Fetch order book for real bid/ask
        try {
            const obResponse = await fetch(`https://api.binance.com/api/v3/depth?symbol=${symbol}&limit=5`);
            if (obResponse.ok) {
                const obData = await obResponse.json();
                return {
                    bid: parseFloat(obData.bids[0][0]),
                    ask: parseFloat(obData.asks[0][0]),
                    last: price,
                    timestamp: Date.now()
                };
            }
        } catch {
            // Fallback to estimated bid/ask
        }

        return {
            bid: price * 0.9995,
            ask: price * 1.0005,
            last: price,
            timestamp: Date.now()
        };
    }

    async fetchKrakenPrice(symbol) {
        const response = await fetch(`https://api.kraken.com/0/public/Ticker?pair=${symbol}`, {
            timeout: 10000,
            headers: { 'User-Agent': 'Crypto-Universe-Enterprise/2.0' }
        });

        if (!response.ok) {
            throw new Error(`Kraken API error: ${response.status}`);
        }

        const data = await response.json();

        if (data.error && data.error.length > 0) {
            throw new Error(`Kraken API errors: ${data.error.join(', ')}`);
        }

        const ticker = Object.values(data.result)[0];
        if (!ticker) {
            throw new Error('Invalid ticker data from Kraken');
        }

        return {
            bid: parseFloat(ticker.b[0]),
            ask: parseFloat(ticker.a[0]),
            last: parseFloat(ticker.c[0]),
            timestamp: Date.now()
        };
    }

    async fetchKuCoinPrice(symbol) {
        const response = await fetch(`https://api.kucoin.com/api/v1/market/orderbook/level1?symbol=${symbol}`, {
            timeout: 10000,
            headers: { 'User-Agent': 'Crypto-Universe-Enterprise/2.0' }
        });

        if (!response.ok) {
            throw new Error(`KuCoin API error: ${response.status}`);
        }

        const data = await response.json();

        if (!data.data) {
            throw new Error('No data received from KuCoin');
        }

        return {
            bid: parseFloat(data.data.bestBid),
            ask: parseFloat(data.data.bestAsk),
            last: parseFloat(data.data.price),
            timestamp: Date.now()
        };
    }

    async fetchCoinbasePrice(symbol) {
        const response = await fetch(`https://api.exchange.coinbase.com/products/${symbol}/ticker`, {
            timeout: 10000,
            headers: { 'User-Agent': 'Crypto-Universe-Enterprise/2.0' }
        });

        if (!response.ok) {
            throw new Error(`Coinbase API error: ${response.status}`);
        }

        const data = await response.json();

        return {
            bid: parseFloat(data.bid),
            ask: parseFloat(data.ask),
            last: parseFloat(data.price),
            timestamp: Date.now()
        };
    }
}
// ===== PART 7: ENTERPRISE ARBITRAGE ENGINE =====

class EnterpriseArbitrageEngine {
    constructor(exchangeManager) {
        this.exchangeManager = exchangeManager;
        this.symbolMapper = new EnterpriseSymbolMapper();
        this.circuitBreaker = new ExchangeCircuitBreaker();
        this.rateLimiter = new ExchangeRateLimiter();
        this.priceFetcher = new EnhancedPriceFetcher(this.symbolMapper, this.circuitBreaker, this.rateLimiter);
    }

    async scanArbitrageOpportunities(exchangeList, minProfit, maxCapital, filter) {
        console.log('ðŸ” Scanning enhanced arbitrage opportunities...');

        const exchangesToScan = this.parseExchangeList(exchangeList);
        const assetsToScan = await this.getAssetsToScan(filter);
        const opportunities = [];
        const exchangeHealthReport = this.circuitBreaker.getHealthReport();

        console.log(`Scanning ${assetsToScan.length} assets across ${exchangesToScan.length} exchanges`);

        for (const asset of assetsToScan) {
            try {
                const exchangePrices = {};
                const priceErrors = [];

                // Fetch prices from all exchanges using REAL data
                for (const exchange of exchangesToScan) {
                    try {
                        const priceData = await this.priceFetcher.fetchPriceWithFallback(asset, exchange);
                        if (priceData) {
                            exchangePrices[exchange] = priceData;
                        } else {
                            priceErrors.push(`${exchange}: No price data`);
                        }
                    } catch (error) {
                        priceErrors.push(`${exchange}: ${error.message}`);
                    }
                }

                if (Object.keys(exchangePrices).length < 2) {
                    console.warn(`Skipping ${asset} - insufficient price data`);
                    continue;
                }

                // Find best buy and sell opportunities
                const exchanges = Object.keys(exchangePrices);
                let bestBuyExchange = null;
                let bestSellExchange = null;
                let lowestAsk = Infinity;
                let highestBid = 0;

                exchanges.forEach(exchange => {
                    const price = exchangePrices[exchange];
                    if (price.ask < lowestAsk) {
                        lowestAsk = price.ask;
                        bestBuyExchange = exchange;
                    }
                    if (price.bid > highestBid) {
                        highestBid = price.bid;
                        bestSellExchange = exchange;
                    }
                });

                if (bestBuyExchange && bestSellExchange && bestBuyExchange !== bestSellExchange) {
                    const rawProfit = highestBid - lowestAsk;
                    const profitBps = (rawProfit / lowestAsk) * 10000;

                    if (profitBps >= minProfit) {
                        const buyFee = this.getExchangeFee(bestBuyExchange) * lowestAsk;
                        const sellFee = this.getExchangeFee(bestSellExchange) * highestBid;
                        const maxQuantity = Math.min(maxCapital / lowestAsk, 100);

                        const netProfitUsd = (rawProfit * maxQuantity) - (buyFee * maxQuantity) - (sellFee * maxQuantity);

                        opportunities.push({
                            asset: asset,
                            buy_exchange: bestBuyExchange,
                            sell_exchange: bestSellExchange,
                            buy_price: lowestAsk,
                            sell_price: highestBid,
                            profit_bps: Math.round(profitBps),
                            profit_usd: parseFloat(netProfitUsd.toFixed(2)),
                            max_quantity: maxQuantity,
                            confidence: this.calculateConfidence(exchangePrices, priceErrors.length),
                            execution_time_estimate: this.estimateExecutionTime(bestBuyExchange, bestSellExchange),
                            market_impact: this.estimateMarketImpact(maxQuantity, exchangePrices[bestBuyExchange], exchangePrices[bestSellExchange]),
                            timestamp: new Date().toISOString()
                        });
                    }
                }

            } catch (error) {
                console.error(`Error processing ${asset}:`, error.message);
            }
        }

        opportunities.sort((a, b) => b.profit_usd - a.profit_usd);

        return {
            success: true,
            timestamp: new Date().toISOString(),
            arbitrage_scan: {
                exchanges_scanned: exchangesToScan,
                assets_scanned: assetsToScan.length,
                opportunities_found: opportunities.length,
                top_opportunities: opportunities.slice(0, 10),
                total_potential_profit: opportunities.reduce((sum, opp) => sum + opp.profit_usd, 0),
                min_profit_threshold: minProfit,
                exchange_health: exchangeHealthReport,
                scanning_performance: {
                    total_assets_processed: assetsToScan.length,
                    successful_scans: opportunities.length,
                    failed_assets: assetsToScan.length - opportunities.length
                }
            }
        };
    }

    async scanTriangularArbitrage(exchangeList, minProfit, maxCapital) {
        console.log('ðŸ”º Scanning triangular arbitrage opportunities...');

        const exchangesToScan = this.parseExchangeList(exchangeList);
        const opportunities = [];

        for (const exchange of exchangesToScan) {
            try {
                const triangularPaths = await this.getTriangularPaths();

                for (const path of triangularPaths) {
                    const pathOpportunity = await this.calculateTriangularArbitrageProfit(exchange, path, minProfit, maxCapital);
                    if (pathOpportunity && pathOpportunity.profit_bps >= minProfit) {
                        opportunities.push(pathOpportunity);
                    }
                }
            } catch (error) {
                console.error(`Triangular arbitrage scan error on ${exchange}:`, error.message);
            }
        }

        opportunities.sort((a, b) => b.profit_usd - a.profit_usd);

        return {
            success: true,
            timestamp: new Date().toISOString(),
            triangular_arbitrage: {
                exchanges_scanned: exchangesToScan,
                opportunities_found: opportunities.length,
                top_opportunities: opportunities.slice(0, 5),
                total_potential_profit: opportunities.reduce((sum, opp) => sum + opp.profit_usd, 0),
                complexity: 'HIGH',
                min_profit_threshold: minProfit,
                exchange_health: this.circuitBreaker.getHealthReport()
            }
        };
    }

    async scanCrossAssetArbitrage(exchangeList, minProfit, maxCapital) {
        console.log('ðŸ”„ Scanning cross-asset arbitrage opportunities...');

        const exchangesToScan = this.parseExchangeList(exchangeList);
        const crossAssetOpportunities = [];

        // Get dynamically discovered asset pairs
        const assetPairs = await this.getDynamicAssetPairs();

        for (const exchange of exchangesToScan) {
            for (const [assetA, assetB] of assetPairs) {
                try {
                    const crossOpportunity = await this.calculateCrossAssetRatio(exchange, assetA, assetB, minProfit, maxCapital);
                    if (crossOpportunity && crossOpportunity.profit_bps >= minProfit) {
                        crossAssetOpportunities.push(crossOpportunity);
                    }
                } catch (error) {
                    console.error(`Cross-asset arbitrage scan error on ${exchange} for ${assetA}/${assetB}:`, error.message);
                }
            }
        }

        crossAssetOpportunities.sort((a, b) => b.profit_usd - a.profit_usd);

        return {
            success: true,
            timestamp: new Date().toISOString(),
            cross_asset_arbitrage: {
                exchanges_scanned: exchangesToScan,
                pairs_analyzed: assetPairs.length,
                opportunities_found: crossAssetOpportunities.length,
                top_opportunities: crossAssetOpportunities.slice(0, 5),
                total_potential_profit: crossAssetOpportunities.reduce((sum, opp) => sum + opp.profit_usd, 0),
                complexity: 'MEDIUM',
                min_profit_threshold: minProfit,
                exchange_health: this.circuitBreaker.getHealthReport()
            }
        };
    }

    async monitorArbitrageSpreads(exchangeList, minProfit) {
        console.log('ðŸ“Š Monitoring enhanced arbitrage spreads...');

        const exchangesToScan = this.parseExchangeList(exchangeList);
        const assets = await this.getDynamicTopAssets();
        const spreadData = {};

        for (const asset of assets) {
            try {
                const prices = [];

                for (const exchange of exchangesToScan) {
                    try {
                        const priceData = await this.priceFetcher.fetchPriceWithFallback(asset, exchange);
                        if (priceData) {
                            prices.push({
                                exchange: exchange,
                                bid: priceData.bid,
                                ask: priceData.ask,
                                spread: priceData.ask - priceData.bid,
                                health: this.circuitBreaker.breakers.get(`${exchange}_price`)?.state || 'CLOSED'
                            });
                        }
                    } catch (error) {
                        console.error(`Failed to fetch ${asset} price from ${exchange}: ${error.message}`);
                    }
                }

                if (prices.length >= 2) {
                    const minAsk = Math.min(...prices.map(p => p.ask));
                    const maxBid = Math.max(...prices.map(p => p.bid));
                    const potentialProfit = maxBid - minAsk;
                    const profitBps = (potentialProfit / minAsk) * 10000;

                    spreadData[asset] = {
                        prices: prices,
                        min_ask: minAsk,
                        max_bid: maxBid,
                        potential_profit: potentialProfit,
                        profit_bps: Math.round(profitBps),
                        profitable: profitBps >= minProfit,
                        best_buy_exchange: prices.find(p => p.ask === minAsk)?.exchange,
                        best_sell_exchange: prices.find(p => p.bid === maxBid)?.exchange,
                        liquidity_score: this.calculateLiquidityScore(prices),
                        risk_score: this.calculateRiskScore(prices),
                        execution_window: this.estimateExecutionWindow(profitBps)
                    };
                }
            } catch (error) {
                console.error(`Error monitoring spreads for ${asset}:`, error.message);
            }
        }

        return {
            success: true,
            timestamp: new Date().toISOString(),
            spread_monitoring: {
                assets_monitored: assets,
                exchanges_monitored: exchangesToScan,
                spread_data: spreadData,
                profitable_opportunities: Object.values(spreadData).filter(data => data.profitable).length,
                exchange_health: this.circuitBreaker.getHealthReport(),
                monitoring_quality: this.calculateMonitoringQuality(spreadData)
            }
        };
    }

    async calculateArbitrageProfit(exchangeList, maxCapital, filter) {
        console.log('ðŸ’° Calculating enhanced arbitrage profits...');

        const scanResult = await this.scanArbitrageOpportunities(exchangeList, 25, 100000, filter);

        if (!scanResult.success) {
            throw new Error('Failed to scan arbitrage opportunities for profit calculation');
        }

        const opportunities = scanResult.arbitrage_scan.top_opportunities;
        const totalPotentialProfit = opportunities.reduce((sum, opp) => sum + opp.profit_usd, 0);
        const averageProfit = opportunities.length > 0 ? totalPotentialProfit / opportunities.length : 0;

        const highConfidenceOpps = opportunities.filter(opp => opp.confidence >= 80);
        const riskAdjustedProfit = highConfidenceOpps.reduce((sum, opp) => sum + opp.profit_usd, 0);

        return {
            success: true,
            timestamp: new Date().toISOString(),
            profit_analysis: {
                total_opportunities: opportunities.length,
                high_confidence_opportunities: highConfidenceOpps.length,
                total_potential_profit: totalPotentialProfit.toFixed(2),
                risk_adjusted_profit: riskAdjustedProfit.toFixed(2),
                average_profit_per_trade: averageProfit.toFixed(2),
                max_single_profit: opportunities.length > 0 ? Math.max(...opportunities.map(opp => opp.profit_usd)).toFixed(2) : '0.00',
                profit_distribution: this.calculateProfitDistribution(opportunities),
                confidence_analysis: this.calculateConfidenceAnalysis(opportunities),
                exchange_health: this.circuitBreaker.getHealthReport()
            }
        };
    }

    // ===== HELPER FUNCTIONS =====

    parseExchangeList(exchangeList) {
        if (exchangeList === 'all') {
            return ['binance', 'kraken', 'kucoin'];
        } else if (exchangeList === 'major') {
            return ['binance', 'kraken', 'kucoin'];
        } else if (exchangeList === 'custom') {
            return ['binance', 'kraken'];
        } else if (typeof exchangeList === 'string') {
            return exchangeList.split(',').map(e => e.trim().toLowerCase());
        } else {
            return exchangeList;
        }
    }

    async getAssetsToScan(filter) {
        // Dynamically get assets based on filter
        if (filter === 'all' || filter === 'ALL_DISCOVERED_ASSETS') {
            try {
                const discovery = await this.exchangeManager.discoverExchangeAssets();
                if (discovery.consolidatedAssets && discovery.consolidatedAssets.length > 0) {
                    // Return all discovered assets, no limit
                    return discovery.consolidatedAssets;
                }
            } catch (error) {
                console.error('Asset discovery failed:', error.message);
            }
        }

        // Fallback to discovered assets from specific exchanges
        try {
            const assets = await this.exchangeManager.fetchBinanceAssets();
            if (assets && assets.length > 0) {
                return filter === 'major' ? assets.slice(0, 20) : 
                       filter === 'liquid' ? assets.slice(0, 10) : 
                       assets.slice(0, 50);
            }
        } catch (error) {
            console.error('Failed to fetch assets:', error.message);
        }

        // Last resort fallback - minimal set
        return ['BTC', 'ETH', 'SOL', 'ADA', 'DOT'];
    }

    async getDynamicTopAssets() {
        try {
            const discovery = await this.exchangeManager.discoverExchangeAssets();
            if (discovery.consolidatedAssets && discovery.consolidatedAssets.length > 0) {
                return discovery.consolidatedAssets.slice(0, 20);
            }
        } catch (error) {
            console.error('Failed to get dynamic top assets:', error.message);
        }

        return ['BTC', 'ETH', 'SOL', 'ADA', 'DOT', 'MATIC', 'LINK', 'AVAX'];
    }

    async getDynamicAssetPairs() {
        try {
            const assets = await this.getDynamicTopAssets();
            const pairs = [];

            // Create pairs from top assets
            for (let i = 0; i < Math.min(assets.length, 10); i++) {
                for (let j = i + 1; j < Math.min(assets.length, 10); j++) {
                    pairs.push([assets[i], assets[j]]);
                }
            }

            return pairs.slice(0, 10); // Limit to 10 pairs
        } catch (error) {
            console.error('Failed to get dynamic asset pairs:', error.message);
        }

        // Fallback pairs
        return [
            ['BTC', 'ETH'], ['ETH', 'SOL'], ['BTC', 'SOL'],
            ['MATIC', 'ADA'], ['LINK', 'DOT']
        ];
    }

    async getTriangularPaths() {
        try {
            const assets = await this.getDynamicTopAssets();
            const paths = [];

            // Create triangular paths from top assets
            if (assets.length >= 3) {
                for (let i = 0; i < Math.min(assets.length, 5); i++) {
                    for (let j = i + 1; j < Math.min(assets.length, 5); j++) {
                        for (let k = j + 1; k < Math.min(assets.length, 5); k++) {
                            paths.push({
                                path: `${assets[i]}->${assets[j]}->${assets[k]}->${assets[i]}`,
                                assets: [assets[i], assets[j], assets[k]]
                            });
                        }
                    }
                }
            }

            return paths.slice(0, 5); // Limit to 5 paths
        } catch (error) {
            console.error('Failed to get triangular paths:', error.message);
        }

        // Fallback paths
        return [
            { path: 'BTC->ETH->SOL->BTC', assets: ['BTC', 'ETH', 'SOL'] },
            { path: 'ETH->ADA->DOT->ETH', assets: ['ETH', 'ADA', 'DOT'] }
        ];
    }

    getExchangeFee(exchange) {
        const fees = {
            binance: 0.001,
            kraken: 0.0026,
            kucoin: 0.001,
            coinbase: 0.005
        };
        return fees[exchange] || 0.002;
    }

    calculateConfidence(exchangePrices, errorCount) {
        const exchangeCount = Object.keys(exchangePrices).length;
        const baseConfidence = Math.min(exchangeCount * 25, 100);
        const errorPenalty = errorCount * 10;
        return Math.max(baseConfidence - errorPenalty, 10);
    }

    estimateExecutionTime(buyExchange, sellExchange) {
        const exchangeTimes = {
            binance: 30,
            kraken: 60,
            kucoin: 45,
            coinbase: 90
        };

        const buyTime = exchangeTimes[buyExchange] || 60;
        const sellTime = exchangeTimes[sellExchange] || 60;

        return Math.max(buyTime, sellTime);
    }

    estimateMarketImpact(quantity, buyPrice, sellPrice) {
        const avgPrice = (buyPrice.last + sellPrice.last) / 2;
        const notionalValue = quantity * avgPrice;

        if (notionalValue > 100000) return 'HIGH';
        if (notionalValue > 10000) return 'MEDIUM';
        return 'LOW';
    }

    calculateLiquidityScore(prices) {
        const avgSpread = prices.reduce((sum, p) => sum + p.spread, 0) / prices.length;
        const avgPrice = prices.reduce((sum, p) => sum + (p.bid + p.ask) / 2, 0) / prices.length;
        const spreadPercentage = (avgSpread / avgPrice) * 100;
        return Math.max(100 - spreadPercentage * 10, 0);
    }

    calculateRiskScore(prices) {
        const avgPrice = prices.reduce((sum, p) => sum + (p.bid + p.ask) / 2, 0) / prices.length;
        const variance = prices.reduce((sum, p) => {
            const price = (p.bid + p.ask) / 2;
            return sum + Math.pow(price - avgPrice, 2);
        }, 0) / prices.length;
        const standardDev = Math.sqrt(variance);
        return Math.min((standardDev / avgPrice) * 1000, 100);
    }

    estimateExecutionWindow(profitBps) {
        if (profitBps > 100) return 30;
        if (profitBps > 50) return 60;
        return 120;
    }

    calculateMonitoringQuality(spreadData) {
        const assets = Object.keys(spreadData);
        const avgExchanges = assets.reduce((sum, asset) => 
            sum + spreadData[asset].prices.length, 0) / assets.length;
        return Math.round((avgExchanges / 4) * 100);
    }

    calculateProfitDistribution(opportunities) {
        const ranges = { low: 0, medium: 0, high: 0, premium: 0 };
        opportunities.forEach(opp => {
            if (opp.profit_bps < 100) ranges.low++;
            else if (opp.profit_bps < 200) ranges.medium++;
            else if (opp.profit_bps < 500) ranges.high++;
            else ranges.premium++;
        });
        return ranges;
    }

    calculateConfidenceAnalysis(opportunities) {
        const confidence = { low: 0, medium: 0, high: 0, premium: 0 };
        opportunities.forEach(opp => {
            if (opp.confidence < 50) confidence.low++;
            else if (opp.confidence < 70) confidence.medium++;
            else if (opp.confidence < 90) confidence.high++;
            else confidence.premium++;
        });
        return confidence;
    }

    async calculateTriangularArbitrageProfit(exchange, path, minProfit, maxCapital) {
        try {
            const prices = {};
            for (const asset of path.assets) {
                prices[asset] = await this.priceFetcher.fetchPriceWithFallback(asset, exchange);
                if (!prices[asset]) {
                    return null;
                }
            }

            const startAsset = path.assets[0];
            const startAmount = 1000;
            let currentAmount = startAmount;
            let totalFees = 0;

            for (let i = 0; i < path.assets.length; i++) {
                const fromAsset = path.assets[i];
                const toAsset = path.assets[(i + 1) % path.assets.length];

                if (fromAsset === toAsset) continue;

                const rate = this.calculateExchangeRate(prices[fromAsset], prices[toAsset]);
                const fee = this.getExchangeFee(exchange);

                currentAmount = currentAmount * rate * (1 - fee);
                totalFees += currentAmount * fee;
            }

            const profit = currentAmount - startAmount;
            const profitBps = (profit / startAmount) * 10000;

            if (profitBps >= minProfit) {
                return {
                    path: path.path,
                    exchange: exchange,
                    profit_bps: Math.round(profitBps),
                    profit_usd: profit,
                    complexity: 'HIGH',
                    execution_steps: path.assets.length
                };
            }

            return null;
        } catch (error) {
            console.error(`Triangular arbitrage calculation failed:`, error.message);
            return null;
        }
    }

    async calculateCrossAssetRatio(exchange, assetA, assetB, minProfit, maxCapital) {
        try {
            const priceA = await this.priceFetcher.fetchPriceWithFallback(assetA, exchange);
            const priceB = await this.priceFetcher.fetchPriceWithFallback(assetB, exchange);

            if (!priceA || !priceB) return null;

            const ratio = priceA.last / priceB.last;
            const historicalRatio = await this.getHistoricalRatio(assetA, assetB);

            const ratioDeviation = Math.abs(ratio - historicalRatio) / historicalRatio;
            const profitBps = ratioDeviation * 10000;

            if (profitBps >= minProfit) {
                return {
                    pair: `${assetA}/${assetB}`,
                    exchange: exchange,
                    current_ratio: ratio,
                    historical_ratio: historicalRatio,
                    deviation: ratioDeviation,
                    profit_bps: Math.round(profitBps),
                    profit_usd: (ratioDeviation * maxCapital * 0.5),
                    suggested_action: ratio > historicalRatio ? `SELL ${assetA}, BUY ${assetB}` : `BUY ${assetA}, SELL ${assetB}`
                };
            }

            return null;
        } catch (error) {
            console.error(`Cross-asset ratio calculation failed:`, error.message);
            return null;
        }
    }

    calculateExchangeRate(fromPrice, toPrice) {
        return fromPrice.last / toPrice.last;
    }

    async getHistoricalRatio(assetA, assetB) {
        // Calculate real historical ratio from data
        try {
            const [historicalA, historicalB] = await Promise.all([
                this.exchangeManager.getHistoricalData(assetA, '1d', 'binance'),
                this.exchangeManager.getHistoricalData(assetB, '1d', 'binance')
            ]);

            if (historicalA && historicalB && historicalA.length > 0 && historicalB.length > 0) {
                const avgA = historicalA.reduce((sum, d) => sum + d.close, 0) / historicalA.length;
                const avgB = historicalB.reduce((sum, d) => sum + d.close, 0) / historicalB.length;
                return avgA / avgB;
            }
        } catch (error) {
            console.error('Failed to get historical ratio:', error.message);
        }

        // Fallback to current ratio
        return 1.0;
    }
}
