async function realTimeMonitoring(params) {
    console.log('ðŸ“¡ Starting real-time market monitoring...');

    let [symbols = null, exchanges = 'binance,kraken', minProfit = 25, duration = 300] = params;

    // Dynamic symbol discovery if not provided
    if (!symbols) {
        const exchangeManager = new DynamicExchangeManager();
        const discovery = await exchangeManager.discoverExchangeAssets();
        if (discovery && discovery.consolidatedAssets) {
            symbols = discovery.consolidatedAssets.slice(0, 10).join(',');
        } else {
            symbols = 'BTC,ETH,SOL';
        }
    }

    try {
        const exchangeManager = new DynamicExchangeManager();
        const arbitrageEngine = new EnterpriseArbitrageEngine(exchangeManager);

        const monitoringResults = [];
        const startTime = Date.now();
        const endTime = startTime + (duration * 1000);

        let iteration = 0;
        while (Date.now() < endTime && iteration < 10) {
            try {
                iteration++;
                console.log(`Monitoring iteration ${iteration}`);

                const snapshot = await arbitrageEngine.monitorArbitrageSpreads(exchanges, minProfit);

                if (snapshot.success) {
                    monitoringResults.push({
                        iteration: iteration,
                        timestamp: new Date().toISOString(),
                        spread_data: snapshot.spread_monitoring.spread_data,
                        profitable_opportunities: snapshot.spread_monitoring.profitable_opportunities,
                        exchange_health: snapshot.spread_monitoring.exchange_health
                    });
                }

                if (Date.now() < endTime) {
                    await new Promise(resolve => setTimeout(resolve, 30000));
                }

            } catch (iterationError) {
                console.error(`Monitoring iteration ${iteration} failed:`, iterationError.message);
                monitoringResults.push({
                    iteration: iteration,
                    timestamp: new Date().toISOString(),
                    error: iterationError.message
                });
            }
        }

        const analysis = analyzeMonitoringResults(monitoringResults);

        return {
            success: true,
            timestamp: new Date().toISOString(),
            real_time_monitoring: {
                monitoring_duration: Math.round((Date.now() - startTime) / 1000),
                total_iterations: iteration,
                successful_iterations: monitoringResults.filter(r => !r.error).length,
                monitoring_results: monitoringResults,
                analysis: analysis,
                persistent_opportunities: analysis.persistent_opportunities,
                trend_analysis: analysis.trend_analysis,
                recommendations: analysis.recommendations
            }
        };

    } catch (error) {
        return {
            success: false,
            error: error.message,
            timestamp: new Date().toISOString()
        };
    }
}

