function calculateComprehensiveSummary(results) {
    const summary = {
        total_opportunities: 0,
        total_potential_profit: 0,
        high_confidence_opportunities: 0,
        data_sources_healthy: 0,
        market_efficiency_score: 0
    };

    if (results.arbitrage_opportunities && results.arbitrage_opportunities.arbitrage_scan) {
        summary.total_opportunities += results.arbitrage_opportunities.arbitrage_scan.opportunities_found || 0;
        summary.total_potential_profit += results.arbitrage_opportunities.arbitrage_scan.total_potential_profit || 0;
    }

    if (results.market_inefficiencies && results.market_inefficiencies.inefficiency_scan) {
        summary.total_opportunities += results.market_inefficiencies.inefficiency_scan.total_inefficiencies || 0;
    }

    if (results.institutional_flow && results.institutional_flow.institutional_flow) {
        summary.total_opportunities += results.institutional_flow.institutional_flow.signals_detected || 0;
    }

    return summary;
}

function generateActionableRecommendations(results) {
    const recommendations = [];

    if (results.arbitrage_opportunities && results.arbitrage_opportunities.success) {
        const opportunities = results.arbitrage_opportunities.arbitrage_scan.opportunities_found || 0;
        if (opportunities > 5) {
            recommendations.push({
                type: 'ARBITRAGE',
                action: 'EXECUTE_TOP_OPPORTUNITIES',
                priority: 'HIGH',
                reason: `${opportunities} arbitrage opportunities detected with high profit potential`
            });
        }
    }

    if (results.technical_analysis) {
        Object.keys(results.technical_analysis).forEach(symbol => {
            const analysis = results.technical_analysis[symbol];
            if (analysis.signals && analysis.signals.overall_signal !== 'HOLD') {
                recommendations.push({
                    type: 'TECHNICAL',
                    action: analysis.signals.overall_signal,
                    symbol: symbol,
                    priority: analysis.signals.signal_strength > 3 ? 'HIGH' : 'MEDIUM',
                    reason: `Technical signals suggest ${analysis.signals.overall_signal} for ${symbol}`
                });
            }
        });
    }

    if (results.institutional_flow && results.institutional_flow.success) {
        const signals = results.institutional_flow.institutional_flow.institutional_signals || [];
        signals.forEach(signal => {
            if (signal.confidence === 'HIGH') {
                recommendations.push({
                    type: 'INSTITUTIONAL',
                    action: signal.direction,
                    symbol: signal.symbol,
                    priority: 'HIGH',
                    reason: `Strong institutional ${signal.primary_signal} detected`
                });
            }
        });
    }

    return recommendations.slice(0, 10);
}

function assessDataQuality(results) {
    let totalSources = 0;
    let healthySources = 0;

    Object.keys(results).forEach(key => {
        totalSources++;
        if (results[key] && !results[key].error) {
            healthySources++;
        }
    });

    const healthPercentage = (healthySources / totalSources) * 100;

    return {
        healthy_sources: healthySources,
        total_sources: totalSources,
        health_percentage: Math.round(healthPercentage),
        quality_rating: healthPercentage > 80 ? 'EXCELLENT' : healthPercentage > 60 ? 'GOOD' : healthPercentage > 40 ? 'FAIR' : 'POOR'
    };
}

function prioritizeOpportunities(results) {
    const priorities = [];

    if (results.arbitrage_opportunities && results.arbitrage_opportunities.arbitrage_scan) {
        const opps = results.arbitrage_opportunities.arbitrage_scan.top_opportunities || [];
        opps.forEach(opp => {
            priorities.push({
                type: 'ARBITRAGE',
                symbol: opp.asset,
                profit_potential: opp.profit_usd,
                confidence: opp.confidence,
                priority_score: opp.profit_usd * (opp.confidence / 100)
            });
        });
    }

    return priorities.sort((a, b) => b.priority_score - a.priority_score).slice(0, 5);
}

function calculateUnifiedScore(opportunity, type) {
    let score = 50;

    if (type === 'alpha') {
        score += opportunity.priority || 0;
        score += (opportunity.confidence || 50) * 0.5;
        if (opportunity.risk_level === 'LOW') score += 10;
        if (opportunity.risk_level === 'HIGH') score -= 10;
    } else if (type === 'arbitrage') {
        score += (opportunity.profit_bps || 0) * 0.1;
        score += (opportunity.confidence || 50) * 0.3;
        score += (opportunity.profit_usd || 0) * 0.01;
    }

    return Math.min(100, Math.max(0, score));
}

function generatePortfolioAllocation(opportunities, maxCapital) {
    const allocation = {
        allocations: [],
        total_allocated: 0,
        total_expected_return: 0,
        diversification_level: 'MEDIUM'
    };

    let remainingCapital = maxCapital;

    for (const opp of opportunities.slice(0, 5)) {
        const allocationPercentage = Math.min(0.3, opp.unified_score / 100 * 0.4);
        const allocatedAmount = Math.min(remainingCapital * allocationPercentage, opp.capital_requirement || 0);

        if (allocatedAmount > 1000) {
            allocation.allocations.push({
                opportunity: opp,
                allocated_capital: allocatedAmount,
                allocation_percentage: (allocatedAmount / maxCapital) * 100,
                expected_return: opp.expected_return || 0,
                risk_level: opp.risk_level || 'MEDIUM'
            });

            allocation.total_allocated += allocatedAmount;
            allocation.total_expected_return += opp.expected_return || 0;
            remainingCapital -= allocatedAmount;
        }
    }

    return allocation;
}

function calculateRiskMetrics(opportunities) {
    const riskLevels = { LOW: 0, MEDIUM: 0, HIGH: 0 };

    opportunities.forEach(opp => {
        const risk = opp.risk_level || 'MEDIUM';
        riskLevels[risk]++;
    });

    return {
        risk_distribution: riskLevels,
        avg_risk_score: opportunities.length > 0 ? (riskLevels.LOW * 1 + riskLevels.MEDIUM * 2 + riskLevels.HIGH * 3) / opportunities.length : 0,
        high_risk_percentage: opportunities.length > 0 ? (riskLevels.HIGH / opportunities.length) * 100 : 0
    };
}

function calculateDiversificationScore(opportunities) {
    const sources = new Set(opportunities.map(opp => opp.source));
    const symbols = new Set(opportunities.map(opp => opp.symbol || opp.asset));

    const sourceScore = Math.min(sources.size * 25, 100);
    const symbolScore = Math.min(symbols.size * 15, 100);

    return Math.round((sourceScore + symbolScore) / 2);
}

function generateUnifiedRecommendations(opportunities, allocation) {
    const recommendations = [];

    if (allocation.total_allocated < allocation.total_allocated * 0.8) {
        recommendations.push({
            type: 'CAPITAL_ALLOCATION',
            message: 'Consider increasing capital allocation to capture more opportunities',
            priority: 'MEDIUM'
        });
    }

    const highRiskOpps = opportunities.filter(opp => opp.risk_level === 'HIGH').length;
    if (highRiskOpps > opportunities.length * 0.3) {
        recommendations.push({
            type: 'RISK_MANAGEMENT',
            message: 'High concentration of high-risk opportunities detected',
            priority: 'HIGH'
        });
    }

    return recommendations;
}

// Health calculation functions
function calculateLiquidityHealth(priceData) {
    let totalVolume = 0;
    let validPairs = 0;

    Object.keys(priceData).forEach(exchange => {
        Object.keys(priceData[exchange]).forEach(symbol => {
            const data = priceData[exchange][symbol];
            if (data && !data.error && data.volume24h) {
                totalVolume += data.volume24h;
                validPairs++;
            }
        });
    });

    const avgVolume = validPairs > 0 ? totalVolume / validPairs : 0;
    const score = Math.min((avgVolume / 1000000) * 20, 100);

    return {
        score: Math.round(score),
        avg_volume: avgVolume,
        status: score > 70 ? 'HEALTHY' : score > 40 ? 'MODERATE' : 'POOR'
    };
}

function calculateVolatilityHealth(priceData, technicalData) {
    let totalVolatility = 0;
    let validPairs = 0;

    Object.keys(priceData).forEach(exchange => {
        Object.keys(priceData[exchange]).forEach(symbol => {
            const data = priceData[exchange][symbol];
            if (data && !data.error && data.change24h !== undefined) {
                totalVolatility += Math.abs(data.change24h);
                validPairs++;
            }
        });
    });

    const avgVolatility = validPairs > 0 ? totalVolatility / validPairs : 0;

    let score = 100;
    if (avgVolatility > 15) score = 30;
    else if (avgVolatility > 8) score = 60;
    else if (avgVolatility < 0.5) score = 40;

    return {
        score: Math.round(score),
        avg_volatility: avgVolatility,
        status: score > 70 ? 'OPTIMAL' : score > 40 ? 'SUBOPTIMAL' : 'POOR'
    };
}

function calculateEfficiencyHealth(inefficiencies) {
    if (!inefficiencies || !inefficiencies.success) {
        return { score: 50, status: 'UNKNOWN' };
    }

    const totalInefficiencies = inefficiencies.inefficiency_scan.total_inefficiencies || 0;
    const score = Math.max(100 - (totalInefficiencies * 5), 0);

    return {
        score: Math.round(score),
        inefficiencies_detected: totalInefficiencies,
        status: score > 80 ? 'EFFICIENT' : score > 60 ? 'MODERATE' : 'INEFFICIENT'
    };
}

function calculateTechnicalHealth(technicalData) {
    if (!technicalData) {
        return { score: 50, status: 'UNKNOWN' };
    }

    let bullishSignals = 0;
    let bearishSignals = 0;
    let totalSignals = 0;

    Object.keys(technicalData).forEach(symbol => {
        const analysis = technicalData[symbol];
        if (analysis.signals) {
            totalSignals++;
            if (analysis.signals.overall_signal === 'BUY') bullishSignals++;
            else if (analysis.signals.overall_signal === 'SELL') bearishSignals++;
        }
    });

    const neutralSignals = totalSignals - bullishSignals - bearishSignals;
    const balance = totalSignals > 0 ? Math.abs(bullishSignals - bearishSignals) / totalSignals : 0;
    const score = Math.round((1 - balance) * 100);

    return {
        score: score,
        bullish_signals: bullishSignals,
        bearish_signals: bearishSignals,
        neutral_signals: neutralSignals,
        status: score > 70 ? 'BALANCED' : 'IMBALANCED'
    };
}

function calculateCorrelationHealth(priceData) {
    const changes = [];

    Object.keys(priceData).forEach(exchange => {
        Object.keys(priceData[exchange]).forEach(symbol => {
            const data = priceData[exchange][symbol];
            if (data && !data.error && data.change24h !== undefined) {
                changes.push(data.change24h);
            }
        });
    });

    if (changes.length < 2) {
        return { score: 50, status: 'INSUFFICIENT_DATA' };
    }

    const avgChange = changes.reduce((sum, change) => sum + change, 0) / changes.length;
    const variance = changes.reduce((sum, change) => sum + Math.pow(change - avgChange, 2), 0) / changes.length;

    const score = Math.min(variance * 10, 100);

    return {
        score: Math.round(score),
        variance: variance,
        status: score > 60 ? 'DIVERSIFIED' : score > 30 ? 'MODERATE' : 'HIGHLY_CORRELATED'
    };
}

function calculateVolumeHealth(priceData) {
    let exchangeVolumes = {};

    Object.keys(priceData).forEach(exchange => {
        let exchangeTotal = 0;
        let symbolCount = 0;

        Object.keys(priceData[exchange]).forEach(symbol => {
            const data = priceData[exchange][symbol];
            if (data && !data.error && data.volume24h) {
                exchangeTotal += data.volume24h;
                symbolCount++;
            }
        });

        if (symbolCount > 0) {
            exchangeVolumes[exchange] = exchangeTotal / symbolCount;
        }
    });

    const volumes = Object.values(exchangeVolumes);
    const avgVolume = volumes.length > 0 ? volumes.reduce((sum, vol) => sum + vol, 0) / volumes.length : 0;
    const score = Math.min((avgVolume / 5000000) * 100, 100);

    return {
        score: Math.round(score),
        avg_volume: avgVolume,
        exchange_volumes: exchangeVolumes,
        status: score > 70 ? 'HIGH' : score > 40 ? 'MODERATE' : 'LOW'
    };
}

function getHealthRating(score) {
    if (score >= 80) return 'EXCELLENT';
    if (score >= 60) return 'GOOD';
    if (score >= 40) return 'FAIR';
    return 'POOR';
}

function assessMarketConditions(healthMetrics) {
    const conditions = [];

    if (healthMetrics.volatility_health.score < 40) {
        conditions.push('HIGH_VOLATILITY');
    }

    if (healthMetrics.liquidity_health.score < 50) {
        conditions.push('LOW_LIQUIDITY');
    }

    if (healthMetrics.efficiency_health.score < 60) {
        conditions.push('MARKET_INEFFICIENCIES');
    }

    if (conditions.length === 0) {
        conditions.push('NORMAL_TRADING');
    }

    return conditions;
}

function generateHealthBasedRecommendations(healthMetrics) {
    const recommendations = [];

    if (healthMetrics.liquidity_health.score < 50) {
        recommendations.push({
            type: 'LIQUIDITY',
            message: 'Low liquidity detected - reduce position sizes and increase execution time',
            priority: 'HIGH'
        });
    }

    if (healthMetrics.volatility_health.score < 40) {
        recommendations.push({
            type: 'VOLATILITY',
            message: 'High volatility - implement stricter risk management',
            priority: 'HIGH'
        });
    }

    if (healthMetrics.efficiency_health.score < 60) {
        recommendations.push({
            type: 'EFFICIENCY',
            message: 'Market inefficiencies present - opportunity for alpha generation',
            priority: 'MEDIUM'
        });
    }

    return recommendations;
}

function generateRiskWarnings(healthMetrics) {
    const warnings = [];

    if (healthMetrics.correlation_health.score < 30) {
        warnings.push({
            level: 'HIGH',
            message: 'High correlation detected - systemic risk elevated'
        });
    }

    if (healthMetrics.volatility_health.score < 30) {
        warnings.push({
            level: 'HIGH',
            message: 'Extreme volatility - potential for significant losses'
        });
    }

    return warnings;
}

function generateOpportunityOutlook(healthMetrics) {
    let outlook = 'NEUTRAL';
    let reasoning = [];

    if (healthMetrics.efficiency_health.score < 70) {
        outlook = 'POSITIVE';
        reasoning.push('Market inefficiencies present');
    }

    if (healthMetrics.volatility_health.score > 60 && healthMetrics.volatility_health.score < 80) {
        outlook = 'POSITIVE';
        reasoning.push('Optimal volatility for trading');
    }

    if (healthMetrics.liquidity_health.score > 70) {
        reasoning.push('Good liquidity for execution');
    }

    return {
        outlook: outlook,
        reasoning: reasoning,
        confidence: reasoning.length > 1 ? 'HIGH' : 'MEDIUM'
    };
}

function analyzeMonitoringResults(results) {
    const successfulResults = results.filter(r => !r.error);

    if (successfulResults.length === 0) {
        return {
            persistent_opportunities: [],
            trend_analysis: { trend: 'UNKNOWN' },
            recommendations: []
        };
    }

    const opportunityTracker = new Map();

    successfulResults.forEach(result => {
        Object.keys(result.spread_data || {}).forEach(symbol => {
            const data = result.spread_data[symbol];
            if (data.profitable) {
                if (!opportunityTracker.has(symbol)) {
                    opportunityTracker.set(symbol, []);
                }
                opportunityTracker.get(symbol).push({
                    iteration: result.iteration,
                    profit_bps: data.profit_bps,
                    timestamp: result.timestamp
                });
            }
        });
    });

    const persistentOpportunities = [];
    opportunityTracker.forEach((occurrences, symbol) => {
        if (occurrences.length >= Math.max(2, successfulResults.length * 0.3)) {
            persistentOpportunities.push({
                symbol: symbol,
                occurrences: occurrences.length,
                avg_profit_bps: occurrences.reduce((sum, occ) => sum + occ.profit_bps, 0) / occurrences.length,
                persistence_rate: occurrences.length / successfulResults.length
            });
        }
    });

    return {
        persistent_opportunities: persistentOpportunities,
        trend_analysis: {
            trend: persistentOpportunities.length > 0 ? 'STABLE_OPPORTUNITIES' : 'VOLATILE_MARKET',
            opportunity_count_trend: 'STABLE'
        },
        recommendations: persistentOpportunities.length > 0 ? [
            {
                type: 'EXECUTION',
                message: `${persistentOpportunities.length} persistent opportunities detected - consider execution`,
                priority: 'HIGH'
            }
        ] : []
    };
}



