async function masterSystemController(functionType, workflow_type, service_name, trading_mode) {
    console.log(`ðŸŽ›ï¸ Master System Controller Enterprise - Function: ${functionType}`);

    try {
        performanceMetrics.totalRequests++;
        const startTime = Date.now();

        let result;
        switch (functionType) {
            case 'system_health':
                result = await performComprehensiveHealthCheck();
                break;

            case 'performance_metrics':
                result = await getPerformanceMetrics();
                break;

            case 'circuit_breaker':
                result = await manageCircuitBreaker(serviceName, workflowType);
                break;

            case 'auto_recovery':
                result = await executeAutoRecovery(serviceName);
                break;

            case 'dashboard_data':
                result = await generateDashboardData();
                break;

            case 'emergency_stop':
                result = await executeEmergencyStop();
                break;

            case 'system_reset':
                result = await performSystemReset();
                break;

            case 'service_diagnostics':
                result = await runServiceDiagnostics(serviceName);
                break;

            case 'performance_optimization':
                result = await optimizeSystemPerformance();
                break;

            case 'alert_management':
                result = await manageAlerts(workflowType);
                break;

            default:
                result = {
                    success: false,
                    error: `Unknown function: ${functionType}`,
                    available_functions: [
                        'system_health',
                        'performance_metrics',
                        'circuit_breaker',
                        'auto_recovery',
                        'dashboard_data',
                        'emergency_stop',
                        'system_reset',
                        'service_diagnostics',
                        'performance_optimization',
                        'alert_management'
                    ]
                };
        }

        // Track performance
        const responseTime = Date.now() - startTime;
        performanceMetrics.averageResponseTime = 
            (performanceMetrics.averageResponseTime * (performanceMetrics.totalRequests - 1) + responseTime) / 
            performanceMetrics.totalRequests;

        if (responseTime > performanceMetrics.peakResponseTime) {
            performanceMetrics.peakResponseTime = responseTime;
        }

        if (result.success) {
            performanceMetrics.successfulRequests++;
        } else {
            performanceMetrics.failedRequests++;
        }

        return result;

    } catch (error) {
        performanceMetrics.failedRequests++;
        console.error('âŒ Master System Controller error:', error);

        // Log to dashboard
        addDashboardEvent('error', `System controller error: ${error.message}`, 'high');

        return {
            success: false,
            error: error.message,
            function_attempted: functionType,
            recovery_attempted: autoRecovery.enabled
        };
    }
}

// ===== ENHANCED HEALTH MONITORING =====

async function performComprehensiveHealthCheck() {
    console.log('ðŸ¥ Performing comprehensive enterprise health check...');

    const healthChecks = {
        services: {},
        infrastructure: {},
        performance: {},
        security: {},
        data_integrity: {}
    };

    // Parallel service health checks
    const serviceChecks = await Promise.allSettled([
        testServiceWithMetrics('marketIntelligence', testMarketIntelligenceService),
        testServiceWithMetrics('portfolioRisk', testPortfolioRiskService),
        testServiceWithMetrics('alphaGeneration', testAlphaGenerationService),
        testServiceWithMetrics('multiAI', testMultiAIService),
        testServiceWithMetrics('telegram', testTelegramService),
        testServiceWithMetrics('arbitrage', testArbitrageService),
        testServiceWithMetrics('tradeExecution', testTradeExecutionService),
        testServiceWithMetrics('advancedTrading', testAdvancedTradingService)
    ]);

    // Process service health results
    let healthyServices = 0;
    let degradedServices = 0;
    let criticalServices = 0;

    serviceChecks.forEach((check, index) => {
        const serviceName = Object.keys(serviceHealth)[index];
        if (check.status === 'fulfilled' && check.value.success) {
            healthyServices++;
            healthChecks.services[serviceName] = 'HEALTHY';
        } else if (check.status === 'fulfilled' && check.value.degraded) {
            degradedServices++;
            healthChecks.services[serviceName] = 'DEGRADED';
        } else {
            criticalServices++;
            healthChecks.services[serviceName] = 'CRITICAL';

            // Trigger auto-recovery if enabled
            if (autoRecovery.enabled) {
                console.log(`ðŸ”§ Triggering auto-recovery for ${serviceName}`);
                executeAutoRecovery(serviceName);
            }
        }
    });

    // Infrastructure health
    healthChecks.infrastructure = await checkInfrastructureHealth();

    // Performance health
    healthChecks.performance = {
        average_response_time: performanceMetrics.averageResponseTime,
        peak_response_time: performanceMetrics.peakResponseTime,
        success_rate: (performanceMetrics.successfulRequests / performanceMetrics.totalRequests * 100).toFixed(2) + '%',
        status: performanceMetrics.averageResponseTime < 500 ? 'OPTIMAL' : 
                performanceMetrics.averageResponseTime < 1000 ? 'ACCEPTABLE' : 'DEGRADED'
    };

    // Security health
    healthChecks.security = await checkSecurityHealth();

    // Data integrity
    healthChecks.data_integrity = await checkDataIntegrity();

    // Calculate overall system health
    const totalServices = healthyServices + degradedServices + criticalServices;
    const healthScore = (healthyServices * 100 + degradedServices * 50) / (totalServices * 100) * 100;

    const systemStatus = healthScore > 80 ? 'HEALTHY' : 
                        healthScore > 60 ? 'DEGRADED' : 
                        healthScore > 40 ? 'WARNING' : 'CRITICAL';

    // Update dashboard
    dashboardData.systemStatus = systemStatus;
    dashboardData.lastUpdate = new Date().toISOString();

    // Generate recommendations
    const recommendations = generateHealthRecommendations(healthChecks, systemStatus);

    // Send alert if critical
    if (systemStatus === 'CRITICAL' || systemStatus === 'WARNING') {
        await sendSystemAlert(systemStatus, healthChecks);
    }

    return {
        success: true,
        timestamp: new Date().toISOString(),
        system_status: systemStatus,
        health_score: healthScore.toFixed(1),
        services: {
            healthy: healthyServices,
            degraded: degradedServices,
            critical: criticalServices,
            total: totalServices
        },
        health_checks: healthChecks,
        recommendations: recommendations,
        auto_recovery_enabled: autoRecovery.enabled,
        circuit_breakers_active: Object.values(circuitBreakers.states).filter(cb => cb.state === 'open').length
    };
}

async function testServiceWithMetrics(serviceName, testFunction) {
    const startTime = Date.now();

    try {
        const result = await testFunction();
        const responseTime = Date.now() - startTime;

        // Update service health metrics
        if (!serviceHealth[serviceName].responseTime) {
            serviceHealth[serviceName].responseTime = [];
        }

        serviceHealth[serviceName].responseTime.push(responseTime);
        if (serviceHealth[serviceName].responseTime.length > 100) {
            serviceHealth[serviceName].responseTime.shift(); // Keep last 100
        }

        serviceHealth[serviceName].status = result.success ? 'healthy' : 'unhealthy';
        serviceHealth[serviceName].lastCheck = new Date().toISOString();

        if (result.success) {
            // Reset error count on success
            serviceHealth[serviceName].errorCount = 0;
        } else {
            serviceHealth[serviceName].errorCount++;
            serviceHealth[serviceName].lastError = result.error;
        }

        // Calculate success rate
        const totalChecks = serviceHealth[serviceName].responseTime.length;
        const successfulChecks = totalChecks - serviceHealth[serviceName].errorCount;
        serviceHealth[serviceName].successRate = (successfulChecks / totalChecks * 100).toFixed(2);

        return result;

    } catch (error) {
        serviceHealth[serviceName].status = 'critical';
        serviceHealth[serviceName].errorCount++;
        serviceHealth[serviceName].lastError = error.message;
        serviceHealth[serviceName].lastCheck = new Date().toISOString();

        throw error;
    }
}

// ===== CIRCUIT BREAKER IMPLEMENTATION =====

async function manageCircuitBreaker(serviceName, action) {
    console.log(`âš¡ Managing circuit breaker for ${serviceName}`);

    if (!circuitBreakers.states[serviceName]) {
        circuitBreakers.states[serviceName] = {
            state: 'closed',
            failures: 0,
            lastFailure: null,
            lastSuccess: null
        };
    }

    const breaker = circuitBreakers.states[serviceName];

    switch (action) {
        case 'check':
            return {
                success: true,
                service: serviceName,
                state: breaker.state,
                failures: breaker.failures,
                can_proceed: breaker.state !== 'open'
            };

        case 'record_success':
            if (breaker.state === 'half-open') {
                breaker.state = 'closed';
                breaker.failures = 0;
                console.log(`âœ… Circuit breaker for ${serviceName} closed after successful recovery`);
            }
            breaker.lastSuccess = Date.now();
            break;

        case 'record_failure':
            breaker.failures++;
            breaker.lastFailure = Date.now();

            if (breaker.failures >= circuitBreakers.maxConsecutiveFailures) {
                breaker.state = 'open';
                console.log(`ðŸ”´ Circuit breaker for ${serviceName} OPENED after ${breaker.failures} failures`);

                // Schedule half-open transition
                setTimeout(() => {
                    breaker.state = 'half-open';
                    console.log(`ðŸŸ¡ Circuit breaker for ${serviceName} moved to half-open`);
                }, circuitBreakers.cooldownPeriod);

                // Trigger alert
                await sendSystemAlert('CIRCUIT_BREAKER_OPEN', {
                    service: serviceName,
                    failures: breaker.failures
                });
            }
            break;

        case 'reset':
            breaker.state = 'closed';
            breaker.failures = 0;
            breaker.lastFailure = null;
            console.log(`ðŸ”„ Circuit breaker for ${serviceName} manually reset`);
            break;
    }

    return {
        success: true,
        service: serviceName,
        breaker_state: breaker.state,
        failures: breaker.failures,
        timestamp: new Date().toISOString()
    };
}

// ===== AUTO-RECOVERY MECHANISMS =====

async function executeAutoRecovery(serviceName) {
    console.log(`ðŸ”§ Executing auto-recovery for ${serviceName}`);

    const recoveryLog = {
        service: serviceName,
        attempts: [],
        final_status: 'pending',
        timestamp: new Date().toISOString()
    };

    // Check if service is in circuit breaker open state
    if (circuitBreakers.states[serviceName]?.state === 'open') {
        console.log(`âš ï¸ Service ${serviceName} circuit breaker is open, waiting for cooldown`);
        return {
            success: false,
            message: 'Circuit breaker is open, recovery postponed',
            service: serviceName
        };
    }

    // Try recovery strategies in order
    const strategies = ['restart', 'reconnect', 'fallback', 'scale'];

    for (const strategy of strategies) {
        if (recoveryLog.attempts.length >= autoRecovery.maxRetries) {
            console.log(`âŒ Max recovery attempts reached for ${serviceName}`);
            break;
        }

        console.log(`Attempting ${strategy} recovery strategy...`);

        try {
            const result = await autoRecovery.recoveryStrategies[strategy](serviceName);

            recoveryLog.attempts.push({
                strategy: strategy,
                success: result.success,
                timestamp: new Date().toISOString()
            });

            if (result.success) {
                // Verify service is working
                await new Promise(resolve => setTimeout(resolve, 2000)); // Wait 2 seconds
                const healthCheck = await testServiceHealth(serviceName);

                if (healthCheck.success) {
                    recoveryLog.final_status = 'recovered';
                    console.log(`âœ… Service ${serviceName} successfully recovered using ${strategy}`);

                    // Reset circuit breaker
                    await manageCircuitBreaker(serviceName, 'reset');

                    // Send recovery notification
                    await sendSystemAlert('SERVICE_RECOVERED', {
                        service: serviceName,
                        strategy: strategy
                    });

                    return {
                        success: true,
                        service: serviceName,
                        recovery_strategy: strategy,
                        attempts: recoveryLog.attempts.length,
                        timestamp: new Date().toISOString()
                    };
                }
            }

        } catch (error) {
            console.error(`Recovery strategy ${strategy} failed:`, error.message);
            recoveryLog.attempts.push({
                strategy: strategy,
                success: false,
                error: error.message,
                timestamp: new Date().toISOString()
            });
        }

        // Wait before next attempt
        await new Promise(resolve => setTimeout(resolve, autoRecovery.retryDelay));
    }

    // All recovery attempts failed
    recoveryLog.final_status = 'failed';

    // Escalate if threshold reached
    if (recoveryLog.attempts.length >= autoRecovery.escalationThreshold) {
        await escalateRecoveryFailure(serviceName, recoveryLog);
    }

    return {
        success: false,
        service: serviceName,
        message: 'All recovery strategies failed',
        attempts: recoveryLog.attempts,
        escalated: recoveryLog.attempts.length >= autoRecovery.escalationThreshold
    };
}

