// ===== PERFORMANCE METRICS & MONITORING =====

async function getPerformanceMetrics() {
    console.log('ðŸ“Š Generating performance metrics...');

    const uptime = Date.now() - performanceMetrics.systemUptime;
    const uptimeHours = uptime / (1000 * 60 * 60);

    // Calculate service-specific metrics
    const serviceMetrics = {};
    for (const [service, health] of Object.entries(serviceHealth)) {
        const avgResponseTime = health.responseTime.length > 0 ?
            health.responseTime.reduce((a, b) => a + b, 0) / health.responseTime.length : 0;

        serviceMetrics[service] = {
            status: health.status,
            success_rate: health.successRate + '%',
            average_response_time: avgResponseTime.toFixed(2) + 'ms',
            error_count: health.errorCount,
            last_check: health.lastCheck
        };
    }

    // API usage tracking
    const apiUsageStatus = {};
    for (const [api, usage] of Object.entries(performanceMetrics.apiUsage)) {
        const usagePercent = (usage.calls / usage.limit * 100).toFixed(2);
        apiUsageStatus[api] = {
            calls: usage.calls,
            limit: usage.limit,
            usage_percent: usagePercent + '%',
            status: usagePercent < 80 ? 'NORMAL' : usagePercent < 95 ? 'WARNING' : 'CRITICAL'
        };
    }

    // Trading performance
    const tradingMetrics = {
        total_trades: performanceMetrics.totalTrades || 0,
        successful_trades: performanceMetrics.successfulTrades || 0,
        win_rate: performanceMetrics.totalTrades > 0 ? 
            ((performanceMetrics.successfulTrades / performanceMetrics.totalTrades) * 100).toFixed(2) + '%' : 'N/A',
        profit_loss: performanceMetrics.profitLoss
    };

    return {
        success: true,
        timestamp: new Date().toISOString(),
        system_metrics: {
            uptime_hours: uptimeHours.toFixed(2),
            total_requests: performanceMetrics.totalRequests,
            successful_requests: performanceMetrics.successfulRequests,
            failed_requests: performanceMetrics.failedRequests,
            success_rate: ((performanceMetrics.successfulRequests / performanceMetrics.totalRequests) * 100).toFixed(2) + '%',
            average_response_time: performanceMetrics.averageResponseTime.toFixed(2) + 'ms',
            peak_response_time: performanceMetrics.peakResponseTime + 'ms'
        },
        service_metrics: serviceMetrics,
        api_usage: apiUsageStatus,
        trading_performance: tradingMetrics,
        resource_usage: await getResourceUsage(),
        alerts: dashboardData.activeAlerts.length,
        recommendations: generatePerformanceRecommendations(serviceMetrics, apiUsageStatus)
    };
}

