async function generateDashboardData() {
    console.log('ðŸ“ˆ Generating real-time dashboard data...');

    const healthCheck = await performComprehensiveHealthCheck();
    const metrics = await getPerformanceMetrics();

    // Recent events (last 50)
    const recentEvents = dashboardData.recentEvents.slice(-50);

    // Active alerts
    const activeAlerts = dashboardData.activeAlerts.filter(alert => 
        !alert.resolved && (Date.now() - alert.timestamp < 3600000) // 1 hour
    );

    // Performance trend (last 24 hours)
    const performanceTrend = generatePerformanceTrend();

    return {
        success: true,
        timestamp: new Date().toISOString(),
        dashboard: {
            system_status: healthCheck.system_status,
            health_score: healthCheck.health_score,
            services: healthCheck.services,
            performance: {
                uptime: metrics.system_metrics.uptime_hours + ' hours',
                success_rate: metrics.system_metrics.success_rate,
                avg_response_time: metrics.system_metrics.average_response_time
            },
            trading: metrics.trading_performance,
            alerts: {
                active: activeAlerts.length,
                critical: activeAlerts.filter(a => a.severity === 'critical').length,
                warning: activeAlerts.filter(a => a.severity === 'warning').length,
                info: activeAlerts.filter(a => a.severity === 'info').length
            },
            recent_events: recentEvents.slice(-10),
            performance_trend: performanceTrend,
            api_usage: metrics.api_usage,
            resource_usage: metrics.resource_usage
        }
    };
}

function generatePerformanceTrend() {
    // Generate hourly performance data for last 24 hours
    const trend = [];
    const now = Date.now();

    for (let i = 23; i >= 0; i--) {
        const hour = new Date(now - i * 3600000);
        trend.push({
            time: hour.toISOString(),
            success_rate: 95 + Math.random() * 5,
            response_time: 200 + Math.random() * 300,
            requests: Math.floor(100 + Math.random() * 500)
        });
    }

    return trend;
}

// ===== ALERT MANAGEMENT =====

async function manageAlerts(action) {
    console.log(`ðŸš¨ Managing alerts - Action: ${action}`);

    switch (action) {
        case 'get_active':
            return {
                success: true,
                alerts: dashboardData.activeAlerts,
                count: dashboardData.activeAlerts.length
            };

        case 'acknowledge':
            // Mark alerts as acknowledged
            dashboardData.activeAlerts.forEach(alert => {
                alert.acknowledged = true;
                alert.acknowledged_at = new Date().toISOString();
            });
            return { success: true, message: 'All alerts acknowledged' };

        case 'resolve':
            // Resolve acknowledged alerts
            dashboardData.activeAlerts = dashboardData.activeAlerts.filter(alert => 
                !alert.acknowledged
            );
            return { success: true, message: 'Acknowledged alerts resolved' };

        case 'clear_old':
            // Clear alerts older than 24 hours
            const cutoff = Date.now() - 86400000;
            dashboardData.activeAlerts = dashboardData.activeAlerts.filter(alert =>
                alert.timestamp > cutoff
            );
            return { success: true, message: 'Old alerts cleared' };

        default:
            return { success: false, error: 'Unknown alert action' };
    }
}

async function sendSystemAlert(type, details) {
    const alert = {
        id: `alert_${Date.now()}`,
        type: type,
        severity: getSeverityForAlertType(type),
        message: formatAlertMessage(type, details),
        details: details,
        timestamp: Date.now(),
        acknowledged: false,
        resolved: false
    };

    dashboardData.activeAlerts.push(alert);

    // Send to Telegram if critical
    if (alert.severity === 'critical') {
        try {
            await telegramCommandCenter(
                'send_alert',
                'system_critical',
                alert.message,
                'critical',
                null
            );
        } catch (error) {
            console.error('Failed to send Telegram alert:', error);
        }
    }

    return alert;
}

function getSeverityForAlertType(type) {
    const severityMap = {
        'CRITICAL': 'critical',
        'WARNING': 'warning',
        'CIRCUIT_BREAKER_OPEN': 'critical',
        'SERVICE_RECOVERED': 'info',
        'API_LIMIT_WARNING': 'warning',
        'PERFORMANCE_DEGRADED': 'warning',
        'SECURITY_THREAT': 'critical'
    };
    return severityMap[type] || 'info';
}

function formatAlertMessage(type, details) {
    const templates = {
        'CRITICAL': `ðŸ”´ CRITICAL: System health is critical. ${details.services?.critical || 0} services down.`,
        'WARNING': `âš ï¸ WARNING: System health degraded. Check services immediately.`,
        'CIRCUIT_BREAKER_OPEN': `âš¡ Circuit breaker opened for ${details.service} after ${details.failures} failures`,
        'SERVICE_RECOVERED': `âœ… Service ${details.service} recovered using ${details.strategy}`,
        'API_LIMIT_WARNING': `âš ï¸ API limit warning: ${details.api} at ${details.usage}% usage`,
        'PERFORMANCE_DEGRADED': `ðŸ“‰ Performance degraded: Response time ${details.response_time}ms`,
        'SECURITY_THREAT': `ðŸ”’ Security threat detected: ${details.threat_type}`
    };
    return templates[type] || `Alert: ${type}`;
}

