function generateHealthRecommendations(healthChecks, systemStatus) {
    const recommendations = [];

    if (systemStatus === 'CRITICAL') {
        recommendations.push({
            priority: 'URGENT',
            action: 'Immediate intervention required - multiple services failing',
            category: 'System Health'
        });
    }

    // Check for degraded services
    for (const [service, status] of Object.entries(healthChecks.services)) {
        if (status === 'CRITICAL') {
            recommendations.push({
                priority: 'HIGH',
                action: `Restart or investigate ${service} - service is critical`,
                category: 'Service Health'
            });
        } else if (status === 'DEGRADED') {
            recommendations.push({
                priority: 'MEDIUM',
                action: `Monitor ${service} - showing signs of degradation`,
                category: 'Service Health'
            });
        }
    }

    // Performance recommendations
    if (healthChecks.performance.status === 'DEGRADED') {
        recommendations.push({
            priority: 'MEDIUM',
            action: 'Optimize system performance - response times are high',
            category: 'Performance'
        });
    }

    return recommendations;
}

function generatePerformanceRecommendations(serviceMetrics, apiUsage) {
    const recommendations = [];

    // Check API usage
    for (const [api, usage] of Object.entries(apiUsage)) {
        if (usage.status === 'CRITICAL') {
            recommendations.push({
                priority: 'HIGH',
                action: `${api} API near limit (${usage.usage_percent}) - implement rate limiting`,
                category: 'API Usage'
            });
        } else if (usage.status === 'WARNING') {
            recommendations.push({
                priority: 'MEDIUM',
                action: `Monitor ${api} API usage (${usage.usage_percent})`,
                category: 'API Usage'
            });
        }
    }

    // Check service performance
    for (const [service, metrics] of Object.entries(serviceMetrics)) {
        if (parseFloat(metrics.success_rate) < 90) {
            recommendations.push({
                priority: 'HIGH',
                action: `Investigate ${service} - success rate only ${metrics.success_rate}`,
                category: 'Service Performance'
            });
        }
    }

    return recommendations;
}

