async function runServiceDiagnostics(serviceName) {
    console.log(`ðŸ” Running diagnostics for ${serviceName}...`);

    const diagnostics = {
        service: serviceName,
        timestamp: new Date().toISOString(),
        checks: {}
    };

    // Connectivity check
    diagnostics.checks.connectivity = await testServiceConnectivity(serviceName);

    // Performance check
    diagnostics.checks.performance = await testServicePerformance(serviceName);

    // Configuration check
    diagnostics.checks.configuration = await testServiceConfiguration(serviceName);

    // Dependencies check
    diagnostics.checks.dependencies = await testServiceDependencies(serviceName);

    // Generate diagnosis
    const issues = [];
    for (const [check, result] of Object.entries(diagnostics.checks)) {
        if (!result.success) {
            issues.push({
                check: check,
                issue: result.issue,
                recommendation: result.recommendation
            });
        }
    }

    diagnostics.issues = issues;
    diagnostics.status = issues.length === 0 ? 'HEALTHY' : 
                        issues.length <= 2 ? 'DEGRADED' : 'CRITICAL';

    return {
        success: true,
        diagnostics: diagnostics
    };
}

async function optimizeSystemPerformance() {
    console.log('âš¡ Optimizing system performance...');

    const optimizations = [];

    // Clear old logs and events
    if (dashboardData.recentEvents.length > 50) {
        dashboardData.recentEvents = dashboardData.recentEvents.slice(-50);
        optimizations.push('Cleared old events');
    }

    // Reset response time arrays if too large
    for (const service in serviceHealth) {
        if (serviceHealth[service].responseTime.length > 100) {
            serviceHealth[service].responseTime = serviceHealth[service].responseTime.slice(-100);
            optimizations.push(`Optimized ${service} metrics`);
        }
    }

    // Clear resolved alerts
    const beforeCount = dashboardData.activeAlerts.length;
    dashboardData.activeAlerts = dashboardData.activeAlerts.filter(alert => !alert.resolved);
    if (dashboardData.activeAlerts.length < beforeCount) {
        optimizations.push(`Cleared ${beforeCount - dashboardData.activeAlerts.length} resolved alerts`);
    }

    // Garbage collection hint (V8)
    if (global.gc) {
        global.gc();
        optimizations.push('Triggered garbage collection');
    }

    return {
        success: true,
        optimizations_performed: optimizations,
        timestamp: new Date().toISOString()
    };
}

