// NEW: Enhanced JSON parsing with recovery
function parseAndSanitizeRequestData(requestData) {
    if (!requestData) {
        throw new Error('No request data provided');
    }

    // If already an object, validate and return
    if (typeof requestData === 'object' && requestData !== null) {
        return requestData;
    }

    let cleanData = requestData;

    // Step 1: Handle string data
    if (typeof requestData === 'string') {
        // Remove common JSON formatting issues
        cleanData = requestData
            .replace(/```json\s*/g, '')
            .replace(/```\s*/g, '')
            .replace(/^\s*["']|["']\s*$/g, '') // Remove surrounding quotes
            .trim();

        // Try direct parse first
        try {
            return JSON.parse(cleanData);
        } catch (e) {
            console.warn('Direct parse failed, attempting cleanup...');
        }

        // Step 2: Advanced cleanup
        cleanData = cleanData
            .replace(/\\n/g, ' ')
            .replace(/\\t/g, ' ')
            .replace(/\\r/g, ' ')
            .replace(/([^\\])"/g, '$1\\"') // Escape unescaped quotes
            .replace(/,\s*}/g, '}') // Remove trailing commas
            .replace(/,\s*]/g, ']');

        // Try parse again
        try {
            return JSON.parse(cleanData);
        } catch (e) {
            console.warn('Advanced cleanup failed, extracting JSON...');
        }

        // Step 3: Extract JSON from mixed content
        const jsonMatch = cleanData.match(/\{[\s\S]*\}/);
        if (jsonMatch) {
            try {
                return JSON.parse(jsonMatch[0]);
            } catch (e) {
                console.warn('JSON extraction failed');
            }
        }
    }

    throw new Error('Unable to parse request data as JSON');
}

