    async function calculateConsensus(aiAnalyses, threshold) {
        const validAnalyses = [];
        let totalCost = 0;

        // Collect valid analyses
        Object.entries(aiAnalyses).forEach(([model, result]) => {
            if (result.status === 'success' && result.analysis) {
                validAnalyses.push({ model, ...result.analysis });
                totalCost += result.cost || 0;
            }
        });

        if (validAnalyses.length === 0) {
            return {
                overall_confidence: 0,
                consensus_decision: 'INSUFFICIENT_DATA',
                agreement_level: 0,
                cost_usd: totalCost,
                error: 'No valid AI analyses received'
            };
        }

        // Calculate consensus
        const decisions = validAnalyses.map(a => a.decision);
        const confidences = validAnalyses.map(a => a.confidence || 0);

        // Find most common decision
        const decisionCounts = {};
        decisions.forEach(d => {
            decisionCounts[d] = (decisionCounts[d] || 0) + 1;
        });

        const consensusDecision = Object.entries(decisionCounts)
            .sort(([,a], [,b]) => b - a)[0][0];

        // Calculate average confidence
        const avgConfidence = confidences.reduce((sum, c) => sum + c, 0) / confidences.length;

        // Calculate agreement level
        const agreementLevel = (decisionCounts[consensusDecision] / validAnalyses.length) * 100;

        return {
            overall_confidence: Math.round(avgConfidence),
            consensus_decision: consensusDecision,
            agreement_level: Math.round(agreementLevel),
            individual_analyses: validAnalyses,
            models_successful: validAnalyses.length,
            cost_usd: totalCost.toFixed(4),
            confidence_threshold_met: avgConfidence >= threshold
        };
    }

