// ===== PRODUCTION JSON SANITIZATION =====
function sanitizeJsonResponse(rawResponse) {
    if (!rawResponse || typeof rawResponse !== 'string') {
        throw new Error('Invalid response format');
    }

    let cleanResponse = rawResponse;

    try {
        // Step 1: Remove markdown code blocks
        cleanResponse = cleanResponse.replace(/```json\s*/g, '').replace(/```\s*/g, '');

        // Step 2: Extract JSON from mixed content
        const jsonMatch = cleanResponse.match(/\{[\s\S]*\}/);
        if (jsonMatch) {
            cleanResponse = jsonMatch[0];
        }

        // Step 3: Fix common JSON issues
        cleanResponse = cleanResponse
            .replace(/\\n/g, ' ')  // Replace escaped newlines
            .replace(/\\t/g, ' ')  // Replace escaped tabs
            .replace(/\\r/g, ' ')  // Replace escaped returns
            .replace(/([\"'])([^"']*?)(?<!\\)\1/g, (match, quote, content) => {
                // Fix unescaped quotes inside strings
                const escapedContent = content.replace(/(?<!\\)"/g, '\\"').replace(/(?<!\\)'/g, "\\'");
                return quote + escapedContent + quote;
            })
            .replace(/,\s*}/g, '}')  // Remove trailing commas
            .replace(/,\s*]/g, ']'); // Remove trailing commas in arrays

        // Step 4: Handle truncated JSON
        if (!cleanResponse.endsWith('}') && !cleanResponse.endsWith(']')) {
            const openBraces = (cleanResponse.match(/\{/g) || []).length;
            const closeBraces = (cleanResponse.match(/\}/g) || []).length;
            const missingBraces = openBraces - closeBraces;

            for (let i = 0; i < missingBraces; i++) {
                cleanResponse += '}';
            }
        }

        // Step 5: Validate JSON structure
        JSON.parse(cleanResponse);
        return cleanResponse;

    } catch (parseError) {
        console.log(`‚ö†Ô∏è JSON sanitization failed, attempting fallback extraction...`);
        return extractJsonFallback(rawResponse);
    }
}

function extractJsonFallback(rawResponse) {
    try {
        // Attempt to extract partial valid JSON
        const jsonRegex = /\{(?:[^{}]|\{[^{}]*\})*\}/g;
        const matches = rawResponse.match(jsonRegex);

        if (matches) {
            // Try each match until one parses successfully
            for (const match of matches) {
                try {
                    JSON.parse(match);
                    return match;
                } catch (e) {
                    continue;
                }
            }
        }

        // Last resort: create minimal valid response
        return JSON.stringify({
            decision: 'INSUFFICIENT_DATA',
            confidence: 0,
            reasoning: 'AI response could not be parsed',
            status: 'parse_error'
        });

    } catch (error) {
        throw new Error(`Complete JSON parsing failure: ${error.message}`);
    }
}

// ===== AI API CALLS WITH CIRCUIT BREAKER =====
async function callAIWithRetry(aiService, prompt, maxTokens = 1500) {
    const serviceState = circuitBreaker[aiService];

    // Check circuit breaker
    if (serviceState.isOpen) {
        const timeSinceLastFailure = Date.now() - serviceState.lastFailure;
        if (timeSinceLastFailure < CONFIG.CIRCUIT_BREAKER_RESET_TIME) {
            throw new Error(`Circuit breaker open for ${aiService}`);
        } else {
            // Reset circuit breaker
            serviceState.isOpen = false;
            serviceState.failures = 0;
        }
    }

    for (let attempt = 1; attempt <= CONFIG.MAX_RETRIES; attempt++) {
        try {
            const result = await makeAIRequest(aiService, prompt, maxTokens);

            // Reset failure count on success
            serviceState.failures = 0;
            return result;

        } catch (error) {
            console.log(`‚ö†Ô∏è ${aiService} attempt ${attempt}/${CONFIG.MAX_RETRIES} failed: ${error.message}`);

            if (attempt === CONFIG.MAX_RETRIES) {
                // Update circuit breaker on final failure
                serviceState.failures++;
                serviceState.lastFailure = Date.now();

                if (serviceState.failures >= CONFIG.CIRCUIT_BREAKER_THRESHOLD) {
                    serviceState.isOpen = true;
                    console.log(`üî¥ Circuit breaker opened for ${aiService}`);
                }

                throw error;
            }

            // Exponential backoff
            const delay = CONFIG.INITIAL_RETRY_DELAY * Math.pow(2, attempt - 1);
            await sleep(delay);
        }
    }
}

async function makeAIRequest(aiService, prompt, maxTokens) {
    const optimizedPrompt = optimizePromptLength(prompt);

    switch (aiService) {
