function parseGeminiResponse(data) {
    console.log('üîç Parsing Gemini response structure...');

    // Strategy 1: Standard Gemini response format
    if (data && data.candidates && Array.isArray(data.candidates) && data.candidates.length > 0) {
        const candidate = data.candidates[0];

        if (candidate.content && candidate.content.parts && Array.isArray(candidate.content.parts)) {
            const textPart = candidate.content.parts.find(part => part.text);
            if (textPart && textPart.text) {
                return cleanAndParseGeminiText(textPart.text);
            }
        }
    }

    // Strategy 2: Alternative response structure
    if (data && data.text) {
        return cleanAndParseGeminiText(data.text);
    }

    // Strategy 3: Direct content extraction
    if (data && typeof data === 'string') {
        return cleanAndParseGeminiText(data);
    }

    // Strategy 4: Search for any text content in the response
    const textContent = extractAnyTextFromResponse(data);
    if (textContent) {
        return cleanAndParseGeminiText(textContent);
    }

    console.warn('‚ö†Ô∏è No valid text content found in Gemini response:', JSON.stringify(data, null, 2));
    throw new Error('No valid response structure found in Gemini API response');
}

// NEW: Clean and parse Gemini text with JSON extraction
function cleanAndParseGeminiText(text) {
    if (!text || typeof text !== 'string') {
        throw new Error('Invalid text content from Gemini');
    }

    // Remove common Gemini formatting artifacts
    let cleanText = text
        .replace(/```json\s*/g, '')
        .replace(/```\s*/g, '')
        .replace(/^\s*["']|["']\s*$/g, '')
        .trim();

    // Try to parse as JSON first
    try {
        return JSON.parse(cleanText);
    } catch (e) {
        // Extract JSON from mixed content
        const jsonMatch = cleanText.match(/\{[\s\S]*\}/);
        if (jsonMatch) {
            try {
                return JSON.parse(jsonMatch[0]);
            } catch (e2) {
                console.warn('‚ö†Ô∏è JSON extraction failed, returning raw text');
            }
        }

        // Return structured fallback
        return {
            raw_response: cleanText,
            analysis: cleanText,
            confidence: 50,
            timestamp: new Date().toISOString(),
            source: 'gemini_fallback'
        };
    }
}

// NEW: Extract any text content from complex response objects
function extractAnyTextFromResponse(obj, depth = 0) {
    if (depth > 3) return null; // Prevent infinite recursion

    if (typeof obj === 'string') {
        return obj.length > 10 ? obj : null;
    }

    if (Array.isArray(obj)) {
        for (const item of obj) {
            const result = extractAnyTextFromResponse(item, depth + 1);
            if (result) return result;
        }
    }

    if (obj && typeof obj === 'object') {
        // Check common text fields
        const textFields = ['text', 'content', 'message', 'response', 'output', 'result'];
        for (const field of textFields) {
            if (obj[field]) {
                const result = extractAnyTextFromResponse(obj[field], depth + 1);
                if (result) return result;
            }
        }

        // Recursively search all properties
        for (const [key, value] of Object.entries(obj)) {
            const result = extractAnyTextFromResponse(value, depth + 1);
            if (result) return result;
        }
    }

    return null;
}

