function optimizePromptLength(prompt) {
    if (prompt.length <= CONFIG.MAX_RESPONSE_LENGTH) {
        return prompt;
    }

    // Truncate prompt while preserving structure
    const lines = prompt.split('\n');
    let optimizedPrompt = '';

    for (const line of lines) {
        if (optimizedPrompt.length + line.length > CONFIG.MAX_RESPONSE_LENGTH) {
            break;
        }
        optimizedPrompt += line + '\n';
    }

    return optimizedPrompt + '\n[Prompt truncated for optimization]';
}

