// MULTI-EXCHANGE PORTFOLIO AND RISK SERVICE - Unified Portfolio Management
const fetch = require('node-fetch');
const crypto = require('crypto');

// Monotonic Kraken nonce + small delay helper (enterprise-safe)
// Replace the getKrakenNonce function with this improved version:
let KRAKEN_LAST_NONCE = BigInt(Date.now()) * 1000000n; // Start with current time in microseconds

function getKrakenNonce() {
    const now = BigInt(Date.now()) * 1000000n; // Microseconds

    // Always increment, never use the same nonce twice
    if (now > KRAKEN_LAST_NONCE) {
        KRAKEN_LAST_NONCE = now;
    } else {
        KRAKEN_LAST_NONCE = KRAKEN_LAST_NONCE + 1n;
    }

    // Add random component to ensure uniqueness across restarts
    const randomComponent = BigInt(Math.floor(Math.random() * 1000));
    return (KRAKEN_LAST_NONCE + randomComponent).toString();
}
function delay(ms) { return new Promise(resolve => setTimeout(resolve, ms)); }

async function multiExchangePortfolioService(functionType, strategy, opportunity, mode, exchangeFilter) {
    console.log(`ðŸ›¡ï¸ Multi-Exchange Portfolio Service - Function: ${functionType}`);

    try {
        const tradingMode = mode || 'balanced';
        const optimizationStrategy = strategy || 'risk_parity';
        const exchanges = parseExchangeFilter(exchangeFilter || 'all');

        // Route to specific function
        switch (functionType.toLowerCase()) {
            case 'get_portfolio':
                return await getMultiExchangePortfolio(exchanges);
            case 'risk_analysis':
                return await performMultiExchangeRiskAnalysis(exchanges);
            case 'optimize_allocation':
                return await optimizeMultiExchangeAllocation(optimizationStrategy, tradingMode, exchanges);
            case 'position_sizing':
                return await calculateMultiExchangePositionSizing(opportunity, tradingMode, exchanges);
            case 'correlation_analysis':
                return await analyzeMultiExchangeCorrelations(exchanges);
            case 'stress_test':
                return await performMultiExchangeStressTest(exchanges);
            case 'complete_assessment':
                return await getCompleteMultiExchangeAssessment(optimizationStrategy, tradingMode, exchanges);
            case 'adaptive_kelly_sizing':
                return await adaptiveKellySizing(opportunity, tradingMode, exchanges);
            case 'portfolio_heat_monitor':
                return await portfolioHeatMonitor(exchanges);
            case 'adaptive_exit_levels':
                return await adaptiveExitLevels(opportunity, tradingMode);
            default:
                throw new Error(`Unknown function: ${functionType}`);
        }

        // ===== CORE FUNCTIONS =====

        async function getMultiExchangePortfolio(exchanges) {
    console.log('ðŸ’° Fetching multi-exchange portfolio data...');

    const results = {
        individual_portfolios: {},
        consolidated: {
            total_value_usd: 0,
            holdings: {},
            exchange_breakdown: {}
        }
    };

    let totalValueUSD = 0;
    const consolidatedHoldings = {};

    for (const exchange of exchanges) {
        try {
            console.log(`Fetching portfolio from ${exchange}...`);
            let portfolio = null;

            // REAL API CALLS - NO MOCK DATA
            switch (exchange) {
                case 'kraken':
                    if ($vars.KRAKEN_API_KEY && $vars.KRAKEN_PRIVATE_KEY) {
                        portfolio = await fetchKrakenPortfolio();
                    }
                    break;
                case 'binance':
                    if ($vars.BINANCE_API_KEY && $vars.BINANCE_SECRET_KEY) {
                        portfolio = await fetchBinancePortfolio();
                    }
                    break;
                case 'kucoin':
                    if ($vars.KUCOIN_API_KEY && $vars.KUCOIN_SECRET_KEY) {
                        portfolio = await fetchKucoinPortfolio();
                    }
                    break;
            }

            if (portfolio && portfolio.success) {
                results.individual_portfolios[exchange] = portfolio;

                // Consolidate holdings
                for (const [asset, data] of Object.entries(portfolio.holdings)) {
                    if (data.quantity > 0) {
                        if (!consolidatedHoldings[asset]) {
                            consolidatedHoldings[asset] = {
                                quantity: 0,
                                value_usd: 0,
                                exchanges: []
                            };
                        }
                        consolidatedHoldings[asset].quantity += data.quantity;
                        consolidatedHoldings[asset].value_usd += data.value_usd;
                        consolidatedHoldings[asset].exchanges.push(exchange);
                    }
                }

                totalValueUSD += portfolio.total_value_usd;
                results.consolidated.exchange_breakdown[exchange] = portfolio.total_value_usd;
            } else {
                console.warn(`âŒ Failed to fetch ${exchange} portfolio`);
                results.individual_portfolios[exchange] = { success: false, error: 'API unavailable' };
            }

        } catch (error) {
            console.error(`âŒ Error fetching ${exchange} portfolio:`, error.message);
            results.individual_portfolios[exchange] = { success: false, error: error.message };
        }
    }

    // Calculate percentages for consolidated holdings
    for (const [asset, data] of Object.entries(consolidatedHoldings)) {
        consolidatedHoldings[asset].percentage = totalValueUSD > 0 ? 
            ((data.value_usd / totalValueUSD) * 100).toFixed(2) : 0;
    }

    results.consolidated.total_value_usd = totalValueUSD;
    results.consolidated.holdings = consolidatedHoldings;

    return {
        success: true,
        timestamp: new Date().toISOString(),
        multi_exchange_portfolio: results
    };
}        
        async function fetchExchangePortfolio(exchange) {
            switch (exchange.toLowerCase()) {
                case 'kraken':
                    return await fetchKrakenPortfolio();
                case 'binance':
                    return await fetchBinancePortfolio();
                case 'kucoin':
                    return await fetchKucoinPortfolio();
                default:
                    throw new Error(`Unsupported exchange: ${exchange}`);
            }
        }

        async function fetchKrakenPortfolio() {
            const apiKey = $vars.KRAKEN_API_KEY;
            const privateKey = $vars.KRAKEN_PRIVATE_KEY;

            if (!apiKey || !privateKey) {
                throw new Error('Kraken API credentials not configured');
            }

            // SURGICAL FIX: Strictly monotonic nonce to prevent "Invalid nonce"
            const urlPath = '/0/private/Balance';
            let attempt = 0;
            while (true) {
                attempt++;
                const nonce = getKrakenNonce();
                const requestData = { nonce: nonce };
                const postData = Object.keys(requestData)
                    .map(key => `${key}=${encodeURIComponent(requestData[key])}`)
                    .join('&');
                const encoded = nonce + postData;
                const sha256Hash = crypto.createHash('sha256').update(encoded).digest();
                const message = Buffer.concat([Buffer.from(urlPath, 'utf8'), sha256Hash]);
                const secretBuffer = Buffer.from(privateKey, 'base64');
                const signature = crypto.createHmac('sha512', secretBuffer).update(message).digest('base64');

                const response = await fetch(`https://api.kraken.com${urlPath}`, {
                    method: 'POST',
                    headers: {
                        'API-Key': apiKey,
                        'API-Sign': signature,
                        'Content-Type': 'application/x-www-form-urlencoded'
                    },
                    body: postData
                });

                if (!response.ok) {
                    throw new Error(`Kraken API error: ${response.status}`);
                }

                const data = await response.json();
                if (data.error && data.error.length > 0) {
                    const combined = data.error.join(', ');
                    if (combined.includes('EAPI:Invalid nonce') && attempt < 3) {
                        console.warn('Kraken Invalid nonce, retrying with new nonce...');
                        await delay(15 * attempt);
                        continue;
                    }
                    throw new Error(`Kraken API errors: ${combined}`);
                }

                return await processKrakenBalances(data.result);
            }
        }

        async function fetchBinancePortfolio() {
            const apiKey = $vars.BINANCE_API_KEY;
            const secretKey = $vars.BINANCE_SECRET_KEY;

            if (!apiKey || !secretKey) {
                throw new Error('Binance API credentials not configured');
            }

            const timestamp = Date.now();
            const queryString = `timestamp=${timestamp}`;
            const signature = crypto.createHmac('sha256', secretKey).update(queryString).digest('hex');

            const response = await fetch(`https://api.binance.com/api/v3/account?${queryString}&signature=${signature}`, {
                method: 'GET',
                headers: {
                    'X-MBX-APIKEY': apiKey
                }
            });

            if (!response.ok) {
                throw new Error(`Binance API error: ${response.status}`);
            }

            const data = await response.json();
            return await processBinanceBalances(data.balances);
        }

        async function fetchKucoinPortfolio() {
            const apiKey = $vars.KUCOIN_API_KEY;
            const secretKey = $vars.KUCOIN_SECRET_KEY;
            const passphrase = $vars.KUCOIN_PASSPHRASE;

            if (!apiKey || !secretKey || !passphrase) {
                throw new Error('KuCoin API credentials not configured');
            }

            const timestamp = Date.now();
            const method = 'GET';
            const endpoint = '/api/v1/accounts';
            const str_to_sign = timestamp + method + endpoint;

            const signature = crypto.createHmac('sha256', secretKey).update(str_to_sign).digest('base64');
            const passphrase_hmac = crypto.createHmac('sha256', secretKey).update(passphrase).digest('base64');

            const response = await fetch(`https://api.kucoin.com${endpoint}`, {
                method: 'GET',
                headers: {
                    'KC-API-KEY': apiKey,
                    'KC-API-SIGN': signature,
                    'KC-API-TIMESTAMP': timestamp.toString(),
                    'KC-API-PASSPHRASE': passphrase_hmac,
                    'KC-API-KEY-VERSION': '2'
                }
            });

            if (!response.ok) {
                throw new Error(`KuCoin API error: ${response.status}`);
            }

            const data = await response.json();

            if (data.code !== '200000') {
                throw new Error(`KuCoin API error: ${data.msg}`);
            }

            return await processKucoinBalances(data.data);
        }

