        async function processKucoinBalances(accounts) {
            const holdings = {};
            let totalValue = 0;

            const currencies = accounts.filter(acc => parseFloat(acc.balance) > 0.001).map(acc => acc.currency);
            const priceData = await getCurrentPrices(currencies, 'kucoin');

            for (const account of accounts) {
                const numBalance = parseFloat(account.balance);
                if (numBalance > 0.001) {
                    const currency = account.currency;
                    const price = priceData[currency];

                    // Only include assets with valid prices
