        function normalizeKrakenCurrency(currency) {
            // Remove Kraken's X/Z prefixes
            if (currency.startsWith('X') || currency.startsWith('Z')) {
                return currency.substring(1);
            }
            return currency;
        }

        async function getCurrentPrices(currencies, exchangeSource) {
            const prices = {};

            for (const currency of currencies) {
                // Handle stablecoins
                if (['USD', 'USDT', 'USDC', 'BUSD', 'DAI', 'TUSD', 'USDP', 'GUSD'].includes(currency.toUpperCase())) {
                    prices[currency] = 1;
                    continue;
                }

                let price = null;

                try {
                    // Try exchange-specific pricing first for accuracy
                    if (exchangeSource === 'binance') {
                        // Try multiple quote currencies
                        const quoteCurrencies = ['USDT', 'BUSD', 'USDC'];
                        for (const quote of quoteCurrencies) {
                            try {
                                const response = await fetch(`https://api.binance.com/api/v3/ticker/price?symbol=${currency}${quote}`);
                                if (response.ok) {
                                    const data = await response.json();
                                    if (data.price) {
                                        price = parseFloat(data.price);
                                        break;
                                    }
                                }
                            } catch (e) {
                                // Try next quote currency
                            }
                        }

                        // Special handling for exchange notations like 1000PEPPER
                        if (price === null && currency.match(/^\d+[A-Z]/)) {
                            const baseSymbol = currency.replace(/^\d+/, '');
                            const multiplier = parseInt(currency.match(/^\d+/)[0]);

                            for (const quote of quoteCurrencies) {
                                try {
                                    const response = await fetch(`https://api.binance.com/api/v3/ticker/price?symbol=${baseSymbol}${quote}`);
                                    if (response.ok) {
                                        const data = await response.json();
                                        if (data.price) {
                                            price = parseFloat(data.price) * multiplier;
                                            break;
                                        }
                                    }
                                } catch (e) {
                                    // Try next
                                }
                            }
                        }

                    } else if (exchangeSource === 'kucoin') {
                        // KuCoin API
                        const response = await fetch(`https://api.kucoin.com/api/v1/market/orderbook/level1?symbol=${currency}-USDT`);
                        if (response.ok) {
                            const data = await response.json();
                            if (data.data && data.data.price) {
                                price = parseFloat(data.data.price);
                            }
                        }

                        // Try USDC if USDT fails
                        if (price === null) {
                            const response2 = await fetch(`https://api.kucoin.com/api/v1/market/orderbook/level1?symbol=${currency}-USDC`);
                            if (response2.ok) {
                                const data = await response2.json();
                                if (data.data && data.data.price) {
                                    price = parseFloat(data.data.price);
                                }
                            }
                        }

                    } else if (exchangeSource === 'kraken') {
                        // Kraken API - handle their special pair notation
                        const pairMappings = {
                            'BTC': 'XXBTZUSD',
                            'ETH': 'XETHZUSD',
                            'XBT': 'XXBTZUSD',
                            'ETHW': 'ETHWUSD'
                        };

                        const pair = pairMappings[currency] || `${currency}USD`;
                        const response = await fetch(`https://api.kraken.com/0/public/Ticker?pair=${pair}`);

                        if (response.ok) {
                            const data = await response.json();
                            if (data.result) {
                                const ticker = Object.values(data.result)[0];
                                if (ticker && ticker.c) {
                                    price = parseFloat(ticker.c[0]);
                                }
                            }
                        }
                    }

                    // Fallback to CoinGecko if exchange price not available
                    if (price === null) {
                        price = await getCoinGeckoPrice(currency);
                    }

                } catch (error) {
                    console.log(`Price fetch failed for ${currency} from ${exchangeSource}: ${error.message}`);
                }

                // Store the price or null (never default to 1 for unknown assets)
                prices[currency] = price;
            }

            return prices;
        }

        async function getCoinGeckoPrice(symbol) {
            try {
                // First try to search for the coin ID dynamically
                const searchResponse = await fetch(`https://api.coingecko.com/api/v3/search?query=${symbol}`);
                if (searchResponse.ok) {
                    const searchData = await searchResponse.json();
                    if (searchData.coins && searchData.coins.length > 0) {
                        // Find exact symbol match
                        const exactMatch = searchData.coins.find(coin => 
                            coin.symbol.toUpperCase() === symbol.toUpperCase()
                        );

                        if (exactMatch) {
                            // Get price using the found coin ID
                            const priceResponse = await fetch(`https://api.coingecko.com/api/v3/simple/price?ids=${exactMatch.id}&vs_currencies=usd`);
                            if (priceResponse.ok) {
                                const priceData = await priceResponse.json();
                                if (priceData[exactMatch.id] && priceData[exactMatch.id].usd) {
                                    return priceData[exactMatch.id].usd;
                                }
                            }
                        }
                    }
                }
            } catch (error) {
                console.log(`CoinGecko price fetch failed for ${symbol}: ${error.message}`);
            }

            return null;
        }

        function consolidatePortfolios(exchangePortfolios) {
            const consolidated = {
                holdings: {},
                total_value_usd: 0,
                significant_holdings: {},
                number_of_positions: 0,
                largest_position_percentage: 0,
                diversification_score: 0,
                exchange_distribution: {}
            };

            let totalValue = 0;

            // Consolidate holdings across exchanges
            for (const [exchange, portfolio] of Object.entries(exchangePortfolios)) {
                if (portfolio.error) continue;

                totalValue += portfolio.total_value_usd;
                consolidated.exchange_distribution[exchange] = {
                    value_usd: portfolio.total_value_usd,
                    percentage: 0
                };

                for (const [currency, holding] of Object.entries(portfolio.holdings)) {
                    if (!consolidated.holdings[currency]) {
                        consolidated.holdings[currency] = {
                            total_balance: 0,
                            total_value_usd: 0,
                            price_usd: holding.price_usd,
                            exchanges: {},
                            percentage: 0
                        };
                    }

                    consolidated.holdings[currency].total_balance += holding.balance;
                    consolidated.holdings[currency].total_value_usd += holding.value_usd;
                    consolidated.holdings[currency].exchanges[exchange] = {
                        balance: holding.balance,
                        value_usd: holding.value_usd,
                        percentage: holding.percentage
                    };
                }
            }

            consolidated.total_value_usd = totalValue;

            // Calculate exchange distribution percentages
            for (const exchange of Object.values(consolidated.exchange_distribution)) {
                exchange.percentage = totalValue > 0 ? (exchange.value_usd / totalValue) * 100 : 0;
            }

            // Calculate asset percentages and metrics
            let largestPosition = 0;
            for (const holding of Object.values(consolidated.holdings)) {
                holding.percentage = totalValue > 0 ? (holding.total_value_usd / totalValue) * 100 : 0;
                if (holding.percentage >= 5) {
                    consolidated.significant_holdings[Object.keys(consolidated.holdings).find(k => consolidated.holdings[k] === holding)] = holding;
                }
                if (holding.total_value_usd > largestPosition) {
                    largestPosition = holding.total_value_usd;
                }
                consolidated.number_of_positions++;
            }

            consolidated.largest_position_percentage = totalValue > 0 ? (largestPosition / totalValue) * 100 : 0;
            consolidated.diversification_score = calculateDiversificationScore(consolidated.holdings);

            return consolidated;
        }

        function calculateDiversificationScore(holdings) {
            const positions = Object.values(holdings);
            if (positions.length <= 1) return 0;

            const hhi = positions.reduce((sum, holding) => {
                const weight = holding.percentage / 100;
                return sum + (weight * weight);
            }, 0);

            return Math.max(0, Math.min(100, (1 - hhi) * 100));
        }

        function findCrossExchangeOpportunities(exchangePortfolios) {
            const opportunities = [];
            const exchangeNames = Object.keys(exchangePortfolios).filter(ex => !exchangePortfolios[ex].error);

            // Find assets available on multiple exchanges
            const assetExchangeMap = {};

            for (const [exchange, portfolio] of Object.entries(exchangePortfolios)) {
                if (portfolio.error) continue;

                for (const [asset, holding] of Object.entries(portfolio.holdings)) {
                    if (!assetExchangeMap[asset]) {
                        assetExchangeMap[asset] = {};
                    }
                    assetExchangeMap[asset][exchange] = holding.price_usd;
                }
            }

            // Find arbitrage opportunities
            for (const [asset, exchanges] of Object.entries(assetExchangeMap)) {
                const exchangeList = Object.keys(exchanges);
                if (exchangeList.length < 2) continue;

                const prices = Object.values(exchanges);
                const minPrice = Math.min(...prices);
                const maxPrice = Math.max(...prices);
                const priceDiff = ((maxPrice - minPrice) / minPrice) * 100;

                if (priceDiff > 0.5) { // 0.5% threshold
                    const minExchange = exchangeList.find(ex => exchanges[ex] === minPrice);
                    const maxExchange = exchangeList.find(ex => exchanges[ex] === maxPrice);

                    opportunities.push({
                        asset: asset,
                        arbitrage_opportunity: priceDiff.toFixed(2) + '%',
                        buy_exchange: minExchange,
                        sell_exchange: maxExchange,
                        buy_price: minPrice,
                        sell_price: maxPrice,
                        potential_profit_pct: priceDiff.toFixed(2)
                    });
                }
            }

            return opportunities.sort((a, b) => parseFloat(b.potential_profit_pct) - parseFloat(a.potential_profit_pct));
        }

        function assessExchangeHealth(exchangePortfolios) {
            const health = {};

            for (const [exchange, portfolio] of Object.entries(exchangePortfolios)) {
                if (portfolio.error) {
                    health[exchange] = {
                        status: 'ERROR',
                        error: portfolio.error,
                        last_successful_fetch: null
                    };
                } else {
                    health[exchange] = {
                        status: 'HEALTHY',
                        total_value: portfolio.total_value_usd,
                        asset_count: Object.keys(portfolio.holdings).length,
                        last_updated: portfolio.last_updated
                    };
                }
            }

            return health;
        }

        async function performMultiExchangeRiskAnalysis(exchanges) {
    console.log('ðŸ“Š Performing multi-exchange risk analysis...');

    try {
        const portfolioData = await getMultiExchangePortfolio(exchanges);
        if (!portfolioData.success) {
            throw new Error('Failed to get portfolio data for risk analysis');
        }

        const consolidated = portfolioData.multi_exchange_portfolio.consolidated;

        // SAFE DATA ACCESS with extensive validation
