        function calculateExchangeConcentrationRisk(exchangeDistribution) {
            const distributions = exchangeDistribution ? Object.values(exchangeDistribution) : [];
            const maxConcentration = Math.max(...distributions.map(d => d.percentage || 0));

            return {
                max_exchange_concentration: maxConcentration.toFixed(1) + '%',
                risk_level: maxConcentration > 70 ? 'HIGH' : maxConcentration > 50 ? 'MEDIUM' : 'LOW',
                diversification_benefit: maxConcentration < 60 ? 'GOOD' : 'LIMITED'
            };
        }

        function calculateAssetConcentrationRisk(holdings) {
            const positions = Object.values(holdings || {});
            const maxPosition = positions.length > 0 ? Math.max(...positions.map(p => p.percentage || 0)) : 0;

            return {
                largest_position: maxPosition.toFixed(1) + '%',
                risk_level: maxPosition > 50 ? 'EXTREME' : maxPosition > 25 ? 'HIGH' : maxPosition > 15 ? 'MEDIUM' : 'LOW'
            };
        }

        async function calculateCrossExchangeCorrelationRisk(exchangePortfolios) {
    console.log('ðŸ” Calculating cross-exchange correlation risk...');
    if (!exchangePortfolios || typeof exchangePortfolios !== 'object') {
        return {
        exchange_count: 0,
        correlation_risk: 'UNKNOWN',
        diversification_benefit: 'UNKNOWN',
        message: 'Insufficient data for correlation analysis'
        };
    }
    // BULLETPROOF NULL/UNDEFINED CHECKS
    if (!exchangePortfolios) {
        console.warn('âš ï¸ exchangePortfolios is null or undefined');
        return {
            exchange_count: 0,
            correlation_risk: 'UNKNOWN', 
            diversification_benefit: 'UNKNOWN',
            message: 'No exchange portfolio data provided'
        };
    }

    if (typeof exchangePortfolios !== 'object') {
        console.warn('âš ï¸ exchangePortfolios is not an object:', typeof exchangePortfolios);
        return {
            exchange_count: 0,
            correlation_risk: 'UNKNOWN', 
            diversification_benefit: 'UNKNOWN',
            message: 'Invalid exchange portfolio data type'
        };
    }

    // SAFE Object.keys() with try/catch
    let activeExchanges = [];
    try {
        const allKeys = Object.keys(exchangePortfolios);
        console.log('ðŸ” Found exchange keys:', allKeys);

        activeExchanges = allKeys.filter(ex => {
            const portfolio = exchangePortfolios[ex];
            return portfolio && !portfolio.error && portfolio.success !== false;
        });

        console.log('âœ… Active exchanges after filtering:', activeExchanges);

    } catch (error) {
        console.error('âŒ Error in Object.keys() operation:', error);
        return {
            exchange_count: 0,
            correlation_risk: 'ERROR',
            diversification_benefit: 'UNKNOWN',
            error: `Object.keys failed: ${error.message}`,
            message: 'Cannot analyze correlation due to data structure error'
        };
    }

    // SAFE CALCULATION
    const exchangeCount = activeExchanges.length;

    let correlationRisk = 'UNKNOWN';
    let diversificationBenefit = 'UNKNOWN';

    if (exchangeCount === 0) {
        correlationRisk = 'CRITICAL';
        diversificationBenefit = 'NONE';
    } else if (exchangeCount === 1) {
        correlationRisk = 'HIGH';
        diversificationBenefit = 'NONE';
    } else if (exchangeCount === 2) {
        correlationRisk = 'MEDIUM';
        diversificationBenefit = 'MEDIUM';
    } else {
        correlationRisk = 'LOW';
        diversificationBenefit = 'HIGH';
    }

    return {
        exchange_count: exchangeCount,
        correlation_risk: correlationRisk,
        diversification_benefit: diversificationBenefit,
        active_exchanges: activeExchanges,
        message: `Correlation analysis completed for ${exchangeCount} exchanges`
    };
}

        function assessOperationalRisk(exchangeHealth) {
            const totalExchanges = Object.keys(exchangeHealth).length;
            const healthyExchanges = Object.values(exchangeHealth).filter(h => h.status === 'HEALTHY').length;
            const healthRatio = healthyExchanges / totalExchanges;

            return {
                healthy_exchanges: healthyExchanges,
                total_exchanges: totalExchanges,
                health_ratio: (healthRatio * 100).toFixed(1) + '%',
                operational_risk: healthRatio > 0.8 ? 'LOW' : healthRatio > 0.6 ? 'MEDIUM' : 'HIGH'
            };
        }

        function assessMultiExchangeLiquidityRisk(holdings) {
            // Enhanced liquidity assessment for multi-exchange setup
            let totalLiquidityScore = 0;
            let totalValue = 0;

            for (const [asset, holding] of Object.entries(holdings)) {
                const assetLiquidity = getAssetLiquidity(asset);
                const exchangeCount = Object.keys(holding.exchanges).length;
                const exchangeBonus = Math.min(exchangeCount * 10, 30); // Up to 30% bonus for multiple exchanges

                const adjustedLiquidity = Math.min(assetLiquidity + exchangeBonus, 100);
                totalLiquidityScore += adjustedLiquidity * holding.total_value_usd;
                totalValue += holding.total_value_usd;
            }

            const avgLiquidityScore = totalValue > 0 ? totalLiquidityScore / totalValue : 0;

            return {
                liquidity_score: Math.round(avgLiquidityScore),
                risk_level: avgLiquidityScore > 80 ? 'LOW' : avgLiquidityScore > 60 ? 'MEDIUM' : 'HIGH',
                multi_exchange_benefit: 'Improved liquidity through exchange diversification'
            };
        }

        function getAssetLiquidity(asset) {
            const liquidityScores = {
