        function assessCounterpartyRisk(exchanges) {
            const riskProfiles = {
                'kraken': { risk_score: 20, rating: 'LOW' },
                'binance': { risk_score: 30, rating: 'LOW-MEDIUM' },
                'kucoin': { risk_score: 40, rating: 'MEDIUM' }
            };

            const totalRisk = exchanges.reduce((sum, ex) => sum + (riskProfiles[ex]?.risk_score || 50), 0);
            const avgRisk = totalRisk / exchanges.length;

            return {
                average_risk_score: Math.round(avgRisk),
                overall_rating: avgRisk < 25 ? 'LOW' : avgRisk < 40 ? 'MEDIUM' : 'HIGH',
                exchange_diversification_benefit: exchanges.length > 1 ? 'GOOD' : 'NONE'
            };
        }

        function calculateMultiExchangeVaR(consolidated) {
            const totalValue = consolidated.total_value_usd;

            if (totalValue === 0) {
                return {
                    '95%_confidence': { '1_day': 0, '7_day': 0, '30_day': 0 },
                    '99%_confidence': { '1_day': 0, '7_day': 0, '30_day': 0 }
                };
            }

            // Enhanced VaR calculation considering multi-exchange setup
            let portfolioVolatility = 0;
            for (const [asset, holding] of Object.entries(consolidated.holdings)) {
                const weight = holding.percentage / 100;
                const assetVolatility = getAssetVolatility(asset);
                const exchangeDiversificationFactor = Object.keys(holding.exchanges).length > 1 ? 0.9 : 1.0;
                portfolioVolatility += weight * assetVolatility * exchangeDiversificationFactor;
            }

            const var95_1d = totalValue * portfolioVolatility * 1.645 / Math.sqrt(365);
            const var99_1d = totalValue * portfolioVolatility * 2.326 / Math.sqrt(365);

            return {
                portfolio_volatility: (portfolioVolatility * 100).toFixed(2) + '%',
                '95%_confidence': {
                    '1_day': var95_1d.toFixed(2),
                    '7_day': (var95_1d * Math.sqrt(7)).toFixed(2),
                    '30_day': (var95_1d * Math.sqrt(30)).toFixed(2)
                },
                '99%_confidence': {
                    '1_day': var99_1d.toFixed(2),
                    '7_day': (var99_1d * Math.sqrt(7)).toFixed(2),
                    '30_day': (var99_1d * Math.sqrt(30)).toFixed(2)
                },
                multi_exchange_benefit: 'Reduced VaR through exchange diversification'
            };
        }

