        async function performMultiExchangeStressTest(exchanges) {
            const portfolioData = await getMultiExchangePortfolio(exchanges);
            const consolidated = portfolioData.multi_exchange_portfolio.consolidated;
            const totalValue = consolidated.total_value_usd;

            const stressScenarios = {
                crypto_winter: {
                    name: 'Crypto Winter (Multi-Exchange)',
                    shocks: { 'BTC': -0.70, 'ETH': -0.75, 'ADA': -0.85, 'SOL': -0.90, 'USD': 0.00 },
                    exchange_failure_probability: 0.1
                },
                exchange_hack: {
                    name: 'Major Exchange Security Breach',
                    shocks: { 'BTC': -0.15, 'ETH': -0.20, 'ADA': -0.25, 'SOL': -0.30, 'USD': 0.00 },
                    exchange_failure_probability: 0.33
                },
                regulatory_crackdown: {
                    name: 'Global Regulatory Restrictions',
                    shocks: { 'BTC': -0.40, 'ETH': -0.50, 'ADA': -0.60, 'SOL': -0.65, 'USD': 0.00 },
                    exchange_failure_probability: 0.2
                },
                liquidity_crisis: {
                    name: 'Multi-Exchange Liquidity Crisis',
                    shocks: { 'BTC': -0.25, 'ETH': -0.30, 'ADA': -0.60, 'SOL': -0.70, 'USD': 0.00 },
                    exchange_failure_probability: 0.15
                }
            };

            const stressResults = {};

            for (const [scenarioKey, scenario] of Object.entries(stressScenarios)) {
                let portfolioLoss = 0;
                let exchangeFailureLoss = 0;

                // Calculate market impact
                for (const [asset, holding] of Object.entries(consolidated.holdings)) {
                    const shock = scenario.shocks[asset] || 0;
                    const assetLoss = holding.total_value_usd * shock;
                    portfolioLoss += assetLoss;
                }

                // Calculate exchange failure impact
                const worstExchangeValue = Math.max(...Object.values(consolidated.exchange_breakdown).map(d => d || 0));
                exchangeFailureLoss = worstExchangeValue * scenario.exchange_failure_probability;

                const totalLoss = portfolioLoss + exchangeFailureLoss;
                const lossPercentage = (totalLoss / totalValue) * 100;

                stressResults[scenarioKey] = {
                    scenario_name: scenario.name,
                    market_loss_usd: portfolioLoss.toFixed(2),
                    exchange_risk_loss_usd: exchangeFailureLoss.toFixed(2),
                    total_loss_usd: totalLoss.toFixed(2),
                    total_loss_percentage: lossPercentage.toFixed(2) + '%',
                    remaining_value_usd: (totalValue + totalLoss).toFixed(2),
                    severity: Math.abs(lossPercentage) > 50 ? 'EXTREME' :
                             Math.abs(lossPercentage) > 30 ? 'HIGH' :
                             Math.abs(lossPercentage) > 15 ? 'MEDIUM' : 'LOW'
                };
            }

            return stressResults;
        }

