        async function analyzeMultiExchangeCorrelations(exchanges) {
            console.log('ðŸ“Š Analyzing multi-exchange correlations...');

            const portfolioData = await getMultiExchangePortfolio(exchanges);
            const holdings = portfolioData.multi_exchange_portfolio.consolidated.holdings;

            const correlationMatrix = {};
            const assets = Object.keys(holdings);

            // Generate correlation matrix (simplified)
            assets.forEach(asset1 => {
                correlationMatrix[asset1] = {};
                assets.forEach(asset2 => {
                    if (asset1 === asset2) {
                        correlationMatrix[asset1][asset2] = 1.00;
                    } else {
                        correlationMatrix[asset1][asset2] = calculateAssetCorrelation(asset1, asset2);
                    }
                });
            });

            return {
                success: true,
                timestamp: new Date().toISOString(),
                multi_exchange_correlations: {
                    correlation_matrix: correlationMatrix,
                    highly_correlated_pairs: findHighlyCorrelatedPairs(correlationMatrix),
                    diversification_opportunities: findDiversificationOpportunities(correlationMatrix),
                    exchange_correlation: calculateExchangeCorrelation(portfolioData.multi_exchange_portfolio.individual_portfolios)
                }
            };
        }

        function calculateAssetCorrelation(asset1, asset2) {
            // Simplified correlation calculation
            const correlations = {
                'BTC': { 'ETH': 0.85, 'ADA': 0.70, 'SOL': 0.75, 'USD': -0.10 },
                'ETH': { 'BTC': 0.85, 'ADA': 0.80, 'SOL': 0.82, 'USD': -0.08 },
                'ADA': { 'BTC': 0.70, 'ETH': 0.80, 'SOL': 0.78, 'USD': -0.05 },
                'SOL': { 'BTC': 0.75, 'ETH': 0.82, 'ADA': 0.78, 'USD': -0.06 }
            };

            return correlations[asset1]?.[asset2] || 0.50;
        }

        function findHighlyCorrelatedPairs(correlationMatrix) {
            const pairs = [];
            const processed = new Set();

            Object.entries(correlationMatrix).forEach(([asset1, correlations]) => {
                Object.entries(correlations).forEach(([asset2, correlation]) => {
                    if (asset1 !== asset2 && correlation > 0.8 && !processed.has(`${asset2}-${asset1}`)) {
                        pairs.push({
                            pair: `${asset1}-${asset2}`,
                            correlation: correlation
                        });
                        processed.add(`${asset1}-${asset2}`);
                    }
                });
            });

            return pairs.sort((a, b) => b.correlation - a.correlation);
        }

        function findDiversificationOpportunities(correlationMatrix) {
            const opportunities = [];
            const processed = new Set();

            Object.entries(correlationMatrix).forEach(([asset1, correlations]) => {
                Object.entries(correlations).forEach(([asset2, correlation]) => {
                    if (asset1 !== asset2 && correlation < 0.3 && !processed.has(`${asset2}-${asset1}`)) {
                        opportunities.push({
                            pair: `${asset1}-${asset2}`,
                            correlation: correlation,
                            benefit: 'Low correlation provides diversification'
                        });
                        processed.add(`${asset1}-${asset2}`);
                    }
                });
            });

            return opportunities;
        }

        function calculateExchangeCorrelation(exchangePortfolios) {
            // Calculate how correlated the holdings are across exchanges
            const exchangeList = Object.keys(exchangePortfolios || {}).filter(ex => !exchangePortfolios[ex].error);

            if (exchangeList.length < 2) {
                return { message: 'Insufficient exchanges for correlation analysis' };
            }

            return {
                exchange_overlap: 'Medium',
                recommendation: 'Consider diversifying holdings across exchanges'
            };
        }

