                riskAnalysis.liquidity_risk.risk_level
            ];

            const riskScores = {
                'LOW': 1, 'MEDIUM': 2, 'HIGH': 3, 'EXTREME': 4
            };

            const avgScore = riskFactors.reduce((sum, factor) => sum + (riskScores[factor] || 2), 0) / riskFactors.length;

            if (avgScore <= 1.5) return 'LOW';
            if (avgScore <= 2.5) return 'MEDIUM';
            if (avgScore <= 3.5) return 'HIGH';
            return 'EXTREME';
        }

