async function adaptiveKellySizing(opportunity, tradingMode, exchanges) {
    try {
        console.log('ðŸŽ¯ Calculating adaptive Kelly position sizing...');

        // Get portfolio data for Kelly calculation
        const portfolioData = await multiExchangePortfolioService('get_portfolio', null, null, null, exchanges);
        if (!portfolioData.success) {
            throw new Error('Failed to get portfolio data for Kelly sizing');
        }

        const totalValue = portfolioData.multi_exchange_portfolio.consolidated.total_value_usd;

        // Simplified Kelly calculation - enhance with real trade history
        const winRate = 0.55;  // 50-60% base win rate
        const avgWin = 0.02 + Math.random() * 0.01; // 2-3% average win
        const avgLoss = 0.01 + Math.random() * 0.005; // 1-1.5% average loss
        const winLossRatio = avgWin / avgLoss;

        // Kelly: f = (bp - q) / b
        const kellyFraction = (winLossRatio * winRate - (1 - winRate)) / winLossRatio;

        // Market regime adjustment
        const regimeMultiplier = {
            'conservative': 0.6,
            'balanced': 1.0,
            'aggressive': 1.3
        }[tradingMode] || 1.0;

        const adjustedKelly = Math.max(0.01, Math.min(0.25, kellyFraction * regimeMultiplier));
        const positionSize = totalValue * adjustedKelly;

        return {
            success: true,
            timestamp: new Date().toISOString(),
            kelly_sizing: {
                kelly_fraction: kellyFraction,
                adjusted_kelly: adjustedKelly,
                regime_multiplier: regimeMultiplier,
                position_size_usd: positionSize,
                win_rate: winRate,
                win_loss_ratio: winLossRatio,
                confidence: winRate > 0.55 ? 'HIGH' : 'MEDIUM',
                recommendation: adjustedKelly > 0.15 ? 'AGGRESSIVE' : adjustedKelly > 0.08 ? 'MODERATE' : 'CONSERVATIVE'
            }
        };
    } catch (error) {
        return {
            success: false,
            error: error.message,
            timestamp: new Date().toISOString()
        };
    }
}

