        const holdings = Object.values(consolidated.holdings || {});
        const maxPosition = holdings.length > 0 ? 
            holdings.reduce((max, holding) => Math.max(max, holding.percentage || 0), 0) : 0;

        const concentrationRisk = maxPosition / 100;
        const estimatedDrawdown = Math.min(concentrationRisk * 0.3, 0.05);
        const unrealizedPnL = -estimatedDrawdown * totalValue;

        // Risk-based action determination
        const heatLevel = estimatedDrawdown > maxDrawdownPercent * 0.8 ? 'HIGH' : 
                         estimatedDrawdown > maxDrawdownPercent * 0.5 ? 'MEDIUM' : 'LOW';

        let actionRequired = 'MONITOR';
        let positionReductionPct = 0;

        if (estimatedDrawdown > maxDrawdownPercent) {
            actionRequired = 'REDUCE_POSITIONS';
            positionReductionPct = Math.min(0.5, (estimatedDrawdown - maxDrawdownPercent) * 2);
        } else if (concentrationRisk > 0.4) {
            actionRequired = 'DIVERSIFY';
        }

        // Count successful exchanges
        const successfulExchanges = Object.keys(portfolioData.multi_exchange_portfolio.individual_portfolios || {})
            .filter(ex => {
                const portfolio = portfolioData.multi_exchange_portfolio.individual_portfolios[ex];
                return portfolio && portfolio.success;
