        return {
            success: true,
            timestamp: new Date().toISOString(),
            portfolio_heat: {
                current_drawdown: estimatedDrawdown,
                max_allowable_drawdown: maxDrawdownPercent,
                heat_level: heatLevel,
                concentration_risk: concentrationRisk,
                total_value: totalValue,
                unrealized_pnl: unrealizedPnL,
                action_required: actionRequired,
                position_reduction_pct: positionReductionPct,
                largest_position_pct: maxPosition,
                number_of_positions: holdings.length,
                exchanges_analyzed: parsedExchanges,
                successful_exchanges: successfulExchanges
            }
        };

    } catch (error) {
