async function adaptiveExitLevels(opportunity, tradingMode) {
    try {
        console.log('ðŸŽ¯ Calculating adaptive exit levels...');

        if (!opportunity || typeof opportunity === 'string') {
            return {
                success: false,
                error: 'Valid opportunity object required for exit level calculation'
            };
        }

        const entryPrice = opportunity.entry_price || opportunity.price || 100; // Default for testing
        const direction = opportunity.direction || opportunity.action || 'LONG';
        const confidence = opportunity.confidence || 'MEDIUM';

        // Simulate ATR calculation - replace with real volatility data
        const atr = entryPrice * 0.015;// 1-2% ATR

        // Calculate base levels
        const baseStopDistance = atr * 1.5;
        const baseTakeDistance = atr * 2.5;

        let stopLoss, takeProfit;

        if (direction.toUpperCase().includes('LONG') || direction.toUpperCase() === 'BUY') {
            stopLoss = entryPrice - baseStopDistance;
            takeProfit = entryPrice + baseTakeDistance;
        } else {
            stopLoss = entryPrice + baseStopDistance;
            takeProfit = entryPrice - baseTakeDistance;
        }

        // Confidence-based adjustments
        const confidenceMultiplier = {
            'HIGH': 1.2,
            'MEDIUM': 1.0,
            'LOW': 0.7
        }[confidence.toUpperCase()] || 1.0;

        // Adjust take profit based on confidence
        if (direction.toUpperCase().includes('LONG') || direction.toUpperCase() === 'BUY') {
            takeProfit = entryPrice + (takeProfit - entryPrice) * confidenceMultiplier;
        } else {
            takeProfit = entryPrice - (entryPrice - takeProfit) * confidenceMultiplier;
        }

        const riskRewardRatio = Math.abs(takeProfit - entryPrice) / Math.abs(entryPrice - stopLoss);

        return {
            success: true,
            timestamp: new Date().toISOString(),
            exit_levels: {
                entry_price: entryPrice,
                stop_loss: stopLoss,
                take_profit: takeProfit,
                direction: direction,
                risk_reward_ratio: riskRewardRatio,
                stop_distance_pct: Math.abs(entryPrice - stopLoss) / entryPrice * 100,
                take_distance_pct: Math.abs(takeProfit - entryPrice) / entryPrice * 100,
                confidence_multiplier: confidenceMultiplier,
                atr_value: atr,
                quality_score: riskRewardRatio >= 2.0 && confidence === 'HIGH' ? 'A+' :
                              riskRewardRatio >= 1.5 ? 'A' : 
                              riskRewardRatio >= 1.0 ? 'B' : 'C'
            }
        };
    } catch (error) {
        return {
            success: false,
            error: error.message,
            timestamp: new Date().toISOString()
        };
    }
}

