async function systemJournal(functionType, data, learningType, analysisPeriod) {
    console.log(`üß† System Journal - Function: ${functionType}`);

    try {
        const supabaseUrl = $vars.SUPABASE_URL;
        const supabaseKey = $vars.SUPABASE_ANON_KEY;

        if (!supabaseUrl || !supabaseKey) {
            console.warn('Supabase credentials not available - using in-memory logging');
        }

        switch (functionType.toLowerCase()) {
            case 'log_trade':
                return await logTrade(data, supabaseUrl, supabaseKey);
            case 'log_decision':
                return await logDecision(data, supabaseUrl, supabaseKey);
            case 'realtime_learning':
                return await performRealtimeLearning(data, learningType);
            case 'deep_analysis':
                return await performDeepAnalysis(analysisPeriod || '7d');
            case 'get_insights':
                return await getSystemInsights(analysisPeriod || '30d');
            case 'update_parameters':
                return await updateSystemParameters(data, supabaseUrl, supabaseKey);
            case 'genetic_algorithm_optimization':
                const strategyParam = data ? JSON.parse(data).strategy || 'default' : 'default';
                const windowParam = analysisPeriod ? parseInt(analysisPeriod) || 30 : 30;
                return await geneticAlgorithmOptimization(strategyParam, windowParam);
            case 'market_adaptive_learning':
                return await marketAdaptiveLearning();
            case 'realtime_performance_guardian':
                return await realtimePerformanceGuardian();
            case 'complete_cycle':
                return await completeLearnCycle(data, learningType);
            default:
                throw new Error(`Unknown function: ${functionType}`);
        }

    } catch (error) {
        console.error('‚ùå System Journal error:', error);
        return {
            success: false,
            error: error.message,
            timestamp: new Date().toISOString(),
            function_attempted: functionType
        };
    }
}

