async function logTrade(tradeData, supabaseUrl, supabaseKey) {
    console.log('üìù Logging trade details...');

    let trade;
    try {
        trade = typeof tradeData === 'string' ? JSON.parse(tradeData) : tradeData;
    } catch (error) {
        throw new Error('Invalid trade data format. Expected JSON.');
    }

    const tradeRecord = {
        trade_id: trade.trade_id || generateTradeId(),
        timestamp: new Date().toISOString(),
        symbol: trade.symbol,
        action: trade.action,
        quantity: parseFloat(trade.quantity || 0),
        entry_price: parseFloat(trade.entry_price || 0),
        exit_price: parseFloat(trade.exit_price || 0),
        pnl_usd: parseFloat(trade.pnl_usd || 0),
        pnl_percentage: parseFloat(trade.pnl_percentage || 0),
        trade_status: trade.trade_status || 'OPEN',
        simulation_mode: trade.simulation_mode === true || trade.simulation_mode === 'true' || false,
        execution_time_ms: parseInt(trade.execution_time_ms || 0),
        confidence_score: parseInt(trade.confidence_score || 0),
        was_profitable: trade.pnl_usd > 0,
        met_expectations: trade.met_expectations || false,
        lessons_learned: trade.lessons_learned || ''
    };

    console.log('üìä Trade record to insert:', JSON.stringify(tradeRecord, null, 2));

    if (supabaseUrl && supabaseKey) {
        const insertResult = await insertSupabaseRecord('trades_log', tradeRecord, supabaseUrl, supabaseKey);
        console.log('üì§ Database insert result:', insertResult);
    }

    let learningTriggered = false;
    if (tradeRecord.trade_status === 'CLOSED') {
        await performRealtimeLearning(tradeRecord, 'micro_learning');
        learningTriggered = true;
    }

    return {
        success: true,
        timestamp: new Date().toISOString(),
        trade_logged: {
            trade_id: tradeRecord.trade_id,
            symbol: tradeRecord.symbol,
            action: tradeRecord.action,
            status: tradeRecord.trade_status,
            pnl_usd: tradeRecord.pnl_usd,
            stored_to_database: !!(supabaseUrl && supabaseKey)
