function calculateAdaptabilityScore(performanceByCondition) {
    const performances = Object.values(performanceByCondition);
    const avgSharpe = performances.reduce((sum, p) => sum + p.sharpe_ratio, 0) / performances.length;
    const sharpeStdDev = Math.sqrt(
        performances.reduce((sum, p) => sum + Math.pow(p.sharpe_ratio - avgSharpe, 2), 0) / performances.length
    );

    return Math.max(0, Math.min(100, 100 - (sharpeStdDev * 50)));
}

function generateGuardianRecommendations(alerts, performance) {
    const recommendations = [];

    if (alerts.length === 0) {
        recommendations.push({
            action: 'CONTINUE_MONITORING',
            message: 'System performing within acceptable parameters',
            priority: 'INFO'
        });
    } else {
        const criticalAlerts = alerts.filter(a => a.severity === 'CRITICAL');
        if (criticalAlerts.length > 0) {
            recommendations.push({
                action: 'IMMEDIATE_INTERVENTION',
                message: `${criticalAlerts.length} critical issues require immediate attention`,
                priority: 'URGENT'
            });
        }

        recommendations.push({
            action: 'REVIEW_STRATEGY_PARAMETERS',
            message: 'Consider optimizing strategy parameters based on recent performance',
            priority: 'HIGH'
        });
    }

    return recommendations;
}

